import { HttpErrorResponse, HttpResponse } from '@angular/common/http';
import {Component, Input, OnChanges, OnInit, SimpleChanges} from '@angular/core';
import { CustomToastService } from 'app/layouts/service/customtoast.service';
import { Ns2HttpService } from 'app/shared/service/ns2-http.service';
import { LazyLoadEvent } from 'primeng/api';
import { IOverpaymentPay, RefundStatus } from '../refund-history/refund-history.model';
import { OverpaymentService } from '../service/overpayment.service';
import { IOverpayment, OverpaymentType } from './overpayment-tab.model';

@Component({
  selector: 'n2-overpayment-tab',
  templateUrl: './overpayment-tab.component.html',
  styleUrls: ['./overpayment-tab.component.scss']
})
export class OverpaymentTabComponent implements OnInit {

  @Input() selectedId = "";
  @Input() opTabIsNSman = true;

  overpaymentList: IOverpayment[] = [];
  // OverpaymentType = OverpaymentType;
  OverpaymentType: { [key: string]: OverpaymentType } = OverpaymentType

  // Lazy loading
  totalRecords = 0;
  loading = false;
  pageData: {currentPage: number} = {
    currentPage: 0
  }
  pageSize = 10;
  lazyLoadEvent!: LazyLoadEvent;

  // Dialog
  showOPDialog = false;
  refundLoading = false;
  selectedOP: IOverpayment = {};
  selectedRefundDetails: IOverpaymentPay[];
  // RefundStatus = RefundStatus;
  RefundStatus: { [key: string]: RefundStatus } = RefundStatus

  constructor (
    private overpaymentService: OverpaymentService,
    private n2httpService: Ns2HttpService,
    private toastService: CustomToastService
  ) {}

  ngOnInit(): void {
    // No empty
  }

  exportCSV(): void {
     // TODO: Implement
  }

  loadData(event: any): void {
    this.loading =  true;

    if(this.opTabIsNSman) {
      setTimeout(() => {
        const options = this.n2httpService.convertLazyEvent(event, this.pageData, this.pageSize);

        this.overpaymentService.getByNric(this.selectedId, options).subscribe(
          (res : HttpResponse<any>) => {
            this.overpaymentList = res.body.data.content;
            this.totalRecords = res.body.data.totalElements;
            this.loading = false;
          },
          (error: HttpErrorResponse) => {
              this.toastService.error(error.error?.error_desc);
              this.loading = false;

          }
        );
      }, 1000);
    } else {
      setTimeout(() => {
        const options = this.n2httpService.convertLazyEvent(event, this.pageData, this.pageSize);

        this.overpaymentService.getByCsn(this.selectedId, options).subscribe(
          (res : HttpResponse<any>) => {
            this.overpaymentList = res.body.data.content;
            this.totalRecords = res.body.data.totalElements;
            this.loading = false;
          },
          (error: HttpErrorResponse) => {
            this.toastService.error(error.error?.error_desc);
            this.loading = false;
          }
        );
      }, 1000);
    }
  }

  onPage(event:any): void{
    this.pageSize = event.rows;
    this.pageData.currentPage = event.first/event.rows;
  }

  showOverpaymentDetails(): void {
    this.showOPDialog = true;
    console.log(this.selectedOP);
    this.loadRefundDetails(this.selectedOP.id);
  }

  // Overpayment Details Dialog
  loadRefundDetails(overpaymentId: string): void {
    this.refundLoading = true;

    setTimeout(() => {
      console.log("Load Refund Details");
      this.overpaymentService.getRefundDetailsByOverpaymentId(overpaymentId).subscribe(
        (res : HttpResponse<any>) => {
          this.selectedRefundDetails = res.body.data;
          this.refundLoading = false;
        },
        (error: HttpErrorResponse) => {
            this.toastService.add({
              severity: 'error',
              summary: 'Error',
              detail: error.error.error_desc
            });
            this.refundLoading = false;
        }
      );
    }, 1000);
  }

}
