                <td>{{data.overpaymentRefund.refundActualAmt | currency:'SGD':'':'.2-2'}}</td>
                <td>{{data.overpaymentRefund.refundType | defaultString }}</td>
                <td>{{data.overpaymentRefund.paydayDate | date:'dd-MM-yyyy' | defaultString}}</td>
                <td>{{data.overpaymentRefund.payee | defaultString }}</td>
                <td>{{data.overpaymentRefund.payeePayDate | date:'dd-MM-yyyy'  | defaultString }}</td>
                <td>{{data.overpaymentRefund.refundPrincipalAmt | currency:'SGD':''}}</td>
                <td>{{data.overpaymentRefund.refundInterestAmt | currency:'SGD':''}}</td>
                <td>{{data.overpaymentRefund.requestedBy | defaultString}} <br>
                    {{data.overpaymentRefund.requestedDt | date:'dd-MM-yyyy' | defaultString}} <br>
                    {{data.overpaymentRefund.requestReviewedBy}} <br>
                    {{data.overpaymentRefund.requestReviewedDt | date:'dd-MM-yyyy'}}
