import {Component, OnInit} from '@angular/core';
import {InstalmentService} from "app/payment/instalment/service/instalment.service";

@Component({
  selector: 'n2-instalment-create-generate-email',
  templateUrl: './instalment-create-generate-email.component.html',
  styleUrls: ['./instalment-create-generate-email.component.scss']
})
export class InstalmentCreateGenerateEmailComponent implements OnInit {
  generatedEmailContent = "";

  constructor(private instalmentService: InstalmentService) {
  }

  ngOnInit(): void {
    //
  }

  generate(): void {
    // TODO: Call API endpoint to generate the email contents
    this.generatedEmailContent = "Dear <addressee>,\n\nThis is a sample email letter to be replaced with the " +
      "actual email template to be provided by NSmen Payment Center (NPC)\n\nRegards,\nNSmen Payment Center";
  }
}
<div class="textarea-container">
  <textarea [rows]="10" pInputTextarea [readonly]="true" #emailTextArea>{{generatedEmailContent}}</textarea>
  <button pButton type="button" icon="pi pi-copy" class="clipboard-button p-button-rounded p-button-text" [ngxClipboard]="emailTextArea" [cbContent]="generatedEmailContent"></button>
</div>
