import {EventEmitter, Injectable, OnDestroy} from '@angular/core';
import {Observable, Subscription} from 'rxjs';
import {Ns2HttpService} from 'app/shared/service/ns2-http.service';
import {ApplicationConfigService} from 'app/core/config/application-config.service';
import { IComputeFormula } from '../model/compute-formula.model';

@Injectable({ providedIn: 'root' })
export class FormulaService{

    public resourceUrl = this.applicationConfigService.getEndpointFor('api/formula');

    private subscription: Subscription;

    constructor(
        private ns2Http: Ns2HttpService,
        private applicationConfigService: ApplicationConfigService,
        ){
       this.subscription = new Subscription();
    }

    computeFormula(request: IComputeFormula, options: any) : Observable<any>{
        const path =`${this.resourceUrl}/compute`;
        return this.ns2Http.post(path, JSON.stringify(request), options);
    }

}
