import { HttpErrorResponse, HttpResponse } from '@angular/common/http';
import {Component, Input, OnChanges, OnInit, SimpleChanges} from '@angular/core';
import { FormArray, FormBuilder, FormControl, FormGroup } from '@angular/forms';
import { ICompanyHistory } from '../amendment-history/amendment-history.model';
import { CompanyHistoryService } from '../amendment-history/amendment-history.service';

@Component({
  selector: 'n2-admendment-snapshot',
  templateUrl: './admendment-snapshot.component.html',
  styleUrls: ['./admendment-snapshot.component.scss']
})
export class AdmendmentSnapshotComponent implements OnInit, OnChanges {

  // eslint-disable-next-line @angular-eslint/no-input-rename
  @Input('companyHistoryId') selectedId: string;

  ep: ICompanyHistory = {};

  disableForm = false;

  // Fixed
  representativeList = ['CO1', 'CO2', 'EO1', 'EO2', 'SUP'];

  salutationOptions = [
    {label: "Ms", value: "MS"},
    {label: "Mr", value: "MR"},
    {label: "Mrs", value: "MRS"},
    {label: "Dr", value: "DR"}
  ];

  companyForm: FormGroup = this.fb.group({
    companyInfo: this.fb.group({
      csn1: new FormControl(''),
      csn2: new FormControl(''),
      csn3: new FormControl(''),
      name: new FormControl(''),
      blk: new FormControl(''),
      street: new FormControl(''),
      unit: new FormControl(''),
      building: new FormControl(''),
      postal: new FormControl('')
    }),
    officers: this.fb.array([])
  });

  directSchemeForm: FormGroup = this.fb.group({
    directSchemeHistory: this.fb.array([])
  });

  constructor(
    private fb: FormBuilder,
    private companyHistoryService: CompanyHistoryService
  ) { }

  ngOnInit(): void {
    this.constructForm();
    this.companyForm.disable();
    this.directSchemeForm.disable();
  }

  ngOnChanges(changes: SimpleChanges): void {

    console.log(changes);
    if(changes['selectedId'].currentValue) {
      this.getCompanyHistory(changes['selectedId'].currentValue as string);
    }
  }

  getCompanyHistory(companyHistoryId: string): void {
      this.companyHistoryService.getByCompanyHistoryId(companyHistoryId).subscribe(
        (res : HttpResponse<any>) => {
          this.ep = res.body;
          this.patchFormValues();
        },
        (error: HttpErrorResponse) => {
              console.log(error.message);
        }
      );
  }

  constructForm(): void {
    const officers = this.companyForm.get('officers') as FormArray;

    this.representativeList.forEach((role:string) => {
      officers.push(
        this.fb.group({
          id: new FormControl(),
          officerRole: role,
          nric: new FormControl(''),
          salutation: new FormControl(),
          name: new FormControl(''),
          appointment: new FormControl(''),
          email: new FormControl(''),
          contactNo: new FormControl('')
        })
      );
    }
    );

  }
  patchFormValues(): void {
    this.companyForm.patchValue({
      companyInfo: {
        csn1: this.ep.csn?.substring(0, 10),
        csn2: this.ep.csn?.substring(10, 13),
        csn3: this.ep.csn?.substring(13, 15),
        name: this.ep.companyName,
        blk: this.ep.blk,
        street: this.ep.street,
        unit: this.ep.unit,
        building: this.ep.building,
        postal: this.ep.postalCode
      },
    })

    const tempOfficers = this.companyForm.get('officers') as FormArray;

    this.ep.companyOfficerHistory?.forEach(employerRep => {
      const index = this.getIndexFromTitle(tempOfficers, employerRep.officerRole);
      console.log(employerRep);
      if (index > -1) {
        tempOfficers.at(index).patchValue({
          id: employerRep.id,
          nric: employerRep.nric,
          salutation: employerRep.salutation,
          name: employerRep.name,
          appointment: employerRep.appointment,
          email: employerRep.email,
          contactNo: employerRep.contactNo
        })
      }
    })
  }

  getIndexFromTitle(tempOfficers: FormArray, title: string) : number {
    let resIndex = -1;

    tempOfficers.controls.forEach((c,index) => {
      if (c.get('officerRole')?.value === title) {
        resIndex = index;
      }
    });

    return resIndex;
  }

  log(): void {
    // console.log("dialog = " + this.showDialog);
  }

  get officersFormArray (): FormArray {
    return this.companyForm.get('officers') as FormArray;
  }

  get directSchemeFormArray (): FormArray {
    return this.directSchemeForm.get('directSchemeHistory') as FormArray;
  }
}
<form [formGroup]="companyForm">
    <div class="grid">
      <div class="col-4" formGroupName="companyInfo">
        <p-panel>
          <ng-template pTemplate="header">
            <div>
              <span class="p-panel-title"> Company Information (Size: {{ep.companySize}}, eRegistered: {{ep.eRegistered == 'Y' ? "Yes" : "No"}})</span>
            </div>
          </ng-template>

          <div class="field grid">
            <label for="csn" class="col-3">CSN</label>
            <div class="col-9" id="csn">
              <input type="text" pInputText formControlName="csn1" id="csn1" style="width:30%" maxlength="10" [disabled]="disableForm"/>
              -
              <input type="text" pInputText formControlName="csn2" id="csn2" style="width:20%" maxlength="3" [disabled]="disableForm"/>
              -
              <input type="text" pInputText formControlName="csn3" id="csn3" style="width:20%" maxlength="2" [disabled]="disableForm"/>
            </div>
          </div>

          <div class="field grid">
            <label for="csn" class="col-3">Company Name</label>
            <div class="col-9">
              <input type="text" pInputText formControlName="name" id="companyName" class="w-full" maxlength="50" [disabled]="disableForm" />
            </div>
          </div>

          <div class="field grid">
            <label for="address" class="col-3">Address</label>
            <div class="col-9" id="address">
              <div class="field">
                <input type="text" pInputText formControlName="blk" id="blk" placeholder="Blk" style="width:25%"
                  maxlength="10" [disabled]="disableForm" />
                <input type="text" pInputText formControlName="street" id="street" placeholder="Street" style="width:70%"
                  maxlength="100" [disabled]="disableForm"/>
              </div>

              <div>
                <input type="text" pInputText formControlName="unit" id="unit" placeholder="Unit" style="width:40%"
                  maxlength="10" [disabled]="disableForm"/>
                <input type="text" pInputText formControlName="building" id="building" placeholder="Building"
                  style="width:55%" maxlength="100" [disabled]="disableForm"/>
              </div>
            </div>
          </div>

          <div class="field grid">
            <label for="postal" class="col-3">Postal</label>
            <div class="col-9">
              <input type="text" pInputText pKeyFilter="pint" formControlName="postal" id="postal" maxlength="6" [disabled]="disableForm"/>
            </div>
          </div>
        </p-panel>
      </div>

      <div class="col-8">
        <div formArrayName="officers">
          <p-panel>
            <ng-template pTemplate="header">
              <div>
                <span class="p-panel-title" icTranslate="nspay2App.administration.employer-profile.companyRepresentative.header"></span>
              </div>
            </ng-template>
            <!-- <p-table [value]="companyForm.controls?.officers['controls']" styleClass="p-datatable-gridlines"> -->
            <p-table [value]="officersFormArray.controls" styleClass="p-datatable-gridlines">
              <ng-template pTemplate="caption">
                <div class="p-d-flex">
                  <button type="button" pButton pRipple icon="pi pi-file-o"  pTooltip="CSV"
                    tooltipPosition="bottom"></button>
                </div>
              </ng-template>
              <ng-template pTemplate="header">
                <tr>
                  <th scope="col" style="width: 7%"></th>
                  <th scope="col">NRIC</th>
                  <th scope="col" style="width: 13%">Salutation</th>
                  <th scope="col">Name</th>
                  <th scope="col">Appointment</th>
                  <th scope="col">Email</th>
                  <th scope="col">Contact No.</th>
                </tr>
              </ng-template>
              <ng-template pTemplate="body" let-officer let-i="rowIndex">
                <tr [formGroupName]="i">
                  <td>
                    {{officer.value.officerRole}}
                  </td>
                  <td>
                    <input type="text" pInputText formControlName="nric" class="w-full" [disabled]="disableForm"/>
                  </td>
                  <td>
                    <p-dropdown formControlName="salutation" [options]="salutationOptions" optionLabel="label"
                      optionValue="value" placeholder="&nbsp;" styleClass="full-width" [disabled]="disableForm">
                    </p-dropdown>
                  <td>
                    <input type="text" pInputText formControlName="name" id="name{{i}}" class="w-full" [disabled]="disableForm"/>
                  </td>
                  <td>
                    <input type="text" pInputText formControlName="appointment" class="w-full" [disabled]="disableForm"/>
                  </td>
                  <td>
                    <input type="text" pInputText formControlName="email" class="w-full" [disabled]="disableForm"/>
                  </td>
                  <td>
                    <input type="text" pInputText formControlName="contactNo" class="w-full" [disabled]="disableForm" maxlength="8"/>
                  </td>
                </tr>
              </ng-template>
            </p-table>
          </p-panel>
        </div>
      </div>

      <div class="col-12">
        <form [formGroup]="directSchemeForm">
          <div class="col-8 col-offset-4" formArrayName="directSchemeHistory">
            <p-panel>
              <ng-template pTemplate="header">
                <div>
                  <span class="p-panel-title" icTranslate="nspay2App.administration.employer-profile.directSchemeHistory.header"></span>
                </div>
              </ng-template>
              <!-- <p-table [value]="directSchemeForm.controls.directSchemeHistory['controls']" styleClass="p-datatable-gridlines"> -->
              <p-table [value]="directSchemeFormArray.controls" styleClass="p-datatable-gridlines">
                <ng-template pTemplate="caption">
                  <div class="p-d-flex">
                    <button type="button" pButton pRipple icon="pi pi-file-o" pTooltip="CSV"
                      tooltipPosition="bottom"></button>
                  </div>
                </ng-template>
                <ng-template pTemplate="header">
                  <tr>
                    <th scope="col" style="width: 7%">S/N</th>
                    <th scope="col">Date Joined</th>
                    <th scope="col" style="width: 13%">Submitted By</th>
                    <th scope="col">Submitted On</th>
                    <th scope="col">Date Left</th>
                    <th scope="col">Ceased By</th>
                    <th scope="col">Ceased On</th>
                    <th scope="col">Bank Account.</th>
                  </tr>
                </ng-template>
                <ng-template pTemplate="body" let-data let-i="rowIndex">
                  <tr>
                    <td>{{i+1}}</td>
                    <td>{{data.value.joinDt}}</td>
                    <td>{{data.value.submittedBy}}</td>
                    <td>{{data.value.submittedDt}}</td>
                    <td>
                      <p-calendar id="dateLeft{{i}}"
                        formControlName="dateLeft"
                        dateFormat="dd-mm-yy"
                        [readonlyInput]="true"
                        placeholder="Select a date"
                        appendTo="body"
                        class="inputfield w-full">
                    </p-calendar>
                    </td>
                    <td>{{data.value.ceasedBy}}</td>
                    <td>{{data.value.ceasedDt}}</td>
                    <td>{{data.value.bankAccount}}</td>
                  </tr>
                </ng-template>
              </p-table>
            </p-panel>
          </div>
        </form>
      </div>

    </div>
  </form>
