import { TemplateService } from './template.service';
import { ITEMS_PER_PAGE } from 'app/config/pagination.constants';
import { AccountService } from 'app/core/auth/account.service';
import { NgbModal } from '@ng-bootstrap/ng-bootstrap';
import { TemplateDeleteDialogComponent } from './template-delete-dialog.component';

@Component({
  selector: 'ic-report-template',
  templateUrl: './template.component.html',
})
export class TemplateComponent implements OnInit, OnDestroy {
  currentAccount: any;
  templates: Template[] = [];
  error: any;
  success: any;
  eventSubscriber?: Subscription;
  routeData: any;
  links: any;
  totalItems: any;
  queryCount: any;
  itemsPerPage = ITEMS_PER_PAGE;
  page!: number;
  ascending!: boolean;
  predicate!: string;
  previousPage: any;
  reverse: any;

  constructor(
    private templateService: TemplateService,
    private parseLinks: ParseLinks,
    private alertService: AlertService,
    private accountService: AccountService,
    private activatedRoute: ActivatedRoute,
    private router: Router,
    private eventManager: EventManager,
    private modalService: NgbModal
  ) {}

  deleted(template: Template): void {
    const modalRef = this.modalService.open(TemplateDeleteDialogComponent, { size: 'lg', backdrop: 'static' });
    modalRef.componentInstance.template = template;
  }

  loadAll(): void {
    this.templateService
      .query({
        page: this.page - 1,
        size: this.itemsPerPage,
        sort: this.sort(),
      })
      .subscribe(
        (res: HttpResponse<Template[]>) => this.onSuccess(res.body, res.headers),
        (res: HttpErrorResponse) => this.onError(res.message)
      );
  }
  loadPage(page: number): void {
    if (page !== this.previousPage) {
      this.previousPage = page;
      this.transition();
    }
  }
  transition(): void {
    this.router.navigate(['/admin/reportTemplate'], {
      queryParams: {
        page: this.page,
        size: this.itemsPerPage,
        sort: this.predicate + ',' + (this.reverse ? 'asc' : 'desc'),
      },
    });
  }

  clear(): void {
    this.page = 0;
    this.router.navigate([
      '/admin/reportTemplate',
      {
        page: this.page,
        sort: this.predicate + ',' + (this.reverse ? 'asc' : 'desc'),
      },
    ]);
  }
  ngOnInit(): void {
    this.accountService.identity().subscribe(account => (this.currentAccount = account));
    this.handleNavigation();
    this.registerChangeInTemplates();
  }

  ngOnDestroy(): void {
    if (this.eventSubscriber) {
      this.eventManager.destroy(this.eventSubscriber);
    }
  }

  trackId(index: number, item: Template): any {
    return item.id;
  }
  registerChangeInTemplates(): void {
    this.eventSubscriber = this.eventManager.subscribe('templateListModification', () => this.loadAll());
  }

  sort(): any {
    const result = [this.predicate + ',' + (this.reverse ? 'asc' : 'desc')];
    if (this.predicate !== 'id') {
      result.push('id');
    }
    return result;
  }

  onClickCreate(): void {
    this.router.navigate(['admin/reportTemplate/report-template-new']);
  }

  private handleNavigation(): void {
    combineLatest([this.activatedRoute.data, this.activatedRoute.queryParamMap]).subscribe(([data, params]) => {
      const page = params.get('page');
      this.page = page !== null ? +page : 1;
      const sort = (params.get('sort') ?? data['defaultSort']).split(',');
      this.predicate = sort[0];
      this.ascending = sort[1] === 'asc';
      this.loadAll();
    });
  }

  private onSuccess(data: any, headers: any): void {
    this.links = this.parseLinks.parse(headers.get('link'));
    this.totalItems = headers.get('X-Total-Count');
    this.queryCount = this.totalItems;
    this.templates = data;
  }
  private onError(error: any): void {
    this.alertService.addAlert({ type: 'danger', translationKey: error.message });
  }
}
<div>
  <h2>
    <span icTranslate="reportAdmin.template.home.title">Report Template</span>
    <button class="btn btn-primary float-end jh-create-entity create-customer" (click)="onClickCreate()">
      <fa-icon [icon]="'plus'"></fa-icon>
      <span icTranslate="reportAdmin.template.home.createLabel"> Create new Template </span>
    </button>
  </h2>
  <ic-alert></ic-alert>
  <div class="row"></div>
  <br />
  <div class="table-responsive" *ngIf="templates">
    <table class="table table-striped">
      <thead>
        <tr icSort [(predicate)]="predicate" [(ascending)]="reverse" [callback]="transition.bind(this)">
          <th scope="col" icSortBy="id"><span icTranslate="global.field.id">ID</span> <fa-icon [icon]="'sort'"></fa-icon></th>
          <th scope="col" icSortBy="templateName">
            <span icTranslate="reportAdmin.template.templateName">Template Name</span> <fa-icon [icon]="'sort'"></fa-icon>
          </th>
          <th scope="col" icSortBy="createdBy">
            <span icTranslate="reportAdmin.template.uploadBy">Upload By</span> <fa-icon [icon]="'sort'"></fa-icon>
          </th>
          <th scope="col" icSortBy="createdDt">
            <span icTranslate="reportAdmin.template.createdDt">Create Date</span> <fa-icon [icon]="'sort'"></fa-icon>
          </th>
          <th scope="col" icSortBy="templateLastFileName">
            <span icTranslate="reportAdmin.template.fileName">File Name</span><fa-icon [icon]="'sort'"></fa-icon>
          </th>
          <th scope="col" icSortBy="templateStatus">
            <span icTranslate="reportAdmin.template.action">Actions</span> <fa-icon [icon]="'sort'"></fa-icon>
          </th>
          <th scope="col">Operation</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let template of templates; trackBy: trackId">
          <td>
            <a [routerLink]="['../report-template', template.id]">{{ template.id }}</a>
          </td>
          <td>{{ template.templateName }}</td>
          <td>{{ template.createdBy }}</td>
          <td>{{ template.createdDt | date: 'yyyy-MM-dd' }}</td>
          <td>{{ template.templateLastFileName }}</td>
          <td>{{ template.templateStatus }}</td>
          <td class="text-end">
            <div class="btn-group flex-btn-group-container">
              <button type="submit" [routerLink]="['./', template.id]" class="btn btn-info btn-sm">
                <fa-icon [icon]="'eye'"></fa-icon>
                <span class="d-none d-md-inline" icTranslate="entity.action.view">View</span></button
              >&nbsp;
              <button
                type="submit"
                [routerLink]="['./' + template.id + '/edit']"
                [replaceUrl]="true"
                queryParamsHandling="merge"
                class="btn btn-primary btn-sm"
              >
                <fa-icon [icon]="'pencil-alt'"></fa-icon>
                <span class="d-none d-md-inline" icTranslate="entity.action.edit">Edit</span></button
              >&nbsp;

              <button type="submit" (click)="deleted(template)" class="btn btn-danger btn-sm">
                <fa-icon [icon]="'times'"></fa-icon>
                <span class="d-none d-md-inline" icTranslate="entity.action.delete">Delete</span>
              </button>
            </div>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
  <div *ngIf="templates && templates.length">
    <div class="row justify-content-center">
      <ic-item-count [page]="page" [total]="queryCount" [maxSize]="5" [itemsPerPage]="itemsPerPage"></ic-item-count>
    </div>
    <div class="row justify-content-center">
      <ngb-pagination
        [collectionSize]="totalItems"
        [(page)]="page"
        [pageSize]="itemsPerPage"
        [maxSize]="5"
        [rotate]="true"
        [boundaryLinks]="true"
        (pageChange)="loadPage(page)"
      ></ngb-pagination>
    </div>
  </div>
</div>
