            this.mup.push(this.paylist[i].makeUpPay);
            this.computedDt.push(this.paylist[i].computedDt);
          }
          if (this.paylist[i].type === 'NST') {
            this.nst.push(this.paylist[i].topUp);
          }
        }
        this.totalSPMUP = Number(this.sp[0].totalServicePayAmt) + Number(this.mup[0].makeUpPayAmt);
        // total service pay
        this.tsp = Number(this.sp[0].totalServicePayAmt);
        // total mup
        this.tmup = Number(this.mup[0].makeUpPayAmt);
        // total service pay + mup
        this.totalSPMUP = Number(this.sp[0].totalServicePayAmt) + Number(this.mup[0].makeUpPayAmt);
        // top up
        this.tp = Number(this.nst[0].topUpAmt);
        ;
        this.tep = this.totalSPMUP + this.tp;
        this.tableForm.get('ptua').patchValue(this.approvals[0].servicePayTopUpAmt);
        // top up eligibility
        for (let i = 0; i < this.paylist.length; i++) {
          if (this.paylist[i].type === "NST") {
            this.tpeli = this.paylist[i].eligibilityInd;
          }
        }
      }
      // shipboard
      if (this.approvals[0].shipboardInd === "Y") {
        this.isChecked[0] = true;
      }
      // if (this.approvals[0].shipboardInd === "NULLIFY") {
