import { Component, Inject, LOCALE_ID, OnInit, ViewChild } from '@angular/core';
import { CustomToastService } from 'app/layouts/service/customtoast.service';
import { Ns2HttpService } from 'app/shared/service/ns2-http.service';
import { Option, IQueryApproveVES, RemarksList, SupportingDocsList, NsMan, Nric, PayComponent, NST, MUP, SP, Paymode, amendmentFields } from '../model/payroll.model';
import { NspConfirmationService } from 'app/shared/confirm/nsp-confirm.service';
import { PayrollApprovalService } from '../service/payroll-approval.service';
import { FormArray, FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { FileUploadService } from 'app/shared/service/file-upload.service';
import { HttpErrorResponse, HttpResponse, HttpStatusCode } from '@angular/common/http';
import { DateUtilsService } from 'app/core/util/date-utils.service';
import { ExportUtilsService } from 'app/core/util/export-utils.service';
import { ActivatedRoute, Router } from '@angular/router';
import { NSPAY2_REFTYPE, NSPAY2_REFCODE } from 'app/nspay2.constants';
import { CABComponent } from 'app/enquiry/caution-alert-block/cab.component';
import { RemarkValidator } from '../../../../app/shared/validator/remark.directive'
import { toInteger } from 'lodash';
import { disableDebugTools } from '@angular/platform-browser';
import { Table } from 'primeng/table';
import { formatDate } from '@angular/common';
import { Console } from 'console';
import { ThisReceiver } from '@angular/compiler';
import { AnyAaaaRecord } from 'dns';
import { LazyLoadEvent } from 'primeng/api';
import { IPayrollRecordHistory } from 'app/payroll/payroll/model/view-payroll-detail.model';

@Component({
  selector: 'n2-approve-payroll-amend',
  templateUrl: './approve-payroll-amend.component.html',
  styleUrls: ['./approve-payroll-amend.component.scss']
})
export class ApprovePayrollAmendComponent implements OnInit {

  readonly NSPAY2_REFTYPE = NSPAY2_REFTYPE;
  readonly NSPAY2_REFCODE = NSPAY2_REFCODE;

  amendmentHistoryDialog: any = { display: false };

  remarksForm: FormGroup = this.fb.group({
    remarks: new FormControl(null),
  }, {
    validators: RemarkValidator.rejRemark("remarks")
  });

  tableForm: FormGroup = this.fb.group({
    newstartTime: new FormControl(null, [Validators.required]),
    newendTime: new FormControl(null, [Validators.required]),
    originalstartTime: new FormControl(null, [Validators.required]),
    originalendTime: new FormControl(null, [Validators.required]),
    newpayableAmt: new FormControl(null),
    checked: new FormControl(null),
    ptua: new FormControl(null),
    ciltp: new FormControl(null),
    civilianincomeLtopup: this.fb.array([])
