  ngOnInit(): void {
    this.requestId = this.route.snapshot.paramMap.get("id");
    this.loadCodes();
    this.loadData();
    this.loadData2();
    console.log(this.approvals);
  }

  loadData(): void {

    this.payrollApprovalService.getPayrollRecord(this.requestId).subscribe(
      (res: HttpResponse<any>) => {
        console.log(res);

        this.amendTable.push(res.body.data);
        this.requestnric.push(res.body.data.nric);
        this.remarkslist = res.body.data.remarks;
        this.docs = res.body.data.supportingDocs;
      },
      (error: HttpErrorResponse) => {
        console.log(error.error.error_desc);
        this.toastService.error(error.error.error_desc);
        this.router.navigate(['']);
      }
    );
  }

  loadData2(): void {

    this.payrollApprovalService.getPayrollRecord(this.requestId).subscribe(
      (res: HttpResponse<any>) => {
        console.log(res);


        this.approvals.push(res.body.data);
        this.amendmentFields = res.body.data.amendmentFields;

        for (let i = 0; i < this.amendmentFields.length; i++) {
          this.civLossTopUpAmts.push(this.amendmentFields[i].civLossTopUpAmt);
        }

        this.refCode = this.approvals[0].refCode;
        this.refType = this.approvals[0].refType;
        this.payrollReferenceId = this.approvals[0].payrollReferenceId;

        this.selectedValid = this.validIndMap.get(res.body.data.validInd);
        this.setElig(res);
        this.displayDropdown = true;

        this.setPayrollDesc(this.refType, this.refCode);
        this.getDetails();
        this.getTotalComponents();
      },
      (error: HttpErrorResponse) => {
        console.log(error.message);
      }
    );
  }

  getDetails() {

    this.payrollApprovalService.getPayrollDetailByID(this.payrollReferenceId).subscribe(
      (res: HttpResponse<any>) => {
        this.approvals.push(res.body.data);

        for (let i = 0; i < this.approvals.length; i++) {
          this.newStartTime.push(this.approvals[i].payrollStartDt.substring(11, 16).replace(':', ''));
          this.newEndTime.push(this.approvals[i].payrollEndDt.substring(11, 16).replace(':', ''));
        }
      })

    this.payrollApprovalService.getNsman(this.requestnric[0]).subscribe(
      (res: HttpResponse<any>) => {
        this.nsman.push(res.body.data);
      },
      (error: HttpErrorResponse) => {
        console.log(error.message);
      }
    );

    this.payrollApprovalService.getPaymode(this.payrollReferenceId).subscribe(
      (res: HttpResponse<any>) => {
        const data = res.body.data
        for (let i = 0; i < data.length; i++) {
          this.paymode.push(res.body.data[i])
        }
      })
  }

  loadCodes(): void {
    // this.codeService.gets('PRL_SP_ELIG,PRL_NST_ELIG,PRL_PAYROLL_VALID').subscribe((codes: any[]) => {
    //     console.log(codes);
    //   });

    this.payrollApprovalService.getCodes('PRL_SP_ELIG,PRL_NST_ELIG,PRL_PAYROLL_VALID').subscribe((codes: HttpResponse<any>) => {
      console.log(codes);

      this.servicePayEligOptions = codes.body[0];
      this.servicePayEligOptions.forEach((codeDesc) => {
        this.servicePayEligMap.set(codeDesc.codeId, codeDesc.label['en-US']);
      });

      this.topUpEligOptions = codes.body[1];
      this.topUpEligOptions.forEach((codeDesc) => {
        this.topUpEligMap.set(codeDesc.codeId, codeDesc.label['en-US']);
      });

      this.validIndOptions = codes.body[2];
      this.validIndOptions.forEach((codeDesc) => {
        this.validIndMap.set(codeDesc.codeId, codeDesc.label['en-US']);
      })
    });

    this.payrollApprovalService.getCodes('PRL_AWD_CODE,PRL_ALLW_CODE,PRL_ATTD_CODE')
      .subscribe((codes: HttpResponse<any>) => {
        console.log(codes);

        codes.body[0].forEach((codeDesc: any) => {
          this.awardCodes.set(codeDesc.codeId, codeDesc.label['en-US']);
        });

        codes.body[1].forEach((codeDesc: any) => {
          this.allowanceCodes.set(codeDesc.codeId, codeDesc.label['en-US']);
        });

        codes.body[2].forEach((codeDesc: any) => {
          this.attendanceCodes.set(codeDesc.codeId, codeDesc.label['en-US']);
        });

        console.log(this.awardCodes);
        console.log(this.allowanceCodes);
        console.log(this.attendanceCodes);


      });
  }

  setElig(data: HttpResponse<any>): void {
    const spElig = data.body.data.servicePayEligInd;
    const tpElig = data.body.data.topUpEligInd;

    if (spElig !== null) {
      this.selectedServicePayElig = spElig;
    } else {
      this.selectedServicePayElig = this.servicePayEligOptions[2].codeId;
    }

    if (tpElig !== null) {
      this.selectedTopUpElig = tpElig;
    } else {
      this.selectedTopUpElig = this.topUpEligOptions[2].codeId;
    }
  }

  setPayrollDesc(refType: any, refCode: any): void {
    console.log(refType);
    console.log(refCode);

    if (refType === NSPAY2_REFTYPE.AWARD) {
      this.payrollDesc = this.awardCodes.get(refCode);
    } else if (refType === NSPAY2_REFTYPE.ALLOWANCE) {
      this.payrollDesc = this.allowanceCodes.get(refCode);
    } else if (refType === NSPAY2_REFTYPE.ATTENDANCE) {
      this.payrollDesc = this.attendanceCodes.get(refCode);
    }

    console.log(this.payrollDesc);

  }

