      if (this.sp[0].combatAllowanceTopUpDesc === "N" && this.isChecked[0] === true) {
        orange = 'border-dashed border-yellow-300 border-3';
      } if (this.sp[0].combatAllowanceTopUpDesc === "Y" && this.isChecked[0] === false) {
        orange = 'border-dashed border-yellow-300 border-3';
      } if (this.sp[0].combatAllowanceTopUpDesc === "NA" && this.isChecked[0] === false) {
        orange = 'border-dashed border-yellow-300 border-3';
      } if (this.sp[0].combatAllowanceTopUpDesc === "NA" && this.isChecked[0] === true) {
        orange = 'border-dashed border-yellow-300 border-3';
      }
    }
    return orange;
  }

