  exportCSV(dataTable: Table): void {
    this.payrollApprovalService.getPaymode(this.payrollReferenceId).subscribe(
      res => {
        if (res.body.data) {
          const data = res.body.data;
          const columns =
            [
              { field: 'payDay', header: 'Pay Day', index: 0 },
              { field: 'payMode', header: 'Pay Mode', index: 1 },
              { field: 'payee', header: 'Payee', index: 2 },
              { field: 'refType', header: 'Ref Type', index: 3 },
              { field: 'payAmt', header: 'Pay Amount', index: 4 },
              { field: 'status', header: 'Status', index: 5 },
              { field: 'bankName', header: 'Bank Name', index: 6 }
            ];

          const dtoList: any[] = [];
          data.forEach((record: Paymode, index: number) => {
            const dto = {
              payDay: `${String(record.payDay)}`,
              payMode: `${String(record.payMode)}`,
              payee: `${String(record.payee)}`,
              refType: `${String(this.refType)}`,
              payAmt: `${String(record.payAmt)}`,
              status: `${String(record.status)}`,
              bankName: `${String(record.bankName)}`
            }
            dtoList.push(dto);
          });
          this.exportUtilsService.exportCSV(dataTable, dtoList, res.body.data.totalElements, columns,
            "data_".concat(formatDate(new Date(), "dd-MM-YYYY", this.localeId)));
        }
      },
      error => {
        console.log(error.error?.error_desc);
      })
  }

  downloadDoc(docId: string): void {
    if (docId) {
      this.fileUploadService.downloadTxnId(docId).subscribe(
        res => {
          if (res) {
            const data = res;
            const contentDispositionHeader: string = data.headers.get('content-disposition');
            const fileName = contentDispositionHeader.split(';')[1].trim().split('=')[1].replace(/"/g, '');
            const blob = new Blob([data.body], { type: data.headers.get('content-type') });
            const anchor = document.createElement("a");
            anchor.href = URL.createObjectURL(blob);
            anchor.download = fileName;
            anchor.click();
          }
        },
        error => {
          console.log(error);
          this.toastService.error(error.error?.error_desc);
        }
      );
    }
  }

  downloadAll(): void {
    for (let i = 0; i < this.docs.length; i++) {
      const docId = this.docs[i].documentId;
      this.fileUploadService.downloadTxnId(docId).subscribe(
        res => {
          if (res) {
            const data = res;
            const contentDispositionHeader: string = data.headers.get('content-disposition');
            const fileName = contentDispositionHeader.split(';')[1].trim().split('=')[1].replace(/"/g, '');
            const blob = new Blob([data.body], { type: data.headers.get('content-type') });
            const anchor = document.createElement("a");
            anchor.href = URL.createObjectURL(blob);
            anchor.download = fileName;
            anchor.click();
          }
        },
        error => {
          console.log(error);
          this.toastService.error(error.error?.error_desc);
        }
      );
    }
  }

  get civilianincomeLtopup(): FormArray {
    return this.tableForm?.controls["civilianincomeLtopup"] as FormArray;
  }

  getTotalComponents() {

    this.payrollApprovalService.getPayrollDetailByID(this.payrollReferenceId).subscribe(
      (res: HttpResponse<any>) => {
        if (res.body.data.payComponents.length !== 0) {
          this.paylist = res.body.data.payComponents;

          for (let i = 0; i < this.paylist.length; i++) {

            if (this.paylist[i].type === 'SP') {
              this.sp.push(this.paylist[i].servicePay);
            }
            if (this.paylist[i].type === 'MUP') {
              this.mup.push(this.paylist[i].makeUpPay);
              this.computedDt.push(this.paylist[i].computedDt);
            }

            if (this.paylist[i].type === 'NST') {
              this.nst.push(this.paylist[i].topUp);
            }
          }

          this.totalSPMUP = Number(this.sp[0].totalServicePayAmt) + Number(this.mup[0].makeUpPayAmt);

          // total service pay
          this.tsp = Number(this.sp[0].totalServicePayAmt);

          // total mup
          this.tmup = Number(this.mup[0].makeUpPayAmt);

          // total service pay + mup
          this.totalSPMUP = Number(this.sp[0].totalServicePayAmt) + Number(this.mup[0].makeUpPayAmt);

          // top up
          this.tp = Number(this.nst[0].topUpAmt);;

          this.tep = this.totalSPMUP + this.tp;

          this.tableForm.get('ptua').patchValue(this.approvals[0].servicePayTopUpAmt);

          // top up eligibility
          for (let i = 0; i < this.paylist.length; i++) {
            if (this.paylist[i].type === "NST") {
              this.tpeli = this.paylist[i].eligibilityInd;
            }
          }
        }



        // shipboard
        if (this.approvals[0].shipboardInd === "Y") {
          this.isChecked[0] = true;
        }
        // if (this.approvals[0].shipboardInd === "NULLIFY") {
        //   this.isDisabled[0] = true;
        // } 
        if (this.approvals[0].shipboardInd === "N") {
          this.isChecked[0] = false;
        }

        this.constructMUPForm();
      })
  }

  constructMUPForm(): void {
    const arr = this.tableForm.get('civilianincomeLtopup') as FormArray;
    arr.clear();

    this.mup.forEach((data, index) => {
      arr.push(
        this.fb.group({
          civLossAmt: data.civLossAmt,
          civLossTopUpAmt: new FormControl(data.civLossTopUpAmt),
          claimRef: data.claimRef,
          finalCivLossAmt: data.finalCivLossAmt,
          makeUpPayAmt: data.makeUpPayAmt,
        }),
      );
    }
    );
  }

