  checkPristinePayComponentMUP(input: string, rowIndex: number): string {
    let orange = '';
    console.log(this.amendmentFields);

    if (this.amendmentFields.length !== 0) {
      if (this.tableForm.get("civilianincomeLtopup").value[rowIndex].civLossTopUpAmt !== this.amendmentFields[rowIndex].civLossTopUpAmt) {
        orange = 'border-dashed border-yellow-300 border-3';
