import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { NgbActiveModal } from '@ng-bootstrap/ng-bootstrap';
import { EventManager } from 'app/core/util/event-manager.service';
import { Template } from './template.model';
import { TemplateService } from './template.service';

@Component({
  selector: 'ic-template-delete-dialog',
  templateUrl: './template-delete-dialog.component.html',
})
export class TemplateDeleteDialogComponent {
  template?: Template;

  constructor(
    protected templateService: TemplateService,
    public activeModal: NgbActiveModal,
    protected eventManager: EventManager,
    protected router: Router
  ) {}

  cancel(): void {
    this.activeModal.dismiss();
  }

  confirmDelete(id: number): void {
    this.templateService.delete(id).subscribe(() => {
      this.eventManager.broadcast('templateListModification');
      this.activeModal.close();
    });
  }
}
<form name="deleteForm" (ngSubmit)="confirmDelete(template?.id!)">
  <div class="modal-header">
    <h4 class="modal-title" icTranslate="entity.delete.title">Confirm delete operation</h4>
    <button type="button" class="close" data-dismiss="modal" aria-hidden="true" (click)="cancel()">&times;</button>
  </div>
  <div class="modal-body">
    <ic-alert-error></ic-alert-error>
    <p icTranslate="reportAdmin.template.delete.question" [translateValues]="{ id: template?.id }">
      Are you sure you want to delete this Template?
    </p>
  </div>
  <div class="modal-footer">
    <button type="submit" class="btn btn-danger btn-sm">
      <fa-icon [icon]="'times'"></fa-icon>&nbsp;<span icTranslate="entity.action.delete">Delete</span>
    </button>
    <button type="button" class="btn btn-secondary btn-sm" data-dismiss="modal" (click)="cancel()">
      <fa-icon [icon]="'ban'"></fa-icon>&nbsp;<span icTranslate="entity.action.cancel">Cancel</span>
    </button>
  </div>
</form>
