import { Component, OnInit, OnDestroy } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { HttpResponse } from '@angular/common/http';
import { Subscription, combineLatest } from 'rxjs';
import { AlertService } from 'app/core/util/alert.service';
import { EventManager } from 'app/core/util/event-manager.service';
import { ParseLinks } from 'app/core/util/parse-links.service';
import { UserToken } from 'app/admin/access-control/login-control/user-token.model';
import { UserTokenService } from 'app/admin/access-control/login-control/user-token.service';
import { ITEMS_PER_PAGE } from 'app/config/pagination.constants';
import { AccountService } from 'app/core/auth/account.service';

@Component({
  selector: 'ic-user-token-login',
  templateUrl: './user-token.component.html',
})
export class UserTokenComponent implements OnInit, OnDestroy {
  currentAccount: any;
  userTokens: UserToken[] | undefined;
  error: any;
  success: any;
  eventSubscriber?: Subscription;
  routeData: any;
  links: any;
  totalItems: any;
  queryCount: any;
  itemsPerPage = ITEMS_PER_PAGE;
  page!: number;
  ascending!: boolean;
  predicate!: string;
  previousPage: any;
  reverse: any;
  searchCriteria: any;

  constructor(
    private userTokenService: UserTokenService,
    private parseLinks: ParseLinks,
    private alertService: AlertService,
    private accountService: AccountService,
    private activatedRoute: ActivatedRoute,
    private router: Router,
    private eventManager: EventManager
  ) {
    this.searchCriteria = {
      loginId: '',
    };
  }

  loadAll(): void {
    this.userTokenService
      .query({
        loginId: this.searchCriteria.loginId,
        page: this.page - 1,
        size: this.itemsPerPage,
        sort: this.sort(),
      })
      .subscribe(
        (res: HttpResponse<any>) => this.onSuccess(res.body, res.headers),
        (res: HttpResponse<any>) => this.onError(res.body)
      );
  }

  loadPage(page: number): void {
    if (page !== this.previousPage) {
      this.previousPage = page;
      this.transition();
    }
  }

  transition(): void {
    this.router.navigate(['/admin/acm/ic-user-token'], {
      queryParams: {
        page: this.page,
        size: this.itemsPerPage,
        sort: this.predicate + ',' + (this.reverse ? 'desc' : 'asc'),
      },
    });
  }

  search(): void {
    this.links = {
      last: 0,
    };
    this.page = 0;
    this.predicate = 'createdDate';
    this.reverse = false;
    this.loadAll();
  }

  clear(): void {
    this.searchCriteria = {
      loginId: '',
    };
    this.page = 0;
    this.router.navigate([
      '/admin/acm/ic-user-token',
      {
        page: this.page,
        sort: this.predicate + ',' + (this.reverse ? 'desc' : 'asc'),
      },
    ]);
  }

  ngOnInit(): void {
    this.accountService.identity().subscribe(account => (this.currentAccount = account));
    this.handleNavigation();
    this.registerChangeInUserTokens();
  }

  ngOnDestroy(): void {
    if (this.eventSubscriber) {
      this.eventManager.destroy(this.eventSubscriber);
    }
  }

  trackId(index: number, item: UserToken): any {
    return item.id;
  }

  registerChangeInUserTokens(): void {
    this.eventSubscriber = this.eventManager.subscribe('userTokenListModification', () => this.loadAll());
  }

  sort(): any {
    const result = [this.predicate + ',' + (this.reverse ? 'desc' : 'asc')];
    if (this.predicate !== 'createdDate') {
      result.push('createdDate');
    }
    return result;
  }

  private handleNavigation(): void {
    combineLatest([this.activatedRoute.data, this.activatedRoute.queryParamMap]).subscribe(([data, params]) => {
      const page = params.get('page');
      this.page = page !== null ? +page : 1;
      const sort = (params.get('sort') ?? data['defaultSort']).split(',');
      this.predicate = sort[0];
      this.ascending = sort[1] === 'asc';
      this.loadAll();
    });
  }

  private onSuccess(data: any, headers: any): void {
    this.links = this.parseLinks.parse(headers.get('link'));
    this.totalItems = headers.get('X-Total-Count');
    this.queryCount = this.totalItems;
    this.userTokens = data;
  }

  private onError(error: any): void {
    this.alertService.addAlert({ type: 'danger', translationKey: error.message });
  }
}
<div>
  <h2>
    <span icTranslate="acmAdmin.userToken.home.title">Token Management</span>
  </h2>
  <ic-alert></ic-alert>
  <br />
  <div class="row">
    <form name="searchForm" class="col-sm-12">
      <div class="card">
        <div class="card-header"><span icTranslate="acmAdmin.home.SearchCriteria">Search Criteria</span></div>
        <div class="card-block" style="padding: 1.5%">
          <div class="rounded-pill row">
            <label class="col-sm-2 form-control-label" icTranslate="acmAdmin.userToken.loginId">Login ID</label>
            <div class="col-sm-4">
              <input type="text" class="form-control" name="loginId" id="loginId" [(ngModel)]="searchCriteria.loginId" />
            </div>
          </div>

          <div style="margin-top: 1.5%">
            <button type="button" class="btn btn-warning btn-sm" (click)="search()">
              <fa-icon [icon]="'search'"></fa-icon>
              <span class="fa fa-search" icTranslate="acmAdmin.home.search"></span>
            </button>
            <button type="button" class="btn btn-secondary btn-sm" (click)="clear()">
              <fa-icon [icon]="'trash-alt'"></fa-icon>
              <span class="fa fa-trash-o" icTranslate="acmAdmin.home.reset"></span>
            </button>
          </div>
        </div>
      </div>
    </form>
  </div>
  <br />
  <div class="card">
    <div class="card-header"><span icTranslate="acmAdmin.home.SearchResult">Search Criteria</span></div>
    <div class="card-block" style="padding: 1.5% 1.5% 0 1.5%">
      <div class="table-responsive" *ngIf="userTokens">
        <table class="table table-striped">
          <thead>
            <tr icSort [(predicate)]="predicate" [(ascending)]="reverse" [callback]="transition.bind(this)">
              <th scope="col"><span icTranslate="acmAdmin.home.sn">S/No.</span></th>
              <th scope="col" icSortBy="loginId"><span icTranslate="acmAdmin.userToken.loginId">Login ID</span> <fa-icon [icon]="'sort'"></fa-icon></th>
              <th scope="col" icSortBy="tokenType">
                <span icTranslate="acmAdmin.userToken.tokenType">Token Type</span> <fa-icon [icon]="'sort'"></fa-icon>
              </th>
              <th scope="col" icSortBy="createdDate">
                <span icTranslate="acmAdmin.userToken.createdDate">Created Date</span> <fa-icon [icon]="'sort'"></fa-icon>
              </th>
              <th scope="col" icSortBy="expiryDate">
                <span icTranslate="acmAdmin.userToken.expiryDate">Expiry Date</span> <fa-icon [icon]="'sort'"></fa-icon>
              </th>
              <th scope="col" style="text-align: center">
                <span icTranslate="acmAdmin.home.operation">Operation</span>
              </th>
            </tr>
          </thead>
          <tbody>
            <tr *ngFor="let userToken of userTokens; let idx = index; trackBy: trackId">
              <td>{{ idx + 1 }}</td>
              <td>{{ userToken.loginId }}</td>
              <td>{{ userToken.tokenType }}</td>
              <td>{{ userToken.createdDate | date: 'yyyy-MM-dd HH:mm:ss' }}</td>
              <td>{{ userToken.expiryDate | date: 'yyyy-MM-dd HH:mm:ss' }}</td>
              <td class="text-center">
                <div class="btn-group flex-btn-group-container">
                  <button
                    type="submit"
                    [routerLink]="['../ic-user-token/' + userToken.id]"
                    [replaceUrl]="true"
                    class="btn btn-danger btn-sm"
                  >
                    <fa-icon [icon]="'sign-out-alt'" [fixedWidth]="true"></fa-icon>
                    <span class="hidden-md-down" icTranslate="acmAdmin.userToken.revoke">Revoke Token</span>
                  </button>
                </div>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
      <div *ngIf="userTokens && userTokens.length">
        <div class="row justify-content-center">
          <ic-item-count [params]="{ page: page, totalItems: totalItems, itemsPerPage: itemsPerPage }"></ic-item-count>
        </div>
        <div class="row justify-content-center">
          <ngb-pagination
            [collectionSize]="totalItems"
            [pageSize]="itemsPerPage"
            [(page)]="page"
            (pageChange)="loadPage(page)"
          ></ngb-pagination>
        </div>
      </div>
    </div>
  </div>
</div>
