import { Injectable } from '@angular/core';
import { HttpClient, HttpResponse } from '@angular/common/http';
import { Observable } from 'rxjs';
import { ApplicationConfigService } from 'app/core/config/application-config.service';
import { IAdmPayroll } from 'app/administration/black-out-date/payroll-black-out/payroll-black-out.model';

export type EntityResponseType = HttpResponse<IAdmPayroll>;
export type EntityArrayResponseType = HttpResponse<IAdmPayroll[]>;

@Injectable({ providedIn: 'root' })
export class PayrollBlackOutApprovalService {
  public resourceUrl = this.applicationConfigService.getEndpointFor('api/payroll-black-out');

  constructor(protected http: HttpClient, private applicationConfigService: ApplicationConfigService) {}

  getCurrentPending(): Observable<EntityArrayResponseType> {
    return this.http.get<IAdmPayroll[]>(this.resourceUrl + "/currentPending", { observe: 'response' });
  }

  approve(admPayrollList: IAdmPayroll[]): Observable<EntityArrayResponseType> {
    return this.http.post<IAdmPayroll[]>(this.resourceUrl + "/approve", admPayrollList, { observe: 'response' });
  }

  reject(admPayrollList: IAdmPayroll[]): Observable<EntityArrayResponseType> {
    return this.http.post<IAdmPayroll[]>(this.resourceUrl + "/reject", admPayrollList, { observe: 'response' });
  }

}
