    params = params.append("nric", this.currHardCopyDetails?.headerDto?.nsManNric);
    params = params.append("caseId", this.currHardCopyDetails?.headerDto?.caseId);
    params = params.append("coyNameTypeList", coyNameTypeList.join(","));
    params = params.append("startDate", this.dateUtilsService.convertDateToStr(this.phdDeliveryFormGroup.get('startDate').value, "DD-MM-YYYY"));
    params = params.append("endDate", this.dateUtilsService.convertDateToStr(this.phdDeliveryFormGroup.get('endDate').value, "DD-MM-YYYY"));
    this.seClaimService.retrieveRefreshPhdDeliveryWD(params).subscribe(res => {
      console.log(this.workingDataPhdDeliveryCompanyList);
      if (res.data) {
        res.data.forEach(refreshOtherPhdDeliveryInfo => {
          this.workingDataPhdDeliveryCompanyList.get(coyIndexMap.get(refreshOtherPhdDeliveryInfo.coyNameType).toString() + ".workingDataPhdDeliveryInfoList." + refreshOtherPhdDeliveryInfo.weekString)?.setValue(refreshOtherPhdDeliveryInfo.weeklyIncome);
        });
      }
    });
  }
  copyToItem() {
    this.totalAmtPay = Number(this.calculateTotalDailyEarningsForAllCoy().toFixed(2));
    this.totalAmtPayEvent.emit(this.totalAmtPay);
  }
  enableCopytoItem() {
    const claimItemList = this.itemDetailsSection.itemForm?.get('otherSEItemArr');
    let aveItem = null;
    if (claimItemList.length > 0) {
      aveItem = claimItemList?.controls.filter(x => x.get('itemType').value === 'AVE')[0];
      return aveItem?.get('latestStatus').value === this.PEND_VERIFY_STATUS || aveItem?.get('latestStatus').value === this.PEND_ACTION_STATUS;
    } else return false;
  }
  isActiveAndValid() {
    return this.currSelectedOption?.obj?.archiveDt === 0 && !this.invalidDate;
  }
  isActive() {
    return this.currSelectedOption?.obj?.archiveDt === 0;
  }
  loadCodes() {
    this.codeService.gets('phd_delivery_coy_type').subscribe(codes => {
      this.coyCodeType = codes[0]?.map(x => {
        return {
          name: x.label['en-US'],
          code: x.codeId
        };
      });
    });
    this.codeService.gets('phd_delivery_weekly_indicator').subscribe(codes => {
      this.weeklyIndCodeType = codes[0]?.map(x => {
        return {
          name: x.label['en-US'],
          code: x.codeId
        };
      });
    });
  }
  getDescFromCode(codeTypeArray, codeTypeId) {
    return codeTypeArray?.find(x => x.code === codeTypeId)?.name;
  }
  exportToCSV(dataTable) {
    const columns = [{
      field: 'rowHeader',
      header: 'Company Name',
      index: 0
    }];
    const dtoList = [{
      rowHeader: 'Company Type'
    }, {
      rowHeader: 'Weekly/Monthly'
    }, {
      rowHeader: 'Divisor (Days)'
    }];
    const weekStrings = PrivateHirerDriverComponent.populatePhdWeeks(this.phdDeliveryFormGroup.get('startDate').value, this.phdDeliveryFormGroup.get('endDate').value, this.dateUtilsService);
    weekStrings.forEach((weekString, index) => {
      dtoList.push({
        rowHeader: "Week ".concat((index + 1).toString()).concat(": ").concat(weekString)
      });
    });
    dtoList.push({
      rowHeader: "Total Earnings"
    });
    dtoList.push({
      rowHeader: "Total Daily Earnings"
    });
    let totalDailyPhd = 0;
    let totalPhd = 0;
    let totalDailyDelivery = 0;
    let totalDelivery = 0;
    this.workingDataPhdDeliveryCompanyList.controls.forEach((control, index) => {
      const currCoyField = 'coy'.concat((index + 1).toString());
      columns.push({
        field: currCoyField,
        header: control.get('coyName').value,
        index: index + 1
