        dtoList[3 + weekIndex][currCoyField] = infoFormGroup.get(weekString).value?.toFixed(2);
        totalEarnings = totalEarnings + infoFormGroup.get(weekString).value;
      });
      const totalDailyEarnings = totalEarnings / control.get('divisor').value;
      if (control.get('coyType').value === "P") {
        totalPhd = totalPhd + totalEarnings;
        totalDailyPhd = totalDailyPhd + totalDailyEarnings;
      } else if (control.get('coyType').value === "D") {
        totalDelivery = totalDelivery + totalEarnings;
        totalDailyDelivery = totalDailyDelivery + totalDailyEarnings;
      }
      dtoList[3 + weekStrings.length][currCoyField] = totalEarnings.toFixed(2);
      dtoList[3 + weekStrings.length + 1][currCoyField] = totalDailyEarnings.toFixed(2);
    });
    dtoList.push({
      '1': 1,
      '2': 2,
      '3': 3,
      '4': 4
