    this.exportUtilsService.exportCSV(dataTable, dtoList, weekStrings.length, columns, "replacement_data_".concat((0, common_1.formatDate)(new Date(), "dd-MM-YYYY", this.localeId)));
  }
  isValidNumber(obj) {
    return !isNaN(obj);
  }
  tester() {
    console.log("the form", this.phdDeliveryFormGroup.controls);
  }
  //allows input on need basis
  hasValidAuthorityAndStation() {
    if (this.accountService.hasAnyAuthority(nspay2_constants_1.NSPAY2_ROLES.CLAIMS_VERIFIER) && this.station === nspay2_constants_1.NSPAY2_STATION.STATION_VERIFICATION) {
      return true;
    }
