import {
  Component,
  EventEmitter,
  Inject,
  Input,
  LOCALE_ID,
  OnChanges,
  OnInit,
  Output,
  SimpleChanges
} from '@angular/core';
import {SEClaimService} from "app/claim/se-claim/service/se-claim.service";
import {HttpParams} from "@angular/common/http";
import {Ns2Response} from "app/shared/model/ns2-response.model";
import {PhdDeliveryWD} from "app/claim/se-claim/model/phd-delivery.model";
import {DateUtilsService} from "app/core/util/date-utils.service";
import {AbstractControl, FormArray, FormBuilder, FormControl, FormGroup} from "@angular/forms";
import {PhdDeliveryWDCompany} from "app/claim/se-claim/model/phd-delivery-company.model";
import {PhdDeliveryWDInfo} from "app/claim/se-claim/model/phd-delivery-info.model";
import {
  RefreshPhdDeliveryInfo
} from "app/claim/se-claim/model/refresh-phd-delivery-info.model";
import {CodeView, IcCodeService} from "app/ng-connect";
import {DateValidator} from "app/shared/validator/date-validator.directive";
import {SaveSEClaimRequest} from "app/claim/se-claim/model/save-se-claim-request.model";
import {Table} from "primeng/table";
import {formatDate} from "@angular/common";
import {ExportUtilsService} from "app/core/util/export-utils.service";
import { AccountService } from 'app/core/auth/account.service';
import { NSPAY2_ROLES, NSPAY2_STATION } from 'app/nspay2.constants';

@Component({
  selector: 'n2-private-hirer-driver',
  templateUrl: './private-hirer-driver.component.html',
  styleUrls: ['./private-hirer-driver.component.scss']
})
export class PrivateHirerDriverComponent implements OnInit, OnChanges {

  @Input() seHardCopyDetails: any = {};
  @Input() selectedWDOption: any = {};
  @Input() itemDetailsSection: any = {};
  @Input() workingDataWithChanges: Map<string, any>;
  @Input() newUnsavedWorkingData: Map<string, any>;
  @Input() saveSEClaimRequest: SaveSEClaimRequest;
  //meant for routing from enquiry
  @Input() readOnly: boolean;
  @Input() station: string;

  expanded: boolean;

  currSelectedOption: any;
  currHardCopyDetails: any = {};
  @Output() totalAmtPayEvent: EventEmitter<any> = new EventEmitter<any>();

  totalAmtPay=0;
  readonly PEND_VERIFY_STATUS = "PVE";
  readonly PEND_ACTION_STATUS = "PAC";

  coyCodeType:any[] = [];
  weeklyIndCodeType:any[] = [];
  rowHeaders: string[] = [];

  phdDeliveryFormGroup: FormGroup;
  phdDeliveryWD: PhdDeliveryWD;

  invalidDate:boolean;

  constructor(public dateUtilsService: DateUtilsService,
              private seClaimService: SEClaimService,
              private formBuilder: FormBuilder,
              private codeService: IcCodeService,
              private exportUtilsService: ExportUtilsService,
              public accountService: AccountService,
              @Inject(LOCALE_ID) protected localeId: string) {

    this.expanded = true;
    this.invalidDate = true;
  }

  static createNewPhdDeliveryFormGroup(startDate: Date, endDate: Date, workingDataOption:any, dateUtilsService: DateUtilsService, formBuilder: FormBuilder): FormGroup{
    const newPhdDeliveryFormGroup: FormGroup = formBuilder.group({
      id: new FormControl(null,null),
      workingDataId: new FormControl(null,null),
      startDate: new FormControl(startDate,null),
      endDate: new FormControl(endDate,null),
      phdNip: new FormControl(40,null),
      deliveryNip: new FormControl(80,null),
      workingDataPhdDeliveryCompanyList: new FormArray([])
    },{ validators: DateValidator.dateLessThan('startDate', 'endDate')});

    const coyFormGroup:FormGroup = formBuilder.group({
      coyName: new FormControl(null, null),
      coyType: new FormControl("P", null),
      weeklyIndicator: new FormControl("Y", null),
      divisor: new FormControl(dateUtilsService.getDaysBetween(startDate, endDate) + 1, null)
    });

    const weekStrings:string[] = PrivateHirerDriverComponent.populatePhdWeeks(startDate, endDate, dateUtilsService);
    const infoFormGroup:FormGroup = formBuilder.group({});
    weekStrings.forEach((weekString: string) => {
      infoFormGroup.addControl(weekString, new FormControl(null, null));
    });
    coyFormGroup.addControl("workingDataPhdDeliveryInfoList",infoFormGroup);
    (newPhdDeliveryFormGroup.get('workingDataPhdDeliveryCompanyList') as FormArray).push(coyFormGroup);

    return newPhdDeliveryFormGroup;
  }

  static mapPhdDeliveryWDToFormGroup(phdDeliveryWD:PhdDeliveryWD, dateUtilsService: DateUtilsService, formBuilder: FormBuilder): FormGroup {
    const phdDeliveryFormGroup = formBuilder.group({
      id: new FormControl(phdDeliveryWD.id,null),
      workingDataId: new FormControl(phdDeliveryWD.workingDataId,null),
      startDate: new FormControl(phdDeliveryWD.startDate === null? null: dateUtilsService.convertStrToDate(phdDeliveryWD.startDate,"DD-MM-YYYY"),null),
      endDate: new FormControl(phdDeliveryWD.endDate === null? null: dateUtilsService.convertStrToDate(phdDeliveryWD.endDate,"DD-MM-YYYY"),null),
      phdNip: new FormControl(phdDeliveryWD.phdNip,null),
      deliveryNip: new FormControl(phdDeliveryWD.deliveryNip,null),
      workingDataPhdDeliveryCompanyList: new FormArray([])
    },{ validators: DateValidator.dateLessThan('startDate', 'endDate') });

    phdDeliveryWD.workingDataPhdDeliveryCompanyList.forEach((phdDeliveryCoy:PhdDeliveryWDCompany) => {
      const coyFormGroup:FormGroup = formBuilder.group({
        coyName: new FormControl(phdDeliveryCoy.coyName, null),
        coyType: new FormControl(phdDeliveryCoy.coyType, null),
        weeklyIndicator: new FormControl(phdDeliveryCoy.weeklyIndicator, null),
        divisor: new FormControl(phdDeliveryCoy.divisor, null)
      });
      const infoFormGroup:FormGroup = formBuilder.group({});
      phdDeliveryCoy.workingDataPhdDeliveryInfoList.forEach((phdDeliveryInfo: PhdDeliveryWDInfo) => {
        const weekString = phdDeliveryInfo.weekStartDate + " - " + phdDeliveryInfo.weekEndDate;
        infoFormGroup.addControl(weekString, new FormControl(phdDeliveryInfo.weeklyIncome, null));
      });
      // don't need to create filler weeks, since this form will never get edited.
      coyFormGroup.addControl("workingDataPhdDeliveryInfoList",infoFormGroup);
      (phdDeliveryFormGroup.get('workingDataPhdDeliveryCompanyList') as FormArray).push(coyFormGroup);
    });

    return phdDeliveryFormGroup;
  }

  static mapPhdDeliveryFormGroupToWD(fg: FormGroup, obj: any, dateUtilsService: DateUtilsService): PhdDeliveryWD{

    const workingDataPhdDeliveryCompanyList: PhdDeliveryWDCompany[] = [];
    const phdDeliveryWD: PhdDeliveryWD = {
      id: fg.get('id').value,
      workingDataId: fg.get('workingDataId').value,
      startDate: dateUtilsService.isValid(fg.get('startDate').value)? dateUtilsService.convertDateToStr(fg.get('startDate').value, "DD-MM-YYYY"): null,
      endDate: dateUtilsService.isValid(fg.get('endDate').value)? dateUtilsService.convertDateToStr(fg.get('endDate').value, "DD-MM-YYYY"): null,
      phdNip: fg.get('phdNip').value,
      deliveryNip: fg.get('deliveryNip').value,
      archiveDt: obj.archiveDt,
      workingDataPhdDeliveryCompanyList
    };

    const phdWeekSet: Set<string> = PrivateHirerDriverComponent.populatePhdWeeksSet(fg.get('startDate').value, fg.get('endDate').value, dateUtilsService);
    (fg.get('workingDataPhdDeliveryCompanyList') as FormArray).controls.forEach((control: AbstractControl) => {
      const workingDataPhdDeliveryInfoList: PhdDeliveryWDInfo[] = [];
      const phdDeliveryWDCompany:PhdDeliveryWDCompany = {
        id: null,
        coyName: control.get('coyName').value,
        coyType: control.get('coyType').value,
        weeklyIndicator: control.get('weeklyIndicator').value,
        divisor: control.get('divisor').value,
        workingDataPhdDeliveryInfoList
      }
      const infoFormGroup:FormGroup = control.get('workingDataPhdDeliveryInfoList') as FormGroup
      console.log('is this',control.get('workingDataPhdDeliveryInfoList'));
      Object.keys(infoFormGroup.controls).forEach((weekString:string) => {
        if (phdWeekSet.has(weekString)) {
          // not sure if want to also exclude storing null value to save space
          // if (phdWeekSet.has(weekString) && control.get(weekString).value !== null) {
          const phdDeliveryInfo: PhdDeliveryWDInfo = {
            id: null,
            weekStartDate: weekString.substring(0, 10),
            weekEndDate: weekString.substring(13, 23),
            weeklyIncome: infoFormGroup.get(weekString).value,
          }
          workingDataPhdDeliveryInfoList.push(phdDeliveryInfo);
        }
      });

      workingDataPhdDeliveryCompanyList.push(phdDeliveryWDCompany);
    });

    return phdDeliveryWD;
  }

  static populatePhdWeeks(startDate: Date, endDate: Date, dateUtilsService: DateUtilsService): string[]{
    const tempRowHeaders: string[] = [];
    if(dateUtilsService.isValid(startDate) && startDate!== null && dateUtilsService.isValid(endDate) && endDate!== null) {
      let date: Date = new Date(startDate);
      while (dateUtilsService.isBefore(date, endDate)) {
        const sunday = dateUtilsService.getSunForSameWeek(date);
        const weekString = dateUtilsService.convertDateToStr(date, "DD-MM-YYYY") + " - " + dateUtilsService.convertDateToStr(sunday, "DD-MM-YYYY")
        tempRowHeaders.push(weekString);
        date = new Date(sunday);
        date.setDate(date.getDate() + 1);
      }
    }
    return tempRowHeaders;
  }

  static populatePhdWeeksSet(startDate: Date, endDate: Date, dateUtilsService: DateUtilsService): Set<string>{
    const phdWeekSet: Set<string> = new Set<string>();
    if(dateUtilsService.isValid(startDate) && startDate!== null && dateUtilsService.isValid(endDate) && endDate!== null) {
      let date: Date = new Date(startDate);
      while (dateUtilsService.isBefore(date, endDate)) {
        const sunday = dateUtilsService.getSunForSameWeek(date);
        const weekString = dateUtilsService.convertDateToStr(date, "DD-MM-YYYY") + " - " + dateUtilsService.convertDateToStr(sunday, "DD-MM-YYYY")
        phdWeekSet.add(weekString);
        date = new Date(sunday);
        date.setDate(date.getDate() + 1);
      }
    }
    return phdWeekSet;
  }

  ngOnInit(): void {
    this.loadCodes();
  }

  ngOnChanges(changes: SimpleChanges): void{
    if(changes.seHardCopyDetails?.currentValue){
      this.currHardCopyDetails=changes.seHardCopyDetails.currentValue;
    }
    if(changes.selectedWDOption?.currentValue) {
      this.currSelectedOption = changes.selectedWDOption?.currentValue;
      this.retrievePhdDeliveryWD();
    }
  }

  retrievePhdDeliveryWD(): void {
    this.phdDeliveryFormGroup = undefined;
    this.phdDeliveryWD = undefined;
    //if no changes were recorded, take from db, otherwise take from local unsaved copy
    if(!this.currSelectedOption.changes && !this.currSelectedOption.newWD) {
      console.log("get from from db")
      let params = new HttpParams();
      params = params.append("workingDataId", this.currSelectedOption?.obj?.workingDataId);
      this.seClaimService.retrievePhdDeliveryWD(params)
        .subscribe((res: Ns2Response<PhdDeliveryWD>) => {
          if (res.data) {
            this.phdDeliveryWD = res.data;
            this.rowHeaders = this.populatePhdWeeksAndCheckValidDate(this.dateUtilsService.convertStrToDate(this.phdDeliveryWD.startDate, "DD-MM-YYYY"), this.dateUtilsService.convertStrToDate(this.phdDeliveryWD.endDate, "DD-MM-YYYY"));
            this.populatePhdDeliveryFormGroupWithFillerWeeks();
          }
        });
    } else if(this.currSelectedOption.changes && !this.currSelectedOption.newWD) {
      console.log("get from changes WD Map")
      this.phdDeliveryFormGroup = this.workingDataWithChanges.get(this.currSelectedOption?.obj?.workingDataId).formGroup as FormGroup;
      this.phdDeliveryFormGroup.updateValueAndValidity();
      this.rowHeaders = this.populatePhdWeeksAndCheckValidDate(this.phdDeliveryFormGroup.get('startDate').value, this.phdDeliveryFormGroup.get('endDate').value);
    } else if(this.currSelectedOption.newWD) {
      console.log("get from new WD Map");
      this.phdDeliveryFormGroup = this.newUnsavedWorkingData.get(this.currSelectedOption?.key.toString()).formGroup as FormGroup;
      this.phdDeliveryFormGroup.updateValueAndValidity();
      this.rowHeaders = this.populatePhdWeeksAndCheckValidDate(this.phdDeliveryFormGroup.get('startDate').value, this.phdDeliveryFormGroup.get('endDate').value);
    }
  }

  populatePhdDeliveryFormGroupWithFillerWeeks(): void{
    this.phdDeliveryFormGroup = this.formBuilder.group({
      id: new FormControl(this.phdDeliveryWD.id,null),
      workingDataId: new FormControl(this.phdDeliveryWD.workingDataId,null),
      startDate: new FormControl(this.phdDeliveryWD.startDate === null ? null: this.dateUtilsService.convertStrToDate(this.phdDeliveryWD.startDate,"DD-MM-YYYY"),null),
      endDate: new FormControl(this.phdDeliveryWD.endDate === null? null: this.dateUtilsService.convertStrToDate(this.phdDeliveryWD.endDate,"DD-MM-YYYY"),null),
      phdNip: new FormControl(this.phdDeliveryWD.phdNip,null),
      deliveryNip: new FormControl(this.phdDeliveryWD.deliveryNip,null),
      workingDataPhdDeliveryCompanyList: new FormArray([])
    },{ validators: DateValidator.dateLessThan('startDate', 'endDate') });

    this.phdDeliveryWD.workingDataPhdDeliveryCompanyList.forEach((phdDeliveryCoy:PhdDeliveryWDCompany) => {
      const coyFormGroup:FormGroup = this.formBuilder.group({
        coyName: new FormControl(phdDeliveryCoy.coyName, null),
        coyType: new FormControl(phdDeliveryCoy.coyType, null),
        weeklyIndicator: new FormControl(phdDeliveryCoy.weeklyIndicator, null),
        divisor: new FormControl(phdDeliveryCoy.divisor, null)
      });
      const infoFormGroup:FormGroup = this.formBuilder.group({});
      const weekSetInDb: Set<string> = new Set<string>();
      phdDeliveryCoy.workingDataPhdDeliveryInfoList.forEach((phdDeliveryInfo: PhdDeliveryWDInfo) => {
        const weekString = phdDeliveryInfo.weekStartDate + " - " + phdDeliveryInfo.weekEndDate;
        weekSetInDb.add(weekString);
        infoFormGroup.addControl(weekString, new FormControl(phdDeliveryInfo.weeklyIncome, null));
      });
      const diffRowHeaders: string[] = this.rowHeaders.filter( x => !weekSetInDb.has(x));
      //for any weeks that does not exist in db
      diffRowHeaders.forEach((diffRowHeader: string) => {
          if (infoFormGroup.get(diffRowHeader) === null) {
            console.log(diffRowHeader, "is not found, will be added for "+phdDeliveryCoy.coyName);
            infoFormGroup.addControl(diffRowHeader, new FormControl(null, null));
          }
      });

      coyFormGroup.addControl("workingDataPhdDeliveryInfoList",infoFormGroup);
      (this.phdDeliveryFormGroup.get('workingDataPhdDeliveryCompanyList') as FormArray).push(coyFormGroup);
    });

    this.phdDeliveryFormGroup.valueChanges.subscribe(() => {
      this.currSelectedOption.changes = true;
      this.saveSEClaimRequest.workingDataSectionInd = true;
      if(!this.workingDataWithChanges.has(this.currSelectedOption?.obj?.workingDataId)){
        this.workingDataWithChanges.set(this.currSelectedOption?.obj?.workingDataId,
          {
            obj: this.currSelectedOption.obj,
            formGroup: this.phdDeliveryFormGroup
          });
      }
    });

    console.log(this.phdDeliveryFormGroup);
  }

  rePopulateWeeks(): void{
    const startDate = this.phdDeliveryFormGroup.get('startDate').value;
    const endDate = this.phdDeliveryFormGroup.get('endDate').value
    console.log(this.dateUtilsService.isValid(startDate));
    console.log(this.dateUtilsService.isValid(endDate));
    const tempRowHeaders: string[] = this.populatePhdWeeksAndCheckValidDate(startDate, endDate);
    console.log(tempRowHeaders);
    const diffRowHeaders: string[] = tempRowHeaders.filter( x => !this.rowHeaders.includes(x));

    if(this.canPopulate(startDate, endDate)) {
      // add if week does not exist in form
      console.log("got come in ehre ah?")
      diffRowHeaders.forEach((diffRowHeader: string) => {
        this.workingDataPhdDeliveryCompanyList.controls.forEach((control: AbstractControl) => {
          const infoFormGroup = control.get("workingDataPhdDeliveryInfoList") as FormGroup;
          if (infoFormGroup.get(diffRowHeader) === null) {
            console.log(diffRowHeader, "is not found, will be added");
            infoFormGroup.addControl(diffRowHeader, new FormControl(null, null));
          }
        });
      });
      const recalcDivisor = this.dateUtilsService.getDaysBetween(startDate,endDate) + 1;
      this.workingDataPhdDeliveryCompanyList.controls.forEach((control: AbstractControl) => {
        if(control.get('weeklyIndicator').value === "Y"){
          control.get('divisor').setValue(recalcDivisor);
        }
      });

      //update latest rowheaders
      this.rowHeaders = tempRowHeaders;
      this.invalidDate = false;
    } else {
      this.invalidDate = true;
      this.rowHeaders = [""];
      console.log("testy",this.rowHeaders);
    }
  }

  canPopulate(startDate: any, endDate: any) {
    return this.dateUtilsService.isValid(startDate) && startDate !== null && this.dateUtilsService.isValid(endDate) && endDate !== null && !this.dateUtilsService.isBefore(endDate, startDate);
  }

  populatePhdWeeksAndCheckValidDate(startDate: Date, endDate: Date): string[]{
    const tempRowHeaders: string[] = [];
    if(this.canPopulate(startDate, endDate)) {
      let date: Date = new Date(startDate);
      while (!this.dateUtilsService.isAfter(date, endDate)) {
        const sunday = this.dateUtilsService.getSunForSameWeek(date);
        const weekString = this.dateUtilsService.convertDateToStr(date, "DD-MM-YYYY") + " - " + this.dateUtilsService.convertDateToStr(sunday, "DD-MM-YYYY")
        tempRowHeaders.push(weekString);
        date = new Date(sunday);
        date.setDate(date.getDate() + 1);
      }
      this.invalidDate = false;
    } else {
      tempRowHeaders.push("")
      this.invalidDate = true;
    }
    return tempRowHeaders;
  }

  addCompany(): void{
    if(this.workingDataPhdDeliveryCompanyList.length !== 4) {
      const coyFormGroup: FormGroup = this.formBuilder.group({
        coyName: new FormControl("", null),
        coyType: new FormControl("P", null),
        weeklyIndicator: new FormControl("Y", null),
        divisor: new FormControl(this.dateUtilsService.getDaysBetween(this.phdDeliveryFormGroup.get('startDate').value, this.phdDeliveryFormGroup.get('endDate').value) + 1, null),
      });
      const infoFormGroup:FormGroup = this.formBuilder.group({});
      this.rowHeaders.forEach((weekString: string) => {
        infoFormGroup.addControl(weekString, new FormControl(null, null));
      });
      coyFormGroup.addControl("workingDataPhdDeliveryInfoList",infoFormGroup);
      this.workingDataPhdDeliveryCompanyList.push(coyFormGroup);
    }
  }

  deleteCompany(index: number): void{
    if(this.workingDataPhdDeliveryCompanyList.length !== 1) {
      this.workingDataPhdDeliveryCompanyList.removeAt(index);
    }
  }

  toggleExpanded(): void{
    this.expanded = !this.expanded;
  }

  get workingDataPhdDeliveryCompanyList(): FormArray {
    return this.phdDeliveryFormGroup.get('workingDataPhdDeliveryCompanyList') as FormArray
  }

  calculateTotalEarningsPerCoy(formGroup: AbstractControl): number {
    let sum = 0;
    this.rowHeaders.forEach((weekString: string) => {
      sum = sum + (formGroup.get("workingDataPhdDeliveryInfoList."+weekString)?.value as number);
    });
    return sum || 0;
  }

  calculateDailyEarningsPerCoy(formGroup: AbstractControl): number {
    const sum: number = this.calculateTotalEarningsPerCoy(formGroup);
    return sum/formGroup.get('divisor')?.value || 0;
  }

  calculateTotalEarningsPerCoyType(coyType:string): number {
    let sum = 0;
    this.workingDataPhdDeliveryCompanyList.controls.forEach((control: AbstractControl) => {
      if(control.get('coyType').value === coyType){
        sum = sum + this.calculateTotalEarningsPerCoy(control);
      }
    });
    return sum || 0;
  }

  calculateDailyEarningsPerCoyType(coyType:string): number {
    let sum = 0;
    this.workingDataPhdDeliveryCompanyList.controls.forEach((control: AbstractControl) => {
      if(control.get('coyType').value === coyType){
        sum = sum + this.calculateDailyEarningsPerCoy(control);
      }
    });
    return sum || 0;
  }

  calculateTotalDailyEarningsForAllCoy(): number {
    let deliverySum = 0;
    let phdSum =0;
    this.workingDataPhdDeliveryCompanyList.controls.forEach((control: AbstractControl) => {
      if(control.get('coyType').value === 'P'){
        phdSum = phdSum + this.calculateDailyEarningsPerCoy(control);
      } else if (control.get('coyType').value === 'D') {
        deliverySum = deliverySum + this.calculateDailyEarningsPerCoy(control);
      }
    });
    return ((Number(phdSum.toFixed(2)) * this.phdDeliveryFormGroup.get('phdNip').value) + (Number(deliverySum.toFixed(2)) * this.phdDeliveryFormGroup.get('deliveryNip').value))/100 || 0;
  }

  getComputationString(): string {
    let deliverySum = 0;
    let phdSum =0;
    this.workingDataPhdDeliveryCompanyList.controls.forEach((control: AbstractControl) => {
      if(control.get('coyType').value === 'P'){
        phdSum = phdSum + this.calculateDailyEarningsPerCoy(control);
      } else if (control.get('coyType').value === 'D') {
        deliverySum = deliverySum + this.calculateDailyEarningsPerCoy(control);
      }
    });
    return "(" + String(this.phdDeliveryFormGroup.get('phdNip').value) + "% * PHD(" + phdSum.toFixed(2) + ") + " + String(this.phdDeliveryFormGroup.get('deliveryNip').value) +"% * Delivery(" + deliverySum.toFixed(2) + "))"
  }

  getEmptyColumns(): any[]{
    return new Array(4 - this.workingDataPhdDeliveryCompanyList.length);
  }

  refreshFromOtherClaims(): void {
    console.log("hi");
    const coyIndexMap: Map<string,number> = new Map<string, number>();
    const coyNameTypeList: string[] = [];

    this.workingDataPhdDeliveryCompanyList.controls.forEach((control: AbstractControl, index: number) => {
      if(control.get('weeklyIndicator').value === 'Y') {
        const coyNameType = control.get('coyName').value.concat("-").concat(control.get('coyType').value);
        coyNameTypeList.push(coyNameType);
        coyIndexMap.set(coyNameType, index);
        control.get("workingDataPhdDeliveryInfoList").reset();
      }
    });

    let params = new HttpParams();
    params = params.append("nric",this.currHardCopyDetails?.headerDto?.nsManNric);
    params = params.append("caseId",this.currHardCopyDetails?.headerDto?.caseId);
    params = params.append("coyNameTypeList",coyNameTypeList.join(","));
    params = params.append("startDate",this.dateUtilsService.convertDateToStr(this.phdDeliveryFormGroup.get('startDate').value, "DD-MM-YYYY"));
    params = params.append("endDate",this.dateUtilsService.convertDateToStr(this.phdDeliveryFormGroup.get('endDate').value, "DD-MM-YYYY"));

    this.seClaimService.retrieveRefreshPhdDeliveryWD(params)
      .subscribe((res: Ns2Response<RefreshPhdDeliveryInfo[]>) => {
        console.log(this.workingDataPhdDeliveryCompanyList);
        if (res.data) {
          res.data.forEach((refreshOtherPhdDeliveryInfo: RefreshPhdDeliveryInfo) => {
            this.workingDataPhdDeliveryCompanyList
              .get(coyIndexMap.get(refreshOtherPhdDeliveryInfo.coyNameType).toString()+ ".workingDataPhdDeliveryInfoList." +refreshOtherPhdDeliveryInfo.weekString)
              ?.setValue(refreshOtherPhdDeliveryInfo.weeklyIncome);
          });
        }
      });
  }

  copyToItem():void{
    this.totalAmtPay = Number(this.calculateTotalDailyEarningsForAllCoy().toFixed(2));
    this.totalAmtPayEvent.emit(this.totalAmtPay);
  }

  enableCopytoItem(): boolean{
    const claimItemList = this.itemDetailsSection.itemForm?.get('otherSEItemArr') as FormArray;
    let aveItem = null;
    if(claimItemList.length>0){
      aveItem = claimItemList?.controls.filter(x=> x.get('itemType').value === 'AVE')[0];
      return aveItem?.get('latestStatus').value === this.PEND_VERIFY_STATUS || aveItem?.get('latestStatus').value === this.PEND_ACTION_STATUS;
    }else return false;
  }

  isActiveAndValid(): boolean{
    return this.currSelectedOption?.obj?.archiveDt===0 && !this.invalidDate;
  }

  isActive(): boolean{
    return this.currSelectedOption?.obj?.archiveDt===0;
  }

  loadCodes(): void {

    this.codeService.gets('phd_delivery_coy_type')
      .subscribe((codes: any[]) => {
        this.coyCodeType = codes[0]?.map((x:CodeView)=> {
          return {name:x.label['en-US'], code: x.codeId}
        });
      });
    this.codeService.gets('phd_delivery_weekly_indicator')
      .subscribe((codes: any[]) => {
        this.weeklyIndCodeType = codes[0]?.map((x:CodeView)=> {
          return {name:x.label['en-US'], code: x.codeId}
        });
      });
  }

  getDescFromCode(codeTypeArray:any[], codeTypeId:string) : string {
    return codeTypeArray?.find(x => x.code === codeTypeId)?.name as string;
  }


  exportToCSV(dataTable: Table): void{

    const columns =
      [{field: 'rowHeader', header: 'Company Name', index: 0}]
    const dtoList:any[] = [
      {rowHeader: 'Company Type'},
      {rowHeader: 'Weekly/Monthly'},
      {rowHeader: 'Divisor (Days)'}
    ];

    const weekStrings: string[] = PrivateHirerDriverComponent.populatePhdWeeks(this.phdDeliveryFormGroup.get('startDate').value, this.phdDeliveryFormGroup.get('endDate').value, this.dateUtilsService);

    weekStrings.forEach((weekString:string, index) => {
      dtoList.push({rowHeader: ("Week ".concat((index + 1).toString()).concat(": ").concat(weekString))});
    });

    dtoList.push({rowHeader: "Total Earnings"});
    dtoList.push({rowHeader: "Total Daily Earnings"});

    let totalDailyPhd = 0;
    let totalPhd = 0;
    let totalDailyDelivery = 0;
    let totalDelivery = 0;

    this.workingDataPhdDeliveryCompanyList.controls.forEach((control:AbstractControl, index:number) => {

      const currCoyField = 'coy'.concat((index+1).toString());

      columns.push({
        field: currCoyField,
        header: control.get('coyName').value,
        index: (index+1)
      });

      dtoList[0][currCoyField] = this.getDescFromCode(this.coyCodeType, control.get('coyType').value);
      dtoList[1][currCoyField] = this.getDescFromCode(this.weeklyIndCodeType, control.get('weeklyIndicator').value);
      dtoList[2][currCoyField] = control.get('divisor').value;

      const infoFormGroup:FormGroup = control.get('workingDataPhdDeliveryInfoList') as FormGroup

      let totalEarnings = 0;

      weekStrings.forEach((weekString:string, weekIndex:number) => {
        dtoList[3+weekIndex][currCoyField] = (infoFormGroup.get(weekString).value as number)?.toFixed(2);
        totalEarnings = totalEarnings + (infoFormGroup.get(weekString).value as number);
      });

      const totalDailyEarnings = totalEarnings/control.get('divisor').value;

      if(control.get('coyType').value === "P"){
        totalPhd = totalPhd + totalEarnings;
        totalDailyPhd = totalDailyPhd + totalDailyEarnings;
      } else if(control.get('coyType').value === "D") {
        totalDelivery = totalDelivery + totalEarnings;
        totalDailyDelivery = totalDailyDelivery + totalDailyEarnings;
      }
      dtoList[3+weekStrings.length][currCoyField] = totalEarnings.toFixed(2);
      dtoList[3+weekStrings.length + 1][currCoyField] = totalDailyEarnings.toFixed(2);
    });

    dtoList.push({'1':1,
      '2':2,
      '3':3,
        '4':4});

    this.exportUtilsService.exportCSV(dataTable, dtoList,weekStrings.length, columns,
      "replacement_data_".concat(formatDate(new Date(),"dd-MM-YYYY", this.localeId)));
  }

  isValidNumber(obj: any): boolean{
    return !isNaN(obj);
  }

  tester(){
    console.log("the form", this.phdDeliveryFormGroup.controls)
  }

   //allows input on need basis
   hasValidAuthorityAndStation(): boolean{
    if(this.accountService.hasAnyAuthority(NSPAY2_ROLES.CLAIMS_VERIFIER) && 
      this.station === NSPAY2_STATION.STATION_VERIFICATION){
        return true;
      }
    if(this.accountService.hasAnyAuthority(NSPAY2_ROLES.CLAIMS_APPROVER) && 
    this.station === NSPAY2_STATION.STATION_CERTIFICATION) {
      return false;
    }
    if(this.accountService.hasAnyAuthority(NSPAY2_ROLES.CLAIMS_APPROVER) && 
    this.station === NSPAY2_STATION.STATION_APPROVAL) {
      return false;
    }
    return false;
  }
}


