      </div>
    </div>
    <div class = "grid col-12" *ngIf = "this.phdDeliveryFormGroup.hasError('invalidDateLessThan')">
      <label class = "flex col-1 align-items-center pl-1" [style] = "{'flex-basis':'100px'}"></label>
      <div class = "col">
        <small
        class="p-error" icTranslate="nspay2App.claim.validation.CLM-V004"
        [translateValues]="{ endField: 'Period End', startField : 'Period Start' }"></small>
      </div>
    </div>

    <div formArrayName = "workingDataPhdDeliveryCompanyList">
      <p-table #phdDeliveryTable
      [value] = "rowHeaders"
      styleClass="p-datatable-gridlines p-datatable-striped">
      <ng-template pTemplate="header">
        <tr>
          <th scope="col"></th>
          <th scope="col" colspan = "1" [style] ="{'border-right' :'none'}">
            <p-button icon="pi pi-file-o" (click) = "exportToCSV(phdDeliveryTable)" [style]= "{'margin-left': '.5em', 'margin-right': '.5em'}"></p-button>
          </th>
          <th scope="col" colspan = "3" [style] = "{'text-align':'right', 'border-left' :'none'}">
            <p-button *ngIf = "isActiveAndValid() && !readOnly && hasValidAuthorityAndStation()" (click) = "refreshFromOtherClaims()">Refresh from Other Claim</p-button>
          </th>
        </tr>
        <tr>
          <th scope="col" [style] = "{'font-weight':'normal'}">Company Name</th>
          <th scope="col" *ngFor="let coy of this.workingDataPhdDeliveryCompanyList.controls; let i = index"
              [style] = "{'text-align':'center'}"
              [formGroupName] = "i">
            <div *ngIf = "isActiveAndValid() && !readOnly && hasValidAuthorityAndStation()">
              <input type="text" pInputText [style] ="{'text-align':'center','width':'74%','min-width':'74%'}" formControlName = coyName>
              <p-button *ngIf = "this.workingDataPhdDeliveryCompanyList.length !== 1" (click) = "deleteCompany(i)" icon="pi pi-times" [style]= "{'margin-left': '.5em'}"></p-button>
            </div>
            <div *ngIf ="!isActiveAndValid() || readOnly || !hasValidAuthorityAndStation()">{{coy.get('coyName').value}}</div>
          </th>
          <th scope="col" *ngFor = "let col of getEmptyColumns()" class = "empty-columns">
          </th>
        </tr>
      </ng-template>
      <ng-template pTemplate="body" let-rowHeader let-rowIndex="rowIndex" >
        <tr *ngIf = "rowIndex === 0">
          <td>Company Type</td>
          <td *ngFor="let coy of this.workingDataPhdDeliveryCompanyList.controls; let i = index"
              [formGroupName] = "i">
            <p-dropdown *ngIf = "isActiveAndValid() && !readOnly && hasValidAuthorityAndStation()"
              [style]="{'width':'100%','min-width':'100%'}"
              [options] = "coyCodeType"
              formControlName = coyType
              optionLabel="name"
              optionValue="code">
            </p-dropdown>
            <div *ngIf = "!isActiveAndValid() || readOnly  || !hasValidAuthorityAndStation()">
              {{getDescFromCode(this.coyCodeType, coy.get('coyType').value)}}
            </div>
          </td>
          <td *ngFor = "let col of getEmptyColumns()" class = "empty-columns">
          </td>
        </tr>
        <tr *ngIf = "rowIndex === 0 && expanded">
          <td>Weekly/Monthly</td>
          <td *ngFor="let coy of this.workingDataPhdDeliveryCompanyList.controls; let i = index"
              [formGroupName] = "i">
            <p-dropdown *ngIf = "isActiveAndValid() && !readOnly && hasValidAuthorityAndStation()"
              [style]="{'width':'100%','min-width':'100%'}"
              [options] = "weeklyIndCodeType"
              formControlName = weeklyIndicator
              optionLabel="name"
              optionValue="code">
            </p-dropdown>
            <div *ngIf = "!isActiveAndValid() || readOnly || !hasValidAuthorityAndStation() ">
              {{getDescFromCode(this.weeklyIndCodeType, coy.get('weeklyIndicator').value)}}
            </div>
          </td>
          <td *ngFor = "let col of getEmptyColumns()" class = "empty-columns">
          </td>
        </tr>
        <tr *ngIf = "rowIndex === 0 && expanded">
          <td>Divisor (Days)</td>
          <td *ngFor="let coy of this.workingDataPhdDeliveryCompanyList.controls; let i = index"
              [formGroupName] = "i">
            <div *ngIf = "coy.get('weeklyIndicator').value === 'Y' || !isActiveAndValid() || readOnly || !hasValidAuthorityAndStation()"
                 [style] = "{'text-align':'right'}">
              {{coy.get('divisor').value}}
            </div>
            <p-inputNumber *ngIf = "coy.get('weeklyIndicator').value === 'N' && isActiveAndValid() && !readOnly && hasValidAuthorityAndStation()"
                           [style] = "{'text-align':'right','width':'100%','min-width':'100%'}"
                           [min]="0"
                           [max]="1000"
                           [maxlength] = "3"
                           formControlName = divisor>
            </p-inputNumber>
          </td>
          <td *ngFor = "let col of getEmptyColumns()" class = "empty-columns">
          </td>
        </tr>
        <tr *ngIf = "expanded && !invalidDate">
          <td>Week {{rowIndex+1}}: {{rowHeader}}</td>
          <td *ngFor="let coy of this.workingDataPhdDeliveryCompanyList.controls; let i = index"
              [formGroupName] = "i">
            <div *ngIf = "isActiveAndValid() && !readOnly && hasValidAuthorityAndStation()"
                 formArrayName = "workingDataPhdDeliveryInfoList">
                  <p-inputNumber
                    formControlName = {{rowHeader}}
                    [style] = "{'text-align':'right','width':'100%','min-width':'100%'}"
                    mode="decimal"
                    [minFractionDigits]="2">
                  </p-inputNumber>
                  <small *ngIf="(coy?.get('workingDataPhdDeliveryInfoList')).get(rowHeader)?.touched &&
                  !isValidNumber((coy?.get('workingDataPhdDeliveryInfoList')).get(rowHeader)?.value)"
                  class="p-error" icTranslate="nspay2App.claim.validation.CLM-V002"
                  [translateValues]="{ field: 'Week '.concat((rowIndex+1).toString()).concat(' Income') }">
                  </small>
            </div>
            <div *ngIf = "!isActiveAndValid() || readOnly || !hasValidAuthorityAndStation()" [style] = "{'text-align':'right'}">
              {{coy.get("workingDataPhdDeliveryInfoList."+rowHeader).value}}
            </div>
          </td>
          <td *ngFor = "let col of getEmptyColumns()" class = "empty-columns">
          </td>
        </tr>
        <tr *ngIf = "expanded && invalidDate">
          <td>Week NA:</td>
          <td *ngFor="let coy of this.workingDataPhdDeliveryCompanyList.controls; let i = index"
              [style] = "{'text-align':'right'}">
              -
          </td>
          <td *ngFor = "let col of getEmptyColumns()" class = "empty-columns">
          </td>
        </tr>
          <tr *ngIf = "rowIndex === rowHeaders?.length - 1">
            <td>Total Earnings</td>
            <td *ngFor="let coy of this.workingDataPhdDeliveryCompanyList.controls; let i = index"
                [style]="{'border-bottom': '2px solid black', 'border-top': '2px solid black', 'text-align':'right'}"
                [formGroupName] = "i">
              {{calculateTotalEarningsPerCoy(coy) | number : '1.2-2'}}
            </td>
            <td *ngFor = "let col of getEmptyColumns()" class = "empty-columns">
            </td>
          </tr>
          <tr *ngIf = "rowIndex === rowHeaders?.length - 1">
            <td>Total Daily Earnings</td>
            <td *ngFor="let coy of this.workingDataPhdDeliveryCompanyList.controls; let i = index"
                [style]="{'border-bottom': '2px solid black', 'border-top': '2px solid black', 'text-align':'right'}"
                [formGroupName] = "i">
              {{calculateDailyEarningsPerCoy(coy) | number : '1.2-2'}}
            </td>
            <td *ngFor = "let col of getEmptyColumns()" class = "empty-columns-last-row">
            </td>
          </tr>
      </ng-template>
    </p-table>
    </div>
    <hr>
      <div>
        <div class = "grid gap-4">
          <div class = "col-4 flex align-items-center flex-grow-1">
            Total Earnings For All PHD Companies:
          </div>
          <div class = "col-1 flex align-items-center justify-content-end flex-grow-1">
            {{calculateTotalEarningsPerCoyType("P") | number : '1.2-2'}}
          </div>
          <div class = "col-4 flex align-items-center flex-grow-1">
            Total Earnings For All Delivery Companies:
          </div>
          <div class = "col-1 flex align-items-center justify-content-end flex-grow-1">
            {{calculateTotalEarningsPerCoyType("D") | number : '1.2-2'}}
          </div>
        </div>
        <div class = "grid gap-4">
          <div class = "col-4 flex align-items-center flex-grow-1">
            Total Daily Earnings For All PHD Companies:
          </div>
          <div class = "col-1 flex align-items-center justify-content-end flex-grow-1">
            {{calculateDailyEarningsPerCoyType("P") | number : '1.2-2'}}
          </div>
          <div class = "col-4 flex align-items-center flex-grow-1">
            Total Daily Earnings For All Delivery Companies:
          </div>
          <div class = "col-1 flex align-items-center justify-content-end flex-grow-1">
            {{calculateDailyEarningsPerCoyType("D") | number : '1.2-2'}}
          </div>
        </div>
        <div class = "grid gap-4">
          <div *ngIf= "isActive() && !readOnly && hasValidAuthorityAndStation()" class = "col-4 flex align-items-center flex-grow-1">
            <div class = "flex-shrink-0">
              PHD NIP:
            </div>
            <p-inputNumber [style] = "{'padding-left':'1em'}"
                           [min]="0"
                           [max]="100"
                           [maxlength] = "4"
                           suffix = "%"
                           [size] = "3"
                           formControlName = phdNip>
            </p-inputNumber>
            <small *ngIf="phdDeliveryFormGroup.get('phdNip')?.touched && !isValidNumber(phdDeliveryFormGroup.get('phdNip')?.value)"
            class="p-error" icTranslate="nspay2App.claim.validation.CLM-V002"
            [translateValues]="{ field: 'PHD NIP' }">
            </small>

          </div>
          <div *ngIf= "!isActive() || readOnly || !hasValidAuthorityAndStation()" class = "col-4 flex align-items-center flex-grow-1" >
            PHD NIP: {{this.phdDeliveryFormGroup.get('phdNip').value}}%
          </div>
          <div class = "col-1 flex align-items-center flex-grow-1"></div>
          <div *ngIf= "isActive() && !readOnly && hasValidAuthorityAndStation()" class = "col-4 flex align-items-center flex-grow-1">
            <div class = "flex-shrink-0">
              Delivery NIP:
            </div>
            <p-inputNumber [style] = "{'padding-left':'1em'}"
                           [min]="0"
                           [max]="100"
                           [maxlength] = "4"
                           suffix = "%"
                           [size] = "3"
                           formControlName = deliveryNip>
            </p-inputNumber>
            <small *ngIf="phdDeliveryFormGroup.get('deliveryNip')?.touched && !isValidNumber(phdDeliveryFormGroup.get('deliveryNip')?.value)"
            class="p-error" icTranslate="nspay2App.claim.validation.CLM-V002"
            [translateValues]="{ field: 'Delivery NIP' }">
            </small>
          </div>
          <div *ngIf= "!isActive()  || readOnly || !hasValidAuthorityAndStation()" class = "col-4 flex align-items-center flex-grow-1" >
            Delivery NIP: {{this.phdDeliveryFormGroup.get('deliveryNip').value}}%
          </div>
          <div class = "col-1 flex align-items-center flex-grow-1"></div>
        </div>
        <div class = "grid gap-4">
          <div class = "col-4 flex align-items-center flex-grow-1">
            Total Daily Earnings For All Companies:
            <div [style] = "{'padding-left':'1em'}" *ngIf = "isActive() && !readOnly && hasValidAuthorityAndStation()">
              <p-button (click)="copyToItem()" [disabled] = "!enableCopytoItem()">Copy to Item</p-button>
            </div>
          </div>
          <div class = "col-1 flex align-items-center justify-content-end flex-grow-1">
            {{calculateTotalDailyEarningsForAllCoy() | number : '1.2-2'}}
          </div>
          <div class = "col-4 flex align-items-center flex-grow-1">
            {{getComputationString()}}
          </div>
          <div class = "col-1 flex align-items-center flex-grow-1"></div>
        </div>
    </div>
    <hr>
  </form>
</div>
