import { NspConfirmationService } from 'app/shared/confirm/nsp-confirm.service';
import { Component, OnInit } from '@angular/core';
import { Title } from '@angular/platform-browser';
import { Router, ActivatedRoute } from '@angular/router';
import {CustomToastService} from 'app/layouts/service/customtoast.service';
@Component({
  selector: 'n2-black-out-date',
  templateUrl: './black-out-date.component.html',
  providers: []
})
export class BlackOutDateComponent implements OnInit {

  activeIndex = 0;

  constructor(
    private route:ActivatedRoute,
    private router:Router,
    private titleService: Title,
    private nspConfirmService :NspConfirmationService,
    private toastService: CustomToastService
  ) {}

  ngOnInit(): void {
    this.activeIndex = this.route.snapshot.data['tab']
  }

  setTitle(newTitle: string): void {
    this.titleService.setTitle(newTitle);
  }

  changeTitleBasedOnSelectedTabs(): void {
    switch(this.activeIndex) {
      case 0: {
        this.setTitle("Payroll Black Out");
        break;
      }
      case 1: {
        this.setTitle("Public Holiday");
        break;
      }
      default: {
        break;
      }
    }
  }
}
