import { Component, Inject, LOCALE_ID, OnInit, ViewChild } from '@angular/core';
import { CustomToastService } from 'app/layouts/service/customtoast.service';
import { NspConfirmationService } from 'app/shared/confirm/nsp-confirm.service';
import {
  DeductionFileUploadComponent
} from 'app/payment/deduction/deduction-file-upload/deduction-file-upload.component';
import { DeductionType, IDeduction } from 'app/payment/deduction/model/deduction';
import { HttpErrorResponse, HttpResponse, HttpStatusCode } from '@angular/common/http';
import { DeductionService } from 'app/payment/deduction/service/deduction.service';
import { FilterMetadata, LazyLoadEvent } from 'primeng/api';
import { Ns2HttpService } from 'app/shared/service/ns2-http.service';
import { IcCodeService } from 'app/ng-connect';
import { Table } from 'primeng/table';
import { Ns2Status } from 'app/shared/constants/nspapp.constants';
import { EditRemarkComponent } from 'app/shared/component/edit-remark/edit-remark.component';
import { ExportUtilsService } from 'app/core/util/export-utils.service';
import { DateUtilsService } from 'app/core/util/date-utils.service';
import { formatDate } from '@angular/common';
import moment from 'moment';
import { FileUploadService } from 'app/shared/service/file-upload.service';

@Component({
  selector: 'n2-deduction',
  templateUrl: './deduction.component.html',
  styleUrls: ['./deduction.component.scss']
})
export class DeductionComponent implements OnInit {
  deductionTypeValues: string[];
  selectedDeductionType: string;
  deductionStatusValues: string[];
  selectedDeductionStatus: string;

  deductionArray: IDeduction[];
  displayDeductionFileUploadDialog: any = { display: false };

  selectedFile: File;
  
  test: string;

  //Lazy Loading
  loading = false;
  totalRecords = 0;
  pageData: { currentPage: number } = {
    currentPage: 0
  }
  pageSize = 10;
  lazyLoadEvent: LazyLoadEvent;
  
  DeductionType: { [key: string]: DeductionType } = DeductionType;
  Ns2Status: { [key: string]: Ns2Status } = Ns2Status;
  STATUS_ACTIVE = "ACTIVE";
  STATUS_COMPLETED = "COMPLETED";

  //Edit remarks
  selected: IDeduction;
  displayEditRemarkDialog: any = { display: false };

  @ViewChild('deductionDataTable', { static: true }) deductionDataTable: Table;

  @ViewChild('deductionFileUpload', { static: true })
  private fileUploadComponent!: DeductionFileUploadComponent;

  @ViewChild('editRemark', { static: true })
  private editRemark!: EditRemarkComponent;

  constructor(
    private toastService: CustomToastService,
    private nspConfirmService: NspConfirmationService,
    private deductionService: DeductionService,
    private ns2HttpService: Ns2HttpService,
    private codeService: IcCodeService,
    private exportUtilsService: ExportUtilsService,
    private dateUtilsService: DateUtilsService,
    private fileUploadService: FileUploadService,
    @Inject(LOCALE_ID) protected localeId: string
  ) {
  }

  ngOnInit(): void {
    this.loadCodes();
  }

  loadCodes(): void {
    this.codeService.gets('deductionType,deductionStatus')
    .subscribe((codes: any[]) => {
      this.deductionTypeValues = codes[0];
      this.deductionStatusValues = codes[1];
      
      this.selectedDeductionType = this.deductionTypeValues[0];
      this.selectedDeductionStatus = this.deductionStatusValues[0];
    });
  }

  load(event: LazyLoadEvent): void {
    this.loading = true;
    this.lazyLoadEvent = event;

    const options = this.ns2HttpService.convertLazyEvent(event, this.pageData, this.pageSize);
      
    this.deductionService.get(options).subscribe(
      (res: HttpResponse<any>) => {
        // Pipe response to an array of Interface model
        this.deductionArray = res.body?.data?.content;
        console.log ("deductionArray", this.deductionArray)
        this.loading = false;
        this.totalRecords = res.body.data.totalElements;
      },
      (error: HttpErrorResponse) => {
        console.log(error.error?.error_desc);
        this.loading = false;
      }
    );
  }
  
  onPage(event: any): void {
    this.pageSize = event.rows;
    this.pageData.currentPage = event.first / event.rows;
  }

  onOpen(dialog: any): void {
    dialog.display = true;
  }

  search(): void {
    console.log(this.selectedDeductionType);
    console.log(this.selectedDeductionStatus);

    this.deductionDataTable.filter(this.selectedDeductionType, 'deductionType', 'contains');
    this.deductionDataTable.filter(this.selectedDeductionStatus, 'status', 'contains');
    
  }

  trigger(deductionRecordId: string, event: Event, key: string): void {
    console.log('Triggered', deductionRecordId);
    
    this.nspConfirmService.onConfirmPopup(event, key, null, () => {
      this.deductionService.trigger(deductionRecordId).subscribe(
        (res: HttpResponse<any>) => {
          this.toastService.success("Triggered result file generation sucessfully");

          this.load(this.lazyLoadEvent);
        },
        (error: HttpErrorResponse) => {
          console.log(error.error?.error_desc);
        }
      );
    });
  }

  download(deductionRecordId: string, event: Event, key: string){
    this.nspConfirmService.onConfirmPopup(event, key, null, () => {
      
      this.deductionService.download(deductionRecordId, {responseType: 'blob'}).subscribe(
        (res: HttpResponse<any>) => {
          if(res) {
            const data = res;
            const contentDispositionHeader: string = data.headers.get('content-disposition');
            const fileName = contentDispositionHeader.split(';')[1].trim().split('=')[1].replace(/"/g, '');        
            const blob = new Blob([data.body], { type: data.headers.get('content-type')});
            const anchor = document.createElement("a");
            anchor.href = URL.createObjectURL(blob);
            anchor.download = fileName;
            anchor.click();

            this.load(this.lazyLoadEvent);
          }
        },
        (error: HttpErrorResponse) => {
          console.log(error.error?.error_desc);
        }
      );
    });
  }

  onSubmit(event: Event, key: string, dialog: any): void {    
    const request = {
      fileTxnId: this.fileUploadComponent.getVerifiedFiles().map(x=>
        String(x.meta_data.id)
      )[0]
    }

    this.nspConfirmService.onConfirmPopup(event, key, dialog, () => {
      this.deductionService.upload(request).subscribe(
        (res: HttpResponse<any>) => {
          // this.toastService.success("Deduction file uploaded successfully");
          this.toastService.success(res.body?.success_message);

          //Reload table
          this.load(this.lazyLoadEvent);
        },
        (error: HttpErrorResponse) => {
          this.toastService.error(error.error?.error_desc);
        }
      );
    });
  }

  downloadDMS(fileId: string): void {
    //TODO download via DMS
    if(fileId) {
      this.fileUploadService.downloadTxnId(fileId).subscribe(
        res => {
          if(res) {
            const data = res;
            const contentDispositionHeader: string = data.headers.get('content-disposition');
            const fileName = contentDispositionHeader.split(';')[1].trim().split('=')[1].replace(/"/g, '');        
            const blob = new Blob([data.body], { type: data.headers.get('content-type')});
            const anchor = document.createElement("a");
            anchor.href = URL.createObjectURL(blob);
            anchor.download = fileName;
            anchor.click();
          }
        },
        error => {

          this.toastService.error(error.error?.error_desc);
        }
      );
    }
  }
  
  onCancel(): void {
    this.fileUploadComponent.cancel();
  }

  isFormValid(): boolean {
    return this.fileUploadComponent.isFormValid();
  }

  onOpenEditRemark(dialog: any, deductionFileRecord: IDeduction): void {
    dialog.display = true;
    this.selected = deductionFileRecord;
    this.editRemark.remarksValue = deductionFileRecord.remarks;
  }

  onEditRemarkSubmit(event: Event, key: string, dialog: any): void {
    this.nspConfirmService.onConfirmPopup(event, key, dialog, () => {
      const fields = {"remarks": this.editRemark.remarksValue};

      this.deductionService.patch(this.selected.id, fields).subscribe(
        (res: HttpResponse<any>) => {
          this.toastService.success(res.body?.success_message);
          this.fileUploadComponent.clearAndReset();
        },
        (error: HttpErrorResponse) => {
          this.toastService.error(error.error?.error_desc);
        }
      );
    });
  }


  //Exprot CSv
  exportCSV(dataTable: Table): void {
    const options = this.ns2HttpService.convertLazyEvent(this.lazyLoadEvent, 0, this.totalRecords);

    this.deductionService.get(options).subscribe(
      resp => {
        if(resp.body?.data?.content) {

          const data = resp.body?.data?.content;

          const columns = 
          [
            {field: 'sn', header: 'SN', index: 0},
            {field: 'uploadedBy', header: 'Uploaded By', index: 1},
            {field: 'type', header: 'Type', index: 2},
            {field: 'triggeredBy', header: 'Triggered By', index: 3},
            {field: 'downloadedBy', header: 'Downloaded By', index: 4},
            {field: 'collectionCount', header: 'Collection Count', index: 5},
            {field: 'collectionAmount', header: 'Collection Amount', index: 6},
            {field: 'status', header: 'Status', index: 7},
            {field: 'remarks', header: 'Remarks', index: 8}
          ];

          const dtoList:any[] = [];
          console.log("data", data);
          data.forEach((d: IDeduction, index: number) => {
            const dto = {
              sn: index + 1,
              uploadedBy: `${String(this.dateUtilsService.formatStrDate(d.uploadedDt, "DD-MM-YYYY (HHmm)"))} ${String(d.uploadedBy ? d.uploadedBy : "")}`,
              type: this.DeductionType[d.deductionType],
              triggeredBy: `${String(this.dateUtilsService.formatStrDate(d.triggeredDt, "DD-MM-YYYY (HHmm)"))} ${String(d.triggeredBy ? d.triggeredBy : "")}`,
              downloadedBy: `${String(this.dateUtilsService.formatStrDate(d.downloadedDt, "DD-MM-YYYY (HHmm)"))} ${String(d.downloadedBy ? d.downloadedBy : "")}`,
              collectionCount: d.collectedCount,
              collectionAmount: d.collectedAmount,
              status: this.Ns2Status[d.status],
              remarks: `${String(this.dateUtilsService.formatStrDate(d.remarksDt, "DD-MM-YYYY (HHmm)"))} ${String(d.remarksBy ? d.remarksBy : "")} ${String(d.remarks ? d.remarks : "")}`,
            }
            dtoList.push(dto);
          });
          this.exportUtilsService.exportCSV(dataTable, dtoList,resp.body.data.total_no_of_elems, columns, 
            "data_".concat(formatDate(new Date(),"dd-MM-YYYY", this.localeId)));
        }
      },
      error => {
        console.log(error.error?.error_desc);
        this.loading = false;
      })
  }


  dateCallbackFilter(input: any, filter: (a: any) => void, id: string, table: Table): void {
    filter(new Date(input).toISOString());

    //Patch back value without triggering filter
    const filterMetadata = table.filters[id] as FilterMetadata[];
    filterMetadata[0].value = input;
  }
}
<div class='grid'>
  <div class='col-12'>
    <p-panel header='Search'>
      <div class='flex align-items-center gap-2'>
        <div class='search-label'>Type</div>
        <div class='search-input-text'>
          <p-dropdown [options]='deductionTypeValues' [(ngModel)]='selectedDeductionType' optionLabel="label.en-US" optionValue="codeId"></p-dropdown>
        </div>

        <div class='search-label'>Status</div>
        <div class='search-input-text'>
          <p-dropdown [options]='deductionStatusValues' [(ngModel)]='selectedDeductionStatus' optionLabel="label.en-US" optionValue="codeId"></p-dropdown>
        </div>

        <button pButton label='Search' (click)='search()'></button>
      </div>
    </p-panel>
  </div>
</div>

<p-table #deductionDataTable [value]='deductionArray' styleClass='p-datatable-gridlines' [paginator]='true' [rows]='5'
        [rowsPerPageOptions]='[10,25,50]'
        [showCurrentPageReport]='true'
        currentPageReportTemplate='Showing {first} to {last} of {totalRecords} entries'
        [lazy]="true"
        [totalRecords]='totalRecords'
        [loading]='loading'
        (onLazyLoad)='load($event)'
        (onPage) = "onPage($event)">
  <ng-template pTemplate='caption'>
    <div class='grid'>
      <div class='col-6 nsp-align-left flex justify-content-start align-items-center'>
        <button type='button' pButton pRipple icon='pi pi-file-o' (click)='exportCSV(deductionDataTable)' pTooltip='Export as CSV'
                tooltipPosition='bottom'></button>
      </div>
      <div class='col-6 nsp-align-right'>
        <button pButton label='Upload new file' (click)='onOpen(displayDeductionFileUploadDialog)'
                [style]="{'margin-left': '.5em', 'margin-bottom': '.5em'}">
        </button>
      </div>
    </div>
  </ng-template>
  <ng-template pTemplate='header'>
    <tr>
      <th scope="col" class='sn-header'>SN</th>
      <th scope="col" class='uploaded-by-header' pSortableColumn='uploadedDt'>Uploaded by<p-sortIcon field='uploadedDt'></p-sortIcon>
        <p-columnFilter field="uploadedDt" display="menu" matchMode="contains" [showApplyButton]="false">
          <ng-template pTemplate="filter" let-value let-filter="filterCallback">
            <p-calendar [ngModel]="value" (onSelect)="dateCallbackFilter($event, filter, 'uploadedDt', deductionDataTable)" dateFormat="dd-mm-yy">
            </p-calendar>
          </ng-template>
        </p-columnFilter>
      </th>
      <th scope="col" class='type-header' pSortableColumn='deductionType'>Type<p-sortIcon field='deductionType'></p-sortIcon>
        <p-columnFilter field="deductionType" display="menu" matchMode="contains" [showApplyButton]="false">
          <ng-template pTemplate="filter" let-value let-filter="filterCallback">
            <p-dropdown [ngModel]="value" [options]="deductionTypeValues" (onChange)="filter($event.value)" optionLabel="label.en-US" optionValue="codeId"></p-dropdown>
          </ng-template>
        </p-columnFilter>
      </th>
      <th scope="col" class='triggered-by-header' pSortableColumn='triggeredDt'>Triggered by<p-sortIcon field='triggeredDt'></p-sortIcon>
        <p-columnFilter field="triggeredDt" display="menu" matchMode="contains" [showApplyButton]="false">
          <ng-template pTemplate="filter" let-value let-filter="filterCallback">
            <p-calendar dataType="string" [ngModel]="value" (onSelect)="dateCallbackFilter($event, filter, 'triggeredDt', deductionDataTable)" dateFormat="dd-mm-yy">
            </p-calendar>
          </ng-template>
        </p-columnFilter>
      </th>
      <th scope="col" class='downloaded-by-header' pSortableColumn='downloadedDt'>Downloaded by<p-sortIcon field='downloadedDt'></p-sortIcon>
        <p-columnFilter field="downloadedDt" display="menu" matchMode="contains">
          <ng-template pTemplate="filter" let-value let-filter="filterCallback">
            <p-calendar [ngModel]="value" (onSelect)="dateCallbackFilter($event, filter, 'downloadedDt', deductionDataTable)" dateFormat="dd-mm-yy">
            </p-calendar>
          </ng-template>
        </p-columnFilter>
      </th>
      <th scope="col" class='collection-count-header' pSortableColumn='collectedCount'>Collection Count<p-sortIcon field='collectedCount'></p-sortIcon>
        <p-columnFilter type="numeric" field="collectedCount" display="menu" matchMode="contains"></p-columnFilter>
      </th>
      <th scope="col" class='collection-value-header' pSortableColumn='collectedAmount'>Collection Amount<p-sortIcon field='collectedAmount'></p-sortIcon>
        <p-columnFilter type="numeric" field="collectedAmount" display="menu" matchMode="contains"></p-columnFilter>
      </th>
      <th scope="col" class='status-header' pSortableColumn='status'>Status<p-sortIcon field='status'></p-sortIcon>
        <p-columnFilter field="status" display="menu" matchMode="contains">
          <ng-template pTemplate="filter" let-value let-filter="filterCallback">
            <p-dropdown [ngModel]="value" [options]="deductionStatusValues" (onChange)="filter($event.value)" optionLabel="label.en-US" optionValue="codeId"></p-dropdown>
          </ng-template>
        </p-columnFilter>
      </th>
      <th scope="col" class='remark-header' pSortableColumn='remarks'>Remark<p-sortIcon field='remarks'></p-sortIcon>
        <p-columnFilter type="text" field="remarks" display="menu" matchMode="contains"></p-columnFilter>
      </th>
    </tr>
  </ng-template>

  <ng-template pTemplate='body' let-deductionRecord let-i='rowIndex'>
    <tr>
      <td>{{i + 1}}</td>
      <td>
        <a href="javascript:void(0)" (click)="downloadDMS(deductionRecord?.uploadedFileId)">{{deductionRecord?.uploadedDt | date:'dd-MM-yyyy \'(\'HHmm\')\'' | defaultString}}
        <br *ngIf='deductionRecord?.uploadedDt'>{{ deductionRecord?.uploadedBy}}
        </a>
      </td>
      <td>{{DeductionType[deductionRecord?.deductionType] | defaultString}}</td>
      <td *ngIf="deductionRecord?.triggeredDt || deductionRecord?.status !== STATUS_ACTIVE; else enableTriggerButton ">
        {{deductionRecord?.triggeredDt | date:'dd-MM-yyyy \'(\'HHmm\')\'' }}
        <br>{{ deductionRecord?.triggeredBy}}
      </td>
      <ng-template #enableTriggerButton>
        <td class='text-center'>
          <p-confirmPopup key='triggerDeductionFile'></p-confirmPopup>
          <button pButton label='Trigger' (click)="trigger(deductionRecord?.id, $event, 'triggerDeductionFile')"></button>
        </td>
      </ng-template>
      <td *ngIf='deductionRecord?.downloadedDt || deductionRecord?.status !== STATUS_COMPLETED; else enableDownloadButton'>
        <a href="javascript:void(0)" (click)="downloadDMS(deductionRecord?.uploadedFileId)">
          {{deductionRecord?.downloadedDt | date:'dd-MM-yyyy \'(\'HHmm\')\'' | defaultString}}
          <br *ngIf='deductionRecord?.downloadedDt'>{{ deductionRecord?.downloadedBy}}
        </a>
      </td>
      <ng-template #enableDownloadButton>
        <td class='text-center'>
          <p-confirmPopup key='downloadResultFile'></p-confirmPopup>
          <button pButton label='Download' (click)="download(deductionRecord?.id, $event, 'downloadResultFile')"></button>
        </td>
      </ng-template>
      <td>{{deductionRecord?.collectedCount | defaultString}}</td>
      <td>{{deductionRecord?.collectedAmount | currency:'SGD':'symbol-narrow' | defaultString }}</td>
      <td>{{Ns2Status[deductionRecord?.status] | defaultString }}</td>
      <td>
        <div class="grid">
          <div class="col-10 remark-column">
            {{deductionRecord?.remarksBy | defaultString}} {{deductionRecord?.remarksDt | date:'dd-MM-yyyy \'(\'HHmm\')\''}}<br
            *ngIf='deductionRecord?.remarksDt'>{{deductionRecord?.remarks}}
          </div>
          <div class='col'>
            <button pButton pRipple (click)='onOpenEditRemark(displayEditRemarkDialog, deductionRecord)'
              pTooltip='Edit remark'
              tooltipPosition='bottom'
              icon='pi pi-pencil'>
            </button>
         </div>
        </div>
      </td>
    </tr>
  </ng-template>

  <ng-template pTemplate='emptymessage'>
    <tr>
      <td colspan='9'>No Deduction records found.</td>
    </tr>
  </ng-template>
</p-table>

<p-dialog header='Upload Deduction File' [(visible)]='displayDeductionFileUploadDialog.display'
          [breakpoints]="{'960px': '75vw'}"
          [style]="{width: '50vw'}" [draggable]='false' [resizable]='false' [modal]='true'>

  <n2-deduction-file-upload #deductionFileUpload></n2-deduction-file-upload>

  <ng-template pTemplate='footer'>
    <div class="flex justify-content-end align-items-center gap-2">
      <div>
        <button pButton class='p-button-danger' label='Cancel' (click)='displayDeductionFileUploadDialog.display=false'></button>
      </div>
      <div>
        <p-confirmPopup key='deductionFileUpload'></p-confirmPopup>
        <button pButton label='Submit' (click)="onSubmit($event, 'deductionFileUpload', displayDeductionFileUploadDialog)"
        [disabled]="!isFormValid()"></button>
      </div>
    </div>
  </ng-template>
</p-dialog>

<p-dialog header='Edit Remark' [(visible)]='displayEditRemarkDialog.display'
          [breakpoints]="{'960px': '75vw'}"
          [style]="{width: '50vw'}" [draggable]='false' [resizable]='false' [modal]='true'>

  <n2-edit-remark [maxRemarksChar]=200 #editRemark></n2-edit-remark>

  <ng-template pTemplate='footer'>
    <button pButton class='p-button-danger' label='Cancel' (click)='displayEditRemarkDialog.display=false'
            [style]="{'margin-left': '.5em' , 'margin-bottom': '.5em'}"></button>
    <p-confirmPopup key='confirmEditRemark'></p-confirmPopup>
    <button pButton label='Submit' (click)="onEditRemarkSubmit($event, 'confirmEditRemark', displayEditRemarkDialog)"
            [style]="{'margin-left': '.5em' , 'margin-bottom': '.5em'}"></button>
  </ng-template>
</p-dialog>
