import { ChangeDetectorRef, Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { MenuItem } from 'primeng/api';
import { Subscription } from 'rxjs';
import { WorkloadInboxService } from './service/workload-inbox.service';

@Component({
  selector: 'ic-workload-inbox',
  templateUrl: './workload-inbox.component.html',
  styleUrls: ['./workload-inbox.component.scss']
})
export class WorkloadInboxComponent implements OnInit {


  gfg!: MenuItem[];
  activeItem!: MenuItem;

  role!: string[];
  selectedRowsLength!: number;
  public selectedTab: any;
  // public enableChkBoxBtn: boolean = false;
  public enableChkBoxBtn = false;
  // this.payRollRejectionComponent.selectedRows.length >0;
  private subscription!: Subscription;

  constructor(
    private workloadService: WorkloadInboxService,
    private changeDetector: ChangeDetectorRef,
    private route: ActivatedRoute,
  ) {
    this.subscription = new Subscription();

  }


  ngOnInit(): void {
    this.role = ['role-ca'];
    // this.role = ['role-cv'];
    // this.role = ['role-pv'];
    // this.role = ['role-pa'];


    // this.role = ["DEF-func-vw-claim-verifier-inbox"];
    // this.role = ["DEF-func-vw-payroll-verifier-inbox"];
    // this.role = ['DEF-func-vw-claim-approver-inbox'];
    // this.role = ["DEF-func-vw-payroll-approver-inbox"];
    // initialize with base payroll verifier role
    if (this.role.includes('role-pv')) {
      this.getPVLabelAndRecords();
    }

  }

  getPVLabelAndRecords(): void {
    this.workloadService.getPVTabCount(null).subscribe((resp: any) => {
      if (resp.body.data) {
        this.gfg = [
          {
            label: `Data Entry (${String(resp.body.data.dataEntryList)})`,
            id: 'data-entry',
            // routerLink: ['/data-entry'],
            command: (event: Event) => {
              this.selectedTab = 'data-entry';
              console.log('clicked data-entry tab', event);
            }
          },
          {
            label: `Payroll Rejection (${String(resp.body.data.payrollRejList)})`,
            id: 'payroll-rejection',

            // routerLink: ['/payroll-rejection'],
            command: (event: Event) => {
              this.selectedTab = 'payroll-rejection';
              console.log('clicked payroll-rejection tab', event);

            }
          },
          {
            label: `Payroll Notification (${String(resp.body.data.payrollNotifList)})`,
            id: 'payroll-notification',

            // routerLink: ['/payroll-notification'],
            command: (event: Event) => {
              this.selectedTab = 'payroll-notification';
              console.log('clicked payroll-notification tab', event);
            }
          },
          {
            //TODO: The number of claims in payroll approver inbox
            label: `Payroll Approver (??)`,
            id: 'payroll-approver',

            command: (event: Event) => {
              this.selectedTab = 'payroll-approver';
              console.log('clicked payroll-approver tab', event);
            }
          },
          {
            label: `Payroll  Rejection History (${String(resp.body.data.payrollRejHistList)})`,
            id: 'payroll-rejection-history',

            // routerLink: ['/payroll-rejection-history'],
            command: (event: Event) => {
              this.selectedTab = 'payroll-rejection-history';
              console.log('clicked payroll-rejection-history tab', event);

            }
          },
          {
            label: `Payroll  Notification History (${String(resp.body.data.payrollNotifHistList)})`,
            id: 'payroll-notification-history',

            // routerLink: ['/payroll-notification-history'],
            command: (event: Event) => {
              this.selectedTab = 'payroll-notification-history';
              console.log('payroll-notification-history', event);
            }
          },
          {
            label: 'New EC',
            id: 'new-ec',

            command: (event: Event) => {
              this.selectedTab = 'new-ec';
              console.log('new-ec', event);

            }
          },
          {
            label: 'New SE',
            id: 'new-se',

            command: (event: Event) => {
              this.selectedTab = 'new-se';
              console.log('new-se', event);

            }
          },
          {
            label: 'Supporting Document',
            id: 'supp-doc',

            command: (event: Event) => {
              this.selectedTab = 'supp-doc';
              console.log('supp-doc', event);
            }
          }
        ];

        // this.selectedTab = this.gfg[0].id;
        if (this.route.snapshot.paramMap.get("selectedTab")) {
          const selectedTab = this.route.snapshot.paramMap.get("selectedTab");
          console.log("selected tab is ", selectedTab)
          this.activeItem = this.gfg.find(x => x.id === selectedTab);
          this.selectedTab = selectedTab;
        } else {
          this.activeItem = this.gfg[0];
          this.selectedTab = this.activeItem.id;
        }


      }
    });
  }

  enableChkboxBtn(selectedRows: any): void {
    console.log('in parent', selectedRows);
    this.enableChkBoxBtn = selectedRows.length > 0;
    this.selectedRowsLength = selectedRows.length;
    console.log('boolean', this.enableChkBoxBtn);

  }

  changeTab(emittedTab: any) {
    console.log('Received tab content from child', emittedTab);
    this.activeItem = this.gfg.find(x=> x.id === emittedTab['selectedTab']);
    this.selectedTab = emittedTab['selectedTab'];
  }

}
