          header: 'Source',
          index: 6
        }, {
          field: 'updatedBy',
          header: 'Updated By',
          index: 7
        }, {
          field: 'date',
          header: 'Date',
          index: 8
        }, {
          field: 'supportingDoc',
          header: 'Supporting Doc',
          index: 9
        }, {
          field: 'remarks',
          header: 'Remarks',
          index: 10
        }];
        const dtoList = [];
        data.forEach((record, index) => {
          const dto = {
            sn: index + 1,
            // awaiting txn number to be included by backend
            // txnId: this.historyList.txnNo,
            txnId: "T00000",
            desc: record.refCode,
            start: record.payrollStartDt,
            end: record.payrollEndDt,
            status: record.validInd,
            source: record.refId,
            updatedBy: record.updatedBy,
            date: record.updatedDt,
            // currently support up to 1 doc
            supportingDoc: record.supportingDocs[0].fileName,
            remarks: record.remarks[0].remark
          };
          dtoList.push(dto);
        });
        this.exportUtilsService.exportCSV(table, dtoList, this.amendTotalRecords, columns, "MassUploadRecordList_".concat((0, common_1.formatDate)(new Date(), "dd-MM-YYYY", this.localeId)));
      } else {
        this.toastService.info("No records to export");
      }
    }, error => {
      console.log(error.error?.error_desc);
      this.amendLoading = false;
    });
  }
  downloadFromDMS(fileId) {
    if (fileId) {
      this.fileUploadService.downloadTxnId(fileId).subscribe(res => {
        if (res) {
          const data = res;
          const contentDispositionHeader = data.headers.get('content-disposition');
          const fileName = contentDispositionHeader.split(';')[1].trim().split('=')[1].replace(/"/g, '');
          const blob = new Blob([data.body], {
            type: data.headers.get('content-type')
          });
          const anchor = document.createElement("a");
          anchor.href = URL.createObjectURL(blob);
          anchor.download = fileName;
          anchor.click();
        }
      }, error => {
        if (error.HttpStatusCode === 404 /* HttpStatusCode.NotFound */) {
          this.toastService.error(error.error?.error_desc);
        }
      });
    }
  }
