import { Component, Input, Inject, LOCALE_ID, OnInit, ViewChild, ChangeDetectorRef } from '@angular/core';
import { viewPayrollDetailService } from '../service/view-payroll-detail.service';
import { IPayrollRecordDetail, IPayrollRecordHistory, Option, SP, MUP, NST, PayMode } from '../model/view-payroll-detail.model';
import { PayrollCodeType, testType, CodeTranslation } from '../model/view-payroll.model';
import { Ns2HttpService } from 'app/shared/service/ns2-http.service';
import { Table } from 'primeng/table';
import { FormArray, FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
// import { TimeValidator } from 'app/shared/validator/time-validator.directive';
import { DateValidator } from 'app/shared/validator/date-validator.directive';
import { HttpErrorResponse, HttpResponse, HttpStatusCode } from '@angular/common/http';
import { LazyLoadEvent } from 'primeng/api';
import { IcCodeService } from 'app/ng-connect';
import { CustomToastService } from 'app/layouts/service/customtoast.service';
import { result, StringNullableChain } from 'lodash';
import { FileUploadService } from 'app/shared/service/file-upload.service';
import { ExportUtilsService } from 'app/core/util/export-utils.service';
import { formatDate } from '@angular/common';
import { NspConfirmationService } from 'app/shared/confirm/nsp-confirm.service';
import { FileUploadValidator2 } from 'app/shared/validator/fileupload2.directive';
import { FileUploadComponent } from 'app/shared/component/file-upload/file-upload.component';
import { viewPayrollService } from '../service/view-payroll.service';
import { createHistogram } from 'perf_hooks';
import { IPayrollNSManInfo } from '../model/payroll-nsman-info.model';

@Component({
  selector: 'n2-view-payroll-detail',
  templateUrl: './view-payroll-detail.component.html',
  styleUrls: ['./view-payroll-detail.component.scss']
})
export class ViewPayrollDetailComponent implements OnInit {

  // amendment history dialog
  amendmentHistoryDialog: any = { display: false };
  amendAwardDialog: any = { display: false };
  amendComponentDialog: any = { display: false };
  amendPayModeDialog: any = { display: false };

  detailList: IPayrollRecordDetail[] = [];
  historyList: IPayrollRecordHistory[] = [];
  nsManInfo: IPayrollNSManInfo = null;
  displayNric = false;
  // resultList: IPayrollRecord[] =[];
  @Input() selectedAwardDetails: any;
  @Input() payrollID = "";
  // @Input() PayrollDescriptionDict1: { [codeId: string]: testType }= {};
  detailNric = "";
  detailName = '';
  detailRefType = '';
  cab = '';
  service = '';
  refType = '';
  gender = "";
  valid: Option[];
  servicepayeli: Option[];
  topupeli: Option[];
  // isCollapsed = true;
  submitToggle: boolean;
  submitPayComponentToggle: boolean;
  submitPayModeToggle: boolean;

  //PayComponent
  sp: SP[] = [];
  mup: MUP[] = [];
  nst: NST[] = [];
  allComponent: any[] = [];


  //total component
  totalSP: number;
  totalMUP: number;
  totalSPMUP: number;
  topupEli: string;
  nsTopup: number;
  totalPayout: number;
  computedDt: string[] = [];
  isChecked: boolean[] = [true, true];
  paycomponentisDisabled: boolean[] = [false, false];
  shipboardchecked = false;
  paycomponentid: any[] = [];

  //Pay-Mode
  paymode: PayMode[] = [];
  paymodeTotalRecord: number;
  // paymodeLazyLoadEvent!: LazyLoadEvent;
  // paymodePageSize = 10;
  paymodeLoading: boolean;
  chequechecked = false;


  //award dialog
  display: boolean;

  //Service Pay Code Type
  servicepay_typeValues: any[];
  //Top Up Eligibility Code Type
  topupeligibilty_typeValues: any[];
  //Valid Code Type
  valid_typeValues: any[];
  //x3/x4/x5 code type
  attendance_typeValues: any[];

  test_type: any[] = [];

  //CodeType dictionary for Description
  awardCodes = new Map<string, string>();
  allowanceCodes = new Map<string, string>();
  attendanceCodes = new Map<string, string>();

  PayrollDescriptionDict: { [codeId: string]: PayrollCodeType; } = {};
  PayrollDescriptionDict1: { [codeId: string]: testType } = {};
  codeTypeTslDict: { [key: string]: string } = {
    ATTENDANCE: "PRL_ATTD_CODE",
    ALLOWANCES: "PRL_ALLW_CODE",
    AWARDS: "PRL_AWD_CODE"
  };
  //TAB for accordion
  activeTab: boolean[] = [false, false, false];

  tableForm: FormGroup = this.fb.group({
    payrollStartTime: new FormControl(null, [Validators.required]),
    payrollEndTime: new FormControl(null, [Validators.required]),
    payrollStartDate: new FormControl(null, [Validators.required]),
    payrollEndDate: new FormControl(null, [Validators.required]),
    payableAmt: new FormControl(null, [Validators.required, Validators.pattern('^[0-9]*$')]),
    servicepayelig: new FormControl(null),
    topupelig: new FormControl(null),
    valid: new FormControl(null),
    description: new FormControl(null),
  }, {
    //START date cannot be less then end date or date must be same for description x3x4
    validators: [DateValidator.dateLessThan("payrollStartDate", "payrollEndDate"), DateValidator.dateEqual("payrollStartDate", "payrollEndDate"),
    DateValidator.timeLessThan("payrollStartTime", "payrollEndTime")]
    // validators: DateValidator.dateEqual("payrollStartDate", "payrollEndDate")
  });
  //validators: DateValidator.decimalPlace("amt")

  payComponentForm: FormGroup = this.fb.group({
    shipboard: new FormControl(null),
    servicepayadj: new FormControl(null),
    // servicepayadj: this.fb.array([]),
    // civilianincomeLtopup: new FormControl(null)
    civilianincomeLtopup: this.fb.array([])
  }, {});

  // mupForm: FormGroup = this.fb.group({
  //   civilianincomeLtopup: this.fb.array([])
  // })

  //amend award form
  amendAward: FormGroup = this.fb.group({
    remarks: new FormControl(null, [Validators.required, Validators.maxLength(2000)]),
  }, {
  }
  );
  // Supporting Documents Upload Form
  supportingDocsUploadForm: FormGroup = this.fb.group({
    file: new FormControl([], [FileUploadValidator2.allowableExtension(".pdf")])
  }, {});

  supportingDocsUploadForm2: FormGroup = this.fb.group({
    file: new FormControl([], [FileUploadValidator2.allowableExtension(".pdf")])
  }, {});

  supportingDocsUploadForm3: FormGroup = this.fb.group({
    file: new FormControl([], [FileUploadValidator2.allowableExtension(".pdf")])
  }, {});

  payModeForm: FormGroup = this.fb.group({
    // chequepayment: new FormControl(null)
    chequepayment: this.fb.array([])
  },
    {})

  //amend lazy loading
  amendTotalRecords: number;
  amendLoading: boolean;
  amendPageData: { amendCurrentPage: number } = {
    amendCurrentPage: 0
  }
  amendPageSize = 10;
  amendLazyLoadEvent!: LazyLoadEvent;

  @ViewChild("supportingDocsUpload", { static: true })
  private supportingDocsUpload!: FileUploadComponent;

  @ViewChild("supportingDocsUpload2", { static: true })
  private supportingDocsUpload2!: FileUploadComponent;

  @ViewChild("supportingDocsUpload3", { static: true })
  private supportingDocsUpload3!: FileUploadComponent;

  @ViewChild('viewAwardDetailTable', { static: true })
  private viewAwardDetailTable: Table;

  @ViewChild('viewAwardAmendmentHistoryTable', { static: true })
  private viewAwardAmendmentHistoryTable: Table;

  @ViewChild('viewPayModeTable', { static: true })
  private viewPayModeTable: Table;

