  // Amend History Dialog load data event
  loadAmendData(event: any): void {
    this.amendLazyLoadEvent = event;
    this.amendLoading = true;
    setTimeout(() => {
      this.viewpayrollDetailService.getAmendmentHistoryByID(this.payrollID).subscribe(
        (res: HttpResponse<any>) => {
          console.log(res);

          this.amendTotalRecords = res.body.data.length;
          this.amendLoading = false;
          const resultData = res.body.data;
          for (let i = 0; i < resultData.length; i++) {
            this.historyList[i] = resultData[i]
          }
        },
        (error: HttpErrorResponse) => {
          console.log(error);
          // this.toastService.error(error.error?.error_desc)
        }
      );
    }, 1000);
  }

  // Amend History Export Function to CSV
  amendExportCSV(table: Table): void {
    const options = this.ns2HttpService.convertLazyEvent(this.amendLazyLoadEvent, 0, this.amendTotalRecords);

    this.viewpayrollDetailService.getAmendmentHistoryByID(this.payrollID).subscribe(
      res => {
        this.amendTotalRecords = res.body.data.length;
        if (this.amendTotalRecords !== 0) {
          const data = res.body.data;
          const columns = [
            { field: 'sn', header: 'SN', index: 0 },
            { field: 'txnId', header: 'Trx No.', index: 1 },
            { field: 'desc', header: 'Description', index: 2 },
            { field: 'start', header: 'Start', index: 3 },
            { field: 'end', header: 'End', index: 4 },
            { field: 'status', header: 'Status', index: 5 },
            { field: 'source', header: 'Source', index: 6 },
            { field: 'updatedBy', header: 'Updated By', index: 7 },
            { field: 'date', header: 'Date', index: 8 },
            { field: 'supportingDoc', header: 'Supporting Doc', index: 9 },
            { field: 'remarks', header: 'Remarks', index: 10 },
          ];

          const dtoList: any[] = [];
          data.forEach((record: any, index: number) => {
            const dto = {
              sn: index + 1,
              // awaiting txn number to be included by backend
              // txnId: this.historyList.txnNo,
              txnId: "T00000",
              desc: record.refCode,
              start: record.payrollStartDt,
              end: record.payrollEndDt,
              status: record.validInd,
              source: record.refId,
              updatedBy: record.updatedBy,
              date: record.updatedDt,
              // currently support up to 1 doc
              supportingDoc: record.supportingDocs[0].fileName,
              remarks: record.remarks[0].remark,
            }
            dtoList.push(dto);
          });
          this.exportUtilsService.exportCSV(table, dtoList, this.amendTotalRecords, columns,
            "MassUploadRecordList_".concat(formatDate(new Date(), "dd-MM-YYYY", this.localeId)));
        } else {
          this.toastService.info("No records to export")
        }
      },
      error => {
        console.log(error.error?.error_desc);
        this.amendLoading = false;
      })
  }

  downloadFromDMS(fileId: string): void {
    if (fileId) {
      this.fileUploadService.downloadTxnId(fileId).subscribe(
        res => {
          if (res) {
            const data = res;
            const contentDispositionHeader: string = data.headers.get('content-disposition');
            const fileName = contentDispositionHeader.split(';')[1].trim().split('=')[1].replace(/"/g, '');
            const blob = new Blob([data.body], { type: data.headers.get('content-type') });
            const anchor = document.createElement("a");
            anchor.href = URL.createObjectURL(blob);
            anchor.download = fileName;
            anchor.click();
          }
        },
        error => {
          if (error.HttpStatusCode === HttpStatusCode.NotFound) {
            this.toastService.error(error.error?.error_desc);
          }
        }
      );
    }
  }

