  ISODateSlicer(iso: any): string {
    const isoDate = new Date(iso);
    console.log(isoDate);

    const splicedYear = isoDate.getFullYear();
    const splicedMonth = isoDate.getMonth() + 1;
    const splicedDay = isoDate.getDate();

    // const resultDate = new Date( +splicedYear, +splicedMonth, +splicedDay, +0, +0,+0);
    const resultDate = String(splicedDay) + "-" + String(splicedMonth) + "-" + String(splicedYear);
    console.log(resultDate);

    // const zoneOffset = isoDate.getTimezoneOffset() * 60 * 1000;
    // const localDT = new Date(isoDate.getDate() - zoneOffset);
    return resultDate;
  }

  // ISO String converted to local time then split into Date and Time 
  ISOUnSplicer(iso: any): string {
    const isoDate = new Date(iso);

    const splicedHour = isoDate.getHours();
    const splicedMinute = isoDate.getMinutes();

    const checkedHour = this.addZero(splicedHour);
    const checkedMinute = this.addZero(splicedMinute);

    // const resultTime = String(splicedHour)+":"+String(splicedMinute);
    const resultTime = String(checkedHour) + ":" + String(checkedMinute);
    console.log(resultTime);
    // const zoneOffset = isoDate.getTimezoneOffset() * 60 * 1000;
    // const localDT = new Date(isoDate.getTime() - zoneOffset);
    return resultTime;
  }

  addZero(Dt: any): any {
    //empty
    if (Dt < 10) {
      const newDt = "0" + String(Dt);
      return newDt;
    }
    else {
      return Dt;
    }
  }

