  //Loading Payroll Description Code Type
  loadCodes(): void {
    this.codeService.gets('PRL_ATTD_CODE,PRL_ALLW_CODE,PRL_AWD_CODE')
      .subscribe((codes: any[]) => {
        //codeId
        console.log(codes);


        //key = 0,1,2
        codes.forEach((codetypekey) => {
          //loop through arrays from codetypekey
          Object.keys(codetypekey).forEach((key) => {

            const currentId = codetypekey[Number(key)].codeId;
            const currentTypeName = codetypekey[Number(key)].codetypeId;

            if (!this.PayrollDescriptionDict1[currentId]) {
              this.PayrollDescriptionDict1[currentId] =
              {
                [currentTypeName]: ({
                  label: codetypekey[Number(key)].label['en-US']
                })
              };
            }
            else {
              let dupeDictJson = "{\n"
              for (const [superkey, value] of Object.entries(this.PayrollDescriptionDict1[currentId])) {
                dupeDictJson += '\t"' + String(superkey) + '":{ "label":"' + String(value.label) + '"},\n';
              }
              dupeDictJson += '\t"' + String(currentTypeName) + '":{ "label":"' + String(codetypekey[Number(key)].label['en-US']) + '"}\n}';

              this.PayrollDescriptionDict1[currentId] = JSON.parse(dupeDictJson)
            }

            this.PayrollDescriptionDict[codetypekey[Number(key)].codeId] = {
              label: codetypekey[Number(key)].label['en-US']
            };

          });
        });

      });

  }

