  // splices 2 datetime objects together and converts to ISO String
  dateTimeISOSplicer(timeType: string): string {
    let dateType = "";
    if (timeType === "payrollStartTime") {
      dateType = "payrollStartDate";
    }
    else {
      dateType = "payrollEndDate";
    }
    console.log(this.tableForm.controls[dateType].value);
    console.log(this.tableForm.controls[timeType].value);

    const splicedYear = this.tableForm.controls[dateType].value.substring(6, 10);
    const splicedMonth = this.tableForm.controls[dateType].value.substring(3, 5);
    const splicedDay = this.tableForm.controls[dateType].value.substring(0, 2);

    const splicedHour = this.tableForm.controls[timeType].value.substring(0, 2);
    const splicedMinute = this.tableForm.controls[timeType].value.substring(3, 5);

    const splicedPayrollDt = new Date(+ splicedYear, +splicedMonth, +splicedDay, +splicedHour, +splicedMinute, +0);
    console.log(splicedYear);
    // small error, base time is 1:9 while edited time is 01:09. excepted 01:09 for base
    console.log(splicedPayrollDt);
    const splicedISODT = splicedPayrollDt.toISOString();
    console.log(splicedISODT);
    return splicedISODT;
  }

