      this.tableForm.controls['payrollStartTime'].enable();
      this.tableForm.controls['payrollEndTime'].enable();
    }
    else {
      this.tableForm.controls['servicepayelig'].disable();
      this.tableForm.controls['topupelig'].disable();
      this.tableForm.controls['payrollStartTime'].disable();
      this.tableForm.controls['payrollEndTime'].disable();
    }
    this.tableForm.controls['servicepayelig'].markAsPristine();
    this.tableForm.controls['topupelig'].markAsPristine();
  }

