      this.nsTopup = Number(this.nst[0].topUpAmt);
      this.totalSPMUP = this.totalSP + this.totalMUP;
      this.totalPayout = this.totalSPMUP + this.nsTopup;
      console.log(this.sp);
      console.log(this.mup);
      console.log(this.nst);
      console.log(this.totalMUP);
      console.log(this.totalSP);
      console.log(this.totalSPMUP);
      console.log(this.totalPayout);
      this.constructMUPForm();
    }
  }
  //construct form for formarray
  constructMUPForm(): void {
    const arr = this.payComponentForm.get('civilianincomeLtopup') as FormArray;
    arr.clear();
    this.mup.forEach((data, index) => {
      arr.push(
        this.fb.group({
          civLossAmt: data.civLossAmt,
          civLossTopUpAmt: new FormControl(data.civLossTopUpAmt),
          claimRef: data.claimRef,
          finalCivLossAmt: data.finalCivLossAmt,
          makeUpPayAmt: data.makeUpPayAmt,
        }),
      );
    }
    );
    // this.valuechanges();
  }

  constructPayModeForm(): void {
    const arr = this.payModeForm.get('chequepayment') as FormArray;
    // ifPAYMODE = 'CHEQUE' , tick the box & disable it
    // else checkable
    // let paymodecheck="CHEQUE";
    // if(paymodecheck='CHEQUE'){
    //   console.log("test");
    // }
    arr.clear();
    this.paymode.forEach((data, index) => {
      arr.push(
        this.fb.group({
          bankCodeAcc: data.bankCodeAcc,
          bankName: data.bankName,
          id: data.id,
          offsetCSN: data.offsetCSN,
          payAmt: data.payAmt,
          payDay: data.payDay,
          payMode: data.payMode,
          payee: data.payee,
          status: data.status,
          cheque: new FormControl(this.checkChequeBoard(data.payMode))
        }),
      );
    });
  }

  //orange
  checkPristine(input: string): string {
    let orange = '';
    // const oldspa = this.sp[0].servicePayTopUpAmt;

    if (this.tableForm.get(input).dirty) {
      // const newspa = this.payComponentForm.get('servicepayadj').value as number;
      orange = 'border-dashed border-yellow-300 border-3';
      this.submitToggle = true;
    }
    else {
      orange = '';
    }
    return orange;
  }

  checkPristinePayComponent(input: string): string {
    let orange = '';
    // const oldspa = this.sp[0].servicePayTopUpAmt;

    if (this.payComponentForm.get(input).dirty) {
      // const newspa = this.payComponentForm.get('servicepayadj').value as number;
      orange = 'border-dashed border-yellow-300 border-3';
      // this.submitPayComponentToggle = true;
    }
    else {
      orange = '';
    }
    return orange;
  }
  checkPristinePayComponentMUP(input: string, rowIndex: number): string {
    let orange = '';
    // console.log(this.civilianincomeLtopup.at(rowIndex).value.civLossTopUpAmt);
    if (this.civilianincomeLtopup.at(rowIndex).get(input)?.dirty) {
      orange = 'border-dashed border-yellow-300 border-3';
      // this.submitPayComponentToggle = true;
    }
    else {
      orange = '';
    }
    // this.cdr.detectChanges();
    return orange;
  }

  checkPristinePayMode(input: string, rowIndex: number): string {
    let orange = '';
    // console.log(this.chequepayment.at(rowIndex).get(input))
    if (this.chequepayment.at(rowIndex).get(input).dirty) {
      orange = 'border-dashed border-yellow-300 border-3';
      // this.submitPayModeToggle = true;
    }
    else {
      orange = '';
    }
    return orange;
  }

  checkShipboard(input: string): string {
    let orange = '';

    for (let i = 0; i < this.sp.length; i++) {
      if (input === "checked") {
        if (this.sp[i].combatAllowanceTopUpDesc === "NA" && this.isChecked[0] === true) {
          orange = 'border-dashed border-yellow-300 border-3';
          this.submitPayComponentToggle = true;
        }
      }
    }
    return orange;
  }

  checkChequeBoard(input: string): boolean {
    // ifPAYMODE = 'CHEQUE' , tick the box & disable it
    // else checkable

    //improvement to be done. disabling of boxes 
    if (input === 'CHEQUE') {
      return true;
    }
    else {
      return false;
    }

  }

  padTo2Digits(num: number): string {
    return num.toString().padStart(2, '0');
  }

  isoToDDMMYYYY(date: Date): string {
    date = new Date(date);
    return [
      this.padTo2Digits(date.getDate()),
      this.padTo2Digits(Number(date.getMonth()) + 1),
      date.getFullYear(),
    ].join('-');
  }

  isoToHHMM(date: Date): string {
    date = new Date(date);
    const datetext = date.toLocaleTimeString();
    return datetext.split(' ')[0].slice(0, 5);
  }

  loadPayMode(): void {
    setTimeout(() => {
      this.viewpayrollDetailService.getPaymodeByID(this.payrollID).subscribe(
        (res: HttpResponse<any>) => {
          if (res.body.data) {
            console.log("PAYMODE")
            console.log(res);
            this.paymode = res.body.data;
            console.log(this.paymode);
            this.constructPayModeForm();
            // this.loading = false;
          } else {
            console.log("No Pay Mode");

          }
        },
        (error: HttpErrorResponse) => {
          console.log(error);
          // this.toastService.error(error.error?.error_desc)
        }
      );
    }, 1000);
  }

  exportPayMode(paymodetable: Table): void {
    //Export Function to CSV
    // const options = this.ns2HttpService.convertLazyEvent(this.paymodeLazyLoadEvent, 0, this.paymodeTotalRecord);
    this.viewpayrollDetailService.getPaymodeByID(this.payrollID).subscribe(
      res => {
        this.paymodeTotalRecord = res.body.data.length;
        console.log(res);
        console.log(res.body.data.length);
        if (this.paymodeTotalRecord !== 0) {
          const data = res.body.data;
          const columns = [
            { field: 'sn', header: 'SN', index: 0 },
            { field: 'chequepayment', header: 'Cheque Payment', index: 1 },
            { field: 'payday', header: 'Pay Day', index: 2 },
            { field: 'mode', header: 'Mode', index: 3 },
            { field: 'payee', header: 'Payee', index: 4 },
            { field: 'payComponent', header: 'Pay Component', index: 5 },
            { field: 'amount', header: 'Amount', index: 6 },
            { field: 'status', header: 'Status', index: 7 },
            { field: 'bankDetail', header: 'bankDetail', index: 8 },
          ];

          const dtoList: any[] = [];
          data.forEach((record: any, index: number) => {
            const dto = {
              sn: index + 1,
              chequepayment: "T00000",
              payday: record.payDay,
              mode: record.payMode,
              payee: record.payee,
              payComponent: "x",
              amount: record.payAmt,
              status: record.status,
              bankDetail: record.bankCodeAcc,
            }
            dtoList.push(dto);
          });
          this.exportUtilsService.exportCSV(paymodetable, dtoList, this.paymodeTotalRecord, columns,
            "PayMode_".concat(formatDate(new Date(), "dd-MM-YYYY", this.localeId)));
        } else {
          this.toastService.info("No records to export")
        }
      },
      error => {
        console.log(error.error?.error_desc);
        this.paymodeLoading = false;
      })
  }



