<!-- Amend dialog for History -->
<div class="col-12">
    <p-dialog header="Amendment History ({{detailNric | defaultString}} {{detailName | defaultString}})"
        [(visible)]='amendmentHistoryDialog.display' [draggable]='false' [resizable]='false' [modal]='true'
        [breakpoints]="{'960px': '75vw'}" [style]="{height: '55vw', width: '90vw'}"
        (onHide)="onClose(amendmentHistoryDialog)">

        <p-table #viewAwardAmendmentHistoryTable [value]="historyList" styleClass="p-datatable-gridlines"
            [paginator]="true" [rows]="10" [showCurrentPageReport]="true" [rowsPerPageOptions]="[10,25,50]"
            currentPageReportTemplate="Showing {first} to {last} of {totalRecords} entries" dataKey="dataKey"
            [totalRecords]="amendTotalRecords" (onPage)="onAmendPage($event)" [loading]="amendLoading" [lazy]="true"
            (onLazyLoad)="loadAmendData($event)" responsiveLayout="scroll">
            <ng-template pTemplate="caption">
                <div class="flex align-items-center justify-content-between">
                    <span style="font-size: 25px"><button type="button" pButton pRipple icon="pi pi-file"
                            (click)="amendExportCSV(viewAwardAmendmentHistoryTable)" class="mr-2" pTooltip="CSV"
                            tooltipPosition="bottom"></button></span>
                </div>
            </ng-template>
            <ng-template pTemplate="header">
                <tr>
                    <th scope="col">SN</th>
                    <th scope="col">Trx No.</th>
                    <th scope="col">Description</th>
                    <th scope="col">Start</th>
                    <th scope="col">End</th>
                    <th scope="col">Status</th>
                    <th scope="col">Source</th>
                    <th scope="col">Updated By</th>
                    <th scope="col">Date</th>
                    <th scope="col">Supporting Doc</th>
                </tr>
            </ng-template>

            <ng-template pTemplate="body" let-historyList let-i="rowIndex">
                <tr>
                    <td>{{i+1}}</td>
                    <!-- awaiting TXNNO from backend -->
                    <td>txn No.</td>
                    <!-- <td>{{historyList.txnNo}}</td> -->
                    <!-- <td>{{PayrollDescriptionDict1[historyList.refCode][codeTypeTslDict[detailRefType]].label}}</td> -->
                    <td>{{translatePayrollDescription(historyList.refCode,detailRefType)}}</td>
                    <td>{{historyList.payrollStartDt | date: 'dd-MM-yyyy (hhmm)'}}</td>
                    <td>{{historyList.payrollEndDt | date: 'dd-MM-yyyy (hhmm)'}}</td>
                    <td>{{historyList.validInd}}</td>
                    <td>{{historyList.refId}}</td>
                    <td *ngIf="historyList.createdBy === 'SYSTEM'">{{historyList.unit}}<br>{{historyList.tao}}</td>
                    <td *ngIf="historyList.createdBy !== 'SYSTEM'">
                        {{historyList.createdBy}}<br>{{historyList.updatedBy}} ({{historyList.status}})</td>
                    <td>{{historyList.updatedDt | date: 'dd-MM-yyyy (hhmm)'}}</td>
                    <!-- currently support up to 1 doc -->
                    <td>
                        <a href="javascript:void(0)" *ngIf="historyList.supportingDocs.length !== 0"
                            (click)="downloadFromDMS(historyList.supportingDocs[0].documentId)">{{historyList.supportingDocs[0].fileName
                            | defaultString}}</a>
                    </td>
                </tr>
                <tr>
                    <td colspan="2" style="background: #f8f9fa;">Remarks</td>
                    <!--TODO: remarks blank for now,refactoring needed -->
                    <td colspan="10" style="background: #f8f9fa;">
                        <div *ngFor="let remark of historyList.remarks">
                            <span>{{remark.remark}}</span><br>
                        </div>

                    </td>
                </tr>
            </ng-template>

            <ng-template pTemplate="emptymessage">
                <tr>
                    <td colspan="12">No records found.</td>
                </tr>
            </ng-template>

        </p-table>
    </p-dialog>
</div>

