<!-- payroll component attendance -->
<div *ngIf="refType === 'ATTENDANCE'">
    <p-panel styleClass="no-padding" header="Payroll Components">
        <div class="flex justify-content-end align-items-center">
            <button type="button" pButton class="p-button-link underline"
                (click)="expTab(0); expTab(1); expTab(2); expTab(3)">Expand
                All </button>
            <button type="button" pButton class="p-button-link underline"
                (click)="colTab(0); colTab(1); colTab(2); colTab(3)">Collapse
                All </button>
        </div>
        <div>
            <p-accordion>
                <p-accordionTab [(selected)]="activeTab[0]" header="Service Pay">

                    <p-table [value]="sp" styleClass="p-datatable-gridlines" responsiveLayout="scroll">
                        <ng-template pTemplate="header">
                            <tr>
                                <th scope="col" style="width: 5%"> Trg Days</th>
                                <th scope="col" style="width: 5%"> Calculated Service Pay</th>
                                <th scope="col" style="width: 5%"> Shipboard</th>
                                <th scope="col" style="width: 5%"> Nullify Vocation Allowance Status</th>
                                <th scope="col" style="width: 5%"> Vocation Allowance Adjustment</th>
                                <th scope="col" style="width: 5%"> Service Pay (With Vocation Allowance Adjustments)</th>
                                <th scope="col" style="width: 5%"> Service Pay Adjustment</th>
                                <th scope="col" style="width: 5%"> Total Service Pay</th>

                            </tr>
                        </ng-template>
                        <ng-template pTemplate="body" let-record let-i="rowIndex">
                            <tr>
                                <td>{{record.trainingDays}}</td>
                                <td>{{record.servicePayAmt}}</td>
                                <!-- <td>
                                <p-checkbox name="groupname" value="val1"></p-checkbox>
                            </td> -->
                                <td [ngClass]="checkPristinePayComponent('shipboard')">
                                    <form [formGroup]="payComponentForm">
                                        <p-checkbox [binary]="true" formControlName="shipboard"
                                            [ngModel]="shipboardchecked"></p-checkbox>
                                    </form>
                                </td>
                                <td>{{record.combatAllowanceEligibility}}</td>
                                <td>{{record.combatAllowanceTopUpAmt}}</td>
                                <td>{{record.servicePayWithAllwAdjAmt}}</td>
                                <!-- working -->
                                <td [ngClass]="checkPristinePayComponent('servicepayadj')">
                                    <form [formGroup]="payComponentForm">
                                        <input pInputText type="number" formControlName="servicepayadj">
                                    </form>
                                </td>
                                <td>{{record.totalServicePayAmt}}</td>

                            </tr>
                        </ng-template>
                    </p-table>
                </p-accordionTab>

                <p-accordionTab [(selected)]="activeTab[1]">
                    <ng-template pTemplate="header">
                        MUP
                    </ng-template>
                    <form [formGroup]="payComponentForm">
                        <div formArrayName="civilianincomeLtopup">
                            <p-table [value]="civilianincomeLtopup.controls" styleClass="p-datatable-gridlines"
                                responsiveLayout="scroll">
                                <ng-template pTemplate="header">
                                    <tr>
                                        <th scope="col" style="width: 5%"> SN</th>
                                        <th scope="col" style="width: 5%"> Claim</th>
                                        <th scope="col" style="width: 5%"> Payroll Date</th>
                                        <th scope="col" style="width: 5%"> Civilian Income Loss</th>
                                        <th scope="col" style="width: 5%"> Civilian Income Loss Top Up</th>
                                        <th scope="col" style="width: 5%"> Total Civ Income Loss</th>
                                        <th scope="col" style="width: 5%"> MUP</th>
                                    </tr>
                                </ng-template>
                                <ng-template pTemplate="body" let-record let-rowIndex="rowIndex">

                                    <tr>
                                        <td>{{rowIndex + 1}}</td>
                                        <td>{{record.value.claimRef}}</td>
                                        <td>{{computedDt[rowIndex] | date: 'dd-MM-yyyy'}}</td>
                                        <td>{{record.value.civLossAmt}}</td>
                                        <!-- civilianincomeLtopup -->
                                        <!-- <td [ngClass]="checkPristinePayComponent('civilianincomeLtopup')"> -->
                                        <!-- <td [ngClass]="checkPristinePayComponentMUP('civLossTopUpAmt',rowIndex)"> -->
                                        <td>
                                            <div [formGroupName]="rowIndex">
                                                <input type="number" pInputText class="form-control dirtyyellow"
                                                    formControlName="civLossTopUpAmt">
                                            </div>
                                        </td>

                                        <td>{{record.value.finalCivLossAmt}}</td>
                                        <td>{{record.value.makeUpPayAmt}}</td>
                                    </tr>
                                </ng-template>
                            </p-table>
                        </div>
                    </form>
                </p-accordionTab>

                <p-accordionTab [(selected)]="activeTab[2]">
                    <ng-template pTemplate="header">
                        NS Top Up
                    </ng-template>
                    <p-table [value]="nst" styleClass="p-datatable-gridlines" responsiveLayout="scroll">
                        <ng-template pTemplate="header">
                            <tr>
                                <th scope="col" style="width: 5%"> SN</th>
                                <th scope="col" style="width: 5%"> Total Service Pay + MUP</th>
                                <th scope="col" style="width: 5%"> Pro-rated Base Rate</th>
                                <th scope="col" style="width: 5%"> NS Top Up</th>
                            </tr>
                        </ng-template>
                        <ng-template pTemplate="body" let-record let-i="rowIndex">
                            <tr>
                                <td>{{i + 1}}</td>
                                <td>{{totalSPMUP}}</td>
                                <td>{{record.baseRateComputationString}} = {{record.prorateBaseRateAmt}}</td>
                                <td>{{record.topUpAmt}}</td>
                            </tr>
                        </ng-template>
                    </p-table>
                </p-accordionTab>

                <p-accordionTab [(selected)]="activeTab[3]">
                    <ng-template pTemplate="header">
                        Total Component
                    </ng-template>
                    <p-table [value]="detailList" styleClass="p-datatable-gridlines" responsiveLayout="scroll">
                        <ng-template pTemplate="header">
                            <tr>
                                <th scope="col" style="width: 5%"> Total Service Pay</th>
                                <th scope="col" style="width: 5%"> Total MUP</th>
                                <th scope="col" style="width: 5%"> Total SP + MUP</th>
                                <th scope="col" style="width: 5%"> Top Up Eligibility</th>
                                <th scope="col" style="width: 5%"> NS Top Up </th>
                                <th scope="col" style="width: 5%"> Total Entitled Payout</th>
                            </tr>
                        </ng-template>
                        <ng-template pTemplate="body" let-record let-i="rowIndex">
                            <tr>
                                <td>{{totalSP}}</td>
                                <td>{{totalMUP}}</td>
                                <td>{{totalSPMUP}}</td>
                                <td>{{topupEli | defaultString }}</td>
                                <td>{{nsTopup}}</td>
                                <td>{{totalPayout}}</td>
                            </tr>
                        </ng-template>
                    </p-table>
                </p-accordionTab>
            </p-accordion>

        </div>
        <div style="justify-content: end;" class="flex justify-content-flex align-items-right inline-block">
            <button *ngIf="payComponentForm.dirty" id="paycomsubmit" pButton label='Submit'
                (click)="paycomponentSubmit()"></button>
        </div>
    </p-panel>
</div>


