                            <th scope="col" style="width: 5%"> Mode</th>
                            <th scope="col" style="width: 10%"> Payee</th>
                            <th scope="col" style="width: 5%"> Pay Component</th>
                            <th scope="col" style="width: 5%"> Amount</th>
                            <th scope="col" style="width: 10%"> Status</th>
                            <th scope="col" style="width: 10%"> Bank Detail</th>
                        </tr>
                    </ng-template>
                    <ng-template pTemplate="body" let-record let-i="rowIndex">
                        <tr>
                            <td>{{i + 1}}</td>
                            <!-- <td [ngClass]="checkPristinePayMode('chequepayment')">
                        <form [formGroup]="payModeForm">
                            <p-checkbox [binary]="true" formControlName="chequepayment" [ngModel]="chequechecked"></p-checkbox>
                            </form>
                    </td> -->

                            <td>
                                <div [formGroupName]="i">
                                    <p-checkbox [binary]="true" formControlName='cheque' [disabled]='false'
                                        [ngClass]="checkPristinePayMode('cheque',i)"></p-checkbox>
                                </div>
                            </td>

                            <td>{{record.value.payDay}}</td>
                            <td>{{record.value.payMode}}</td>
                            <td>{{record.value.payee}}</td>
                            <td>x</td>
                            <td>{{record.value.payAmt}}</td>
                            <td>{{record.value.status}}</td>
                            <td>{{record.value.bankCodeAcc}}</td>
                        </tr>
                    </ng-template>
                </p-table>
            </div>
        </form>
        <div style="justify-content: end;" class="flex justify-content-flex align-items-right inline-block">
            <button *ngIf="(chequepayment.dirty)" pButton label='Submit' (click)="paymodeSubmit()"></button>
        </div>
        <!-- submitPayModeToggle -->
    </p-panel>
</div>
<br>

<div class="flex justify-content-center align-items-center">
    Amount Entitled: <span class="w-14rem"> {{totalPayout}}</span>
    Amount Paid: <span class="w-14rem"> 0.0</span>
    Amount Unpaid: <span class="w-14rem"> 0.0</span>
</div>
