import { Component, OnInit, OnDestroy } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { HttpResponse } from '@angular/common/http';
import { NgbActiveModal } from '@ng-bootstrap/ng-bootstrap';
import { AlertService } from 'app/core/util/alert.service';
import { EventManager } from 'app/core/util/event-manager.service';
import { Resource } from 'app/admin/access-control';
import { FunctionService } from './function.service';
import { FunctionAssignPopupService } from 'app/admin/access-control';

@Component({
  selector: 'ic-function-assign-roles-dialog',
  templateUrl: './function-assign-roles-dialog.component.html',
})
export class FunctionAssignRolesDialogComponent implements OnInit {
  isSaving?: boolean;
  applicationId?: string;
  resourceId?: string;
  searchedRoles: Resource[] = [];
  chooseedRoles: Resource[] = [];

  constructor(
    public activeModal: NgbActiveModal,
    private alertService: AlertService,
    private functionService: FunctionService,
    private eventManager: EventManager,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.isSaving = false;
  }

  clear(): void {
    this.activeModal.dismiss('cancel');
    this.backToPrevious();
  }

  assign(): void {
    this.isSaving = true;
    if (this.chooseedRoles.length > 0) {
      this.functionService.assignRoles(this.resourceId, this.chooseedRoles).subscribe(() => {
        this.activeModal.dismiss('cancel');
        this.eventManager.broadcast({ name: 'assignedRoleListModification', content: 'OK' });
        this.backToPrevious();
      });
    }
  }

  backToPrevious(): void {
    setTimeout(() => {
      this.router.navigate(['admin/acm/ic-function/' + this.resourceId?.toString()]);
    }, 0);
  }

  searchUnAssignRoles(event: any): void {
    this.functionService
      .searchUnAssignedRoles({
        applicationId: this.applicationId,
        resourceId: this.resourceId,
        roleName: event.query,
        page: 0,
        size: 1000,
        sort: null,
      })
      .subscribe(
        (res: HttpResponse<any>) => {
          this.searchedRoles = res.body;
        },
        (res: HttpResponse<any>) => this.onError(res.body)
      );
  }

  private onError(error: any): void {
    this.alertService.addAlert({ type: 'danger', translationKey: error.message });
  }
}

@Component({
  selector: 'ic-function-assign-roles-popup',
  template: '',
})
export class FunctionAssignRolesPopupComponent implements OnInit, OnDestroy {
  routeSub: any;

  constructor(private route: ActivatedRoute, private functionAssignPopupService: FunctionAssignPopupService) {}

  ngOnInit(): void {
    this.routeSub = this.route.params.subscribe(params => {
      if (params['applicationId'] && params['resourceId']) {
        this.functionAssignPopupService.open(
          FunctionAssignRolesDialogComponent as Component,
          params['applicationId'],
          params['resourceId']
        );
      }
    });
  }

  ngOnDestroy(): void {
    this.routeSub.unsubscribe();
  }
}
<div class="modal-header">
  <h4 class="modal-title" id="myGroupLabel">Assign Roles to Function</h4>
  <button type="button" class="close" data-bs-dismiss="modal" aria-hidden="true" (click)="clear()">&times;</button>
</div>

<div class="modal-body">
  <div class="rounded-pill row">
    <label class="col-sm-3 form-control-label">Search By Role Name</label>
    <div class="col-sm-9">
      <p-autoComplete
        class="ui-fluid"
        [(ngModel)]="chooseedRoles"
        name="chooseedRoles"
        [suggestions]="searchedRoles"
        (completeMethod)="searchUnAssignRoles($event)"
        field="resourceName"
        [multiple]="true"
      ></p-autoComplete>
    </div>
  </div>
</div>

<div class="modal-footer">
  <button
    type="button"
    [disabled]="isSaving || !chooseedRoles || chooseedRoles.length === 0"
    class="btn btn-primary btn-sm"
    (click)="assign()"
  >
    <fa-icon [icon]="'save'"></fa-icon>&nbsp;<span>Assign Roles</span></button
  >&nbsp;
  <button type="button" class="btn btn-secondary btn-sm" data-bs-dismiss="modal" (click)="clear()">
    <fa-icon [icon]="'ban'"></fa-icon>&nbsp;<span icTranslate="entity.action.cancel">Cancel</span>
  </button>
</div>
