import { Pipe, PipeTransform } from '@angular/core';

@Pipe({name: 'amendmentType'})
export class AmendmentTypePipe implements PipeTransform {

  readonly amendmentType : {[key: string]: string} = {
    NEW: 'Create New',
    UPD: 'Update Company Particulars',
    OOD: 'Opt Out of Direct'
  };
  

  transform(value: string): string {
    console.log("Value = " + value);

    if(value !== "" && value !== null) {
      if(value in this.amendmentType) {
        return this.amendmentType[value];
      } else {
        return value;
      }
    }

    return value;
  }
}
