import { Component, OnInit } from '@angular/core';
import {CodeView, IcCodeService} from "app/ng-connect";
import {DateUtilsService} from "app/core/util/date-utils.service";
import {SEClaimService} from "app/claim/se-claim/service/se-claim.service";
import {ActivatedRoute, Router} from "@angular/router";
import {InitialSEClaim} from "app/claim/se-claim/model/initial-se-claim.model";
import {HttpParams} from "@angular/common/http";
import {SEClaim} from "app/claim/se-claim/model/se-claim.model";
import {NSPAppConstants} from "app/shared/constants/nspapp.constants";
import {SeEclaimAdditionalInfo} from "app/claim/se-claim/model/se-eclaim-additional-info.model";
import {SEClaimItemDto} from "app/claim/se-claim/model/se-claim-item.model";

@Component({
  selector: 'n2-view-eclaim',
  templateUrl: './view-eclaim.component.html',
  styleUrls: ['./view-eclaim.component.scss']
})
export class ViewEclaimComponent implements OnInit {

  seClaimDetails: SEClaim;
  initialSEClaimDetails: InitialSEClaim;
  additionalInfo: SeEclaimAdditionalInfo;
  itemDetailsList: SEClaimItemDto[];
  params: any;
  claimOptionShort = "";
  accordionStates: boolean[] = Array(3).fill(true);
  allExpanded = true;

  constructor(
    public dateUtilsService: DateUtilsService,
    private seClaimService: SEClaimService,
    private route: ActivatedRoute,
    private codeService: IcCodeService) {
    console.log("TEST");
  }

  ngOnInit(): void {
    this.retrieveSEClaimDetails();
  }

  retrieveSEClaimDetails(){
    this.route.queryParams.subscribe((params) => {
      this.params = params;
      this.seClaimService.retrieveSEClaimDetails(this.params?.eClaimId, null).subscribe(resp => {
        if (resp.body.data) {
          this.seClaimDetails = resp.body.data;
          this.loadCodes('claim_option_short', this.seClaimDetails?.headerDto?.claimOption.toString());
          this.seClaimService.retrieveClaimItems({params:httpParams}).subscribe((res) => {
            if(res.body.data) {
              this.itemDetailsList = res.body.data;
              this.itemDetailsList.sort((a,b)=>{
                if(a.itemType==='AVE' && this.seClaimDetails?.headerDto?.claimOption === '1' ||
                  a.itemType==='ITX' && this.seClaimDetails?.headerDto?.claimOption === '2' ||
                  a.itemType==='REP' && this.seClaimDetails?.headerDto?.claimOption === '3'){
                  return -1;
                }else if(b.itemType==='AVE' && this.seClaimDetails?.headerDto?.claimOption === '1' ||
                  b.itemType==='ITX' && this.seClaimDetails?.headerDto?.claimOption === '2' ||
                  b.itemType==='REP' && this.seClaimDetails?.headerDto?.claimOption === '3'){
                  return 1;
                }else {
                  return a.itemName < b.itemName ? -1 : 1;
                }
              });
            }
          });
        }
      });

      console.log(this.params);
      let httpParams: HttpParams = new HttpParams();
      httpParams = httpParams.append('claimId', this.params.claimId);
      this.seClaimService.retrieveInitialSEClaim(httpParams).subscribe(res => {
        if (res.data) {
          this.initialSEClaimDetails = res.data;
        }
      });

      httpParams = new HttpParams();
      httpParams = httpParams.append('claimId', this.params.eClaimId);
      this.seClaimService.retrieveEClaimAdditionalInfo(httpParams).subscribe(res => {
        if (res.data) {
          this.additionalInfo = res.data;
        }
      });

    });
  }

  loadCodes(codeType: string, codeId: string): void {
    this.codeService.get(codeType, codeId)
      .subscribe((code: CodeView) => {
        this.claimOptionShort = code.label['en-US'] as string;
      });
  }

  checkAllExpanded():void{
    this.allExpanded = this.accordionStates.every(a => a === true);
  }

  expandCollapseAll(): void{
    if(this.allExpanded){
      this.accordionStates.fill(false,0,3);
    } else {
      this.accordionStates.fill(true,0,3);
    }
    this.checkAllExpanded();
  }

  isNewPolicy(): boolean{
    const claimStart = this.dateUtilsService.convertStrToDate(this.seClaimDetails.trgStartDte,"DD-MM-YYYY");
    const newPolicyDate = this.dateUtilsService.convertStrToDate(NSPAppConstants.CLAIM_NEW_POLICY_CUT_OFF_DATE, "DD-MM-YYYY");
    return !this.dateUtilsService.isBefore(claimStart, newPolicyDate);
  }
}
