  <div class="sticky-header" style="background-color: white">
    <p-toolbar [styleClass]="claimOptionShort==='AVE'?'ave-toolbar': claimOptionShort==='ITX'? 'itx-toolbar': claimOptionShort==='REP'? 'rep-toolbar':'default-toolbar'">
      <div class="p-toolbar-group-left col-9">
        <div class="col-4" [style]="{'display':'flex', 'align-items':'center'}">
          <div class="row">
            <h2>
              {{ seClaimDetails?.headerDto?.caseId }} ({{ claimOptionShort }}) {{ seClaimDetails?.headerDto?.nsManNric }}
            </h2>
          </div>
        </div>
        <div class="col-2">
          <div class="row">
            <div class="col-auto">
              <span>{{ seClaimDetails?.headerDto?.nsManRank }} {{ seClaimDetails?.headerDto?.nsManName }}</span>
            </div>
          </div>
          <div class="row">
            <div class="col-auto">
              <span>
                  <span>{{ seClaimDetails?.headerDto?.nsManPhoneNum }} ({{ seClaimDetails?.headerDto?.nsManGender }})</span>
              </span>
            </div>
          </div>
        </div>
        <div class="col-6">
          <div class="row">
            <div class="col-4">
              <span>CAB: Y/N</span>
            </div>
            <div class="col-4">
              {{seClaimDetails?.headerDto?.claimSource}}
            </div>
          </div>
          <div class="row">
            <div class="col-auto">
              <span>
                  Overlapping attendance: XXX and 4 more attd
              </span>
            </div>
          </div>
        </div>
      </div>
    </p-toolbar>
    <div class = "grid m-1">
      <div class = "col-6 justify-content-start gap-4 flex align-items-center" >
        <div><a href='#'>Prepopulated</a></div>
      </div>
      <div class = "col-6 flex justify-content-end gap-2">
        <button pButton (click) = "expandCollapseAll()">{{this.allExpanded? "Collapse All": "Expand All"}}</button>
      </div>
    </div>
  </div>
  <p-accordion [multiple] = "true">
    <n2-claim-for-eclaim-section (click) = "checkAllExpanded()"
                                 [(selected)] = "accordionStates[0]"
                                 [seClaimDetails]="seClaimDetails"
                                 [initialSEClaimDetails]="initialSEClaimDetails"
                                 [isNewPolicy] ="isNewPolicy()">
    </n2-claim-for-eclaim-section>
    <n2-item-details-for-eclaim-section (click) = "checkAllExpanded()"
                                        [(selected)] = "accordionStates[1]"
                                        [itemDetailsList]="itemDetailsList"
                                        [initialSEClaimDetails]="initialSEClaimDetails"
                                        [isNewPolicy] ="isNewPolicy()">
    </n2-item-details-for-eclaim-section>
    <n2-additional-eclaim-info-section (click) = "checkAllExpanded()"
                                       [(selected)] = "accordionStates[2]"
                                       [additionalInfo]="additionalInfo">
    </n2-additional-eclaim-info-section>
  </p-accordion>
</div>
