    selector: 'n2-view-current-access',
    templateUrl: './view-current-access.component.html',
    styleUrls: ['./view-current-access.component.scss'],
    providers: [ConfirmationService]
})

export class ViewCurrentAccessComponent implements OnInit {

    // Model
    selectCurrentAccess: ICurrentAccess;
    accountStatusEnum: { [key: string]: AccountStatus } = AccountStatus;
    salutationEnum: { [key: string]: Salutation } = Salutation;
    
    // enable and disable button
    isApprover = false;
    
    constructor (
        private dialogRef: DynamicDialogRef,
        private config: DynamicDialogConfig,
        private confirmationService: ConfirmationService,
        private toastService: CustomToastService,
        private currentAccessService:CurrentAccessService,
    ) { }

    ngOnInit(): void {
        this.load();
    }

    load(): void {
        this.selectCurrentAccess = this.config.data.selectedCurrentAccess;
        this.isApprover = this.config.data.isEnabled;
    }

    confirm(event: Event): void {
        this.confirmationService.confirm({
            target: event.target,
            message: 'Good to go?',
            icon: 'pi pi-exclamation-triangle',
            acceptLabel: "Proceed",
            rejectLabel: "No",
            accept: () => {
                this.currentAccessService.enableAccess(this.selectCurrentAccess.subjectId).subscribe({
                    next: (x: HttpResponse<any>) => console.log(x),
                    error: (error: HttpErrorResponse) => {
                        console.log("error is " + String(error?.error?.error_desc));
                        const error_message = String(error?.error?.error_desc);
                        const existing_request = "existing Access Right Request";
                        if (error_message.includes(existing_request)) {
                            this.toastService.error(error.error.error_desc);
                        }
                        this.closeDialog(false);
                    },
                    complete: () => {
                        console.log('User successfully enabled for view current access');
                        this.closeDialog(true);
                    }
                });
            }
        });
    }

    closeDialog(cond: boolean): void {
        this.dialogRef.close(cond);
    }

}
