import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { mergeMap } from 'rxjs/operators';

import { ActivateService } from './activate.service';

@Component({
  selector: 'ic-activate',
  templateUrl: './activate.component.html',
})
export class ActivateComponent implements OnInit {
  error = false;
  success = false;

  constructor(private activateService: ActivateService, private route: ActivatedRoute) {}

  ngOnInit(): void {
    this.route.queryParams.pipe(mergeMap(params => this.activateService.get(params.key))).subscribe(
      () => (this.success = true),
      () => (this.error = true)
    );
  }
}
<div>
  <div class="row justify-content-center">
    <div class="col-md-8">
      <h1 icTranslate="activate.title">Activation</h1>

      <div class="alert alert-success" *ngIf="success">
        <span icTranslate="activate.messages.success"><strong>Your user account has been activated.</strong> Please </span>
        <a class="alert-link" routerLink="/login" icTranslate="global.messages.info.authenticated.link">sign in</a>.
      </div>

      <div class="alert alert-danger" *ngIf="error" icTranslate="activate.messages.error">
        <strong>Your user could not be activated.</strong> Please use the registration form to sign up.
      </div>
    </div>
  </div>
</div>
