import { Component, OnInit } from '@angular/core';

import { Log, LoggersResponse, Level } from './log.model';
import { LogsService } from './logs.service';

@Component({
  selector: 'ic-logs',
  templateUrl: './logs.component.html',
})
export class LogsComponent implements OnInit {
  loggers?: Log[];
  filteredAndOrderedLoggers?: Log[];
  filter = '';
  orderProp: keyof Log = 'name';
  ascending = true;

  constructor(private logsService: LogsService) {}

  ngOnInit(): void {
    this.findAndExtractLoggers();
  }

  changeLevel(name: string, level: Level): void {
    this.logsService.changeLevel(name, level).subscribe(() => this.findAndExtractLoggers());
  }

  filterAndSort(): void {
    this.filteredAndOrderedLoggers = this.loggers?.filter(
      logger => !this.filter || logger.name.toLowerCase().includes(this.filter.toLowerCase())
    ).sort((a, b) => {
      if (a[this.orderProp] < b[this.orderProp]) {
        return this.ascending ? -1 : 1;
      } else if (a[this.orderProp] > b[this.orderProp]) {
        return this.ascending ? 1 : -1;
      } else if (this.orderProp === 'level') {
        return a.name < b.name ? -1 : 1;
      }
      return 0;
    });
  }

  private findAndExtractLoggers(): void {
    this.logsService.findAll().subscribe((response: LoggersResponse) => {
      this.loggers = Object.entries(response.loggers).map(([key, logger]) => new Log(key, logger.effectiveLevel));
      this.filterAndSort();
    });
  }
}
<div class="table-responsive" *ngIf="loggers">
  <h2 id="logs-page-heading" icTranslate="logs.title" data-bs-cy="logsPageHeading">Logs</h2>

  <p icTranslate="logs.nbloggers" [translateValues]="{ total: loggers.length }">There are {{ loggers.length }} loggers.</p>

  <span icTranslate="logs.filter">Filter</span>
  <input type="text" [(ngModel)]="filter" (ngModelChange)="filterAndSort()" class="form-control" />

  <table class="table table-sm table-striped table-bordered" aria-describedby="logs-page-heading">
    <thead>
      <tr icSort [(predicate)]="orderProp" [(ascending)]="ascending" [callback]="filterAndSort.bind(this)">
        <th icSortBy="name" scope="col"><span icTranslate="logs.table.name">Name</span> <fa-icon icon="sort"></fa-icon></th>
        <th icSortBy="level" scope="col"><span icTranslate="logs.table.level">Level</span> <fa-icon icon="sort"></fa-icon></th>
      </tr>
    </thead>

    <tr *ngFor="let logger of filteredAndOrderedLoggers">
      <td>
        <small>{{ logger.name | slice: 0:140 }}</small>
      </td>
      <td>
        <button
          (click)="changeLevel(logger.name, 'TRACE')"
          [ngClass]="logger.level === 'TRACE' ? 'btn-primary' : 'btn-light'"
          class="btn btn-sm"
        >
          TRACE
        </button>

        <button
          (click)="changeLevel(logger.name, 'DEBUG')"
          [ngClass]="logger.level === 'DEBUG' ? 'btn-success' : 'btn-light'"
          class="btn btn-sm"
        >
          DEBUG
        </button>

        <button
          (click)="changeLevel(logger.name, 'INFO')"
          [ngClass]="logger.level === 'INFO' ? 'btn-info' : 'btn-light'"
          class="btn btn-sm"
        >
          INFO
        </button>

        <button
          (click)="changeLevel(logger.name, 'WARN')"
          [ngClass]="logger.level === 'WARN' ? 'btn-warning' : 'btn-light'"
          class="btn btn-sm"
        >
          WARN
        </button>

        <button
          (click)="changeLevel(logger.name, 'ERROR')"
          [ngClass]="logger.level === 'ERROR' ? 'btn-danger' : 'btn-light'"
          class="btn btn-sm"
        >
          ERROR
        </button>

        <button
          (click)="changeLevel(logger.name, 'OFF')"
          [ngClass]="logger.level === 'OFF' ? 'btn-secondary' : 'btn-light'"
          class="btn btn-sm"
        >
          OFF
        </button>
      </td>
    </tr>
  </table>
</div>
