import { Component, Input, OnInit } from '@angular/core';
import { LazyLoadEvent } from 'primeng/api';
import { Ns2HttpService } from 'app/shared/service/ns2-http.service';
import { CustomToastService } from 'app/layouts/service/customtoast.service';
import { ExportUtilsService } from 'app/core/util/export-utils.service';
import { EnquiryNSmanService } from 'app/enquiry/nsman/service/enquiry-nsman.service';
import { Table } from 'primeng/table';
import { EnqPaydayTab } from 'app/enquiry/nsman/model/nsman-tabs.model';
import { NSmanPersonalInfo } from 'app/enquiry/nsman/model/nsman.model';

@Component({
  selector: 'n2-nsman-payday-tab',
  templateUrl: './nsman-payday-tab.component.html',
  styleUrls: ['./nsman-payday-tab.component.scss']
})
export class NsmanPaydayTabComponent implements OnInit {

  @Input() nsmanInfo: NSmanPersonalInfo;

  displayRecordDialog: any = { display: false };

  // LazyLoad variables
  totalRecords?: number;
  loading?: boolean;
  pageData: { currentPage: number } = {
    currentPage: 0
  }
  pageSize = 10;
  lazyLoadEvent: LazyLoadEvent;

  year: string;
  paydayList: EnqPaydayTab[] = [];
  
  constructor(
    private ns2HttpService: Ns2HttpService,
    private toastService: CustomToastService,
    private exportUtilsService: ExportUtilsService,
    private enquiryNSmanService: EnquiryNSmanService,
  ) {}

  ngOnInit(): void {
    //
  }

  load(event: LazyLoadEvent): void {
    setTimeout(() => {
      this.loading = true;
      this.lazyLoadEvent = event;

      const options = this.ns2HttpService.convertLazyEvent(event, this.pageData, this.pageSize);
      options.params = options.params.append("nric", this.nsmanInfo.nric);

      this.enquiryNSmanService.getPaydayTab(options).subscribe(resp => {
        if (resp.body?.data?.content) {
          this.paydayList = resp.body.data.content;
          this.totalRecords = resp.body.data.totalElements;
        } else {
          this.paydayList = [];
          this.totalRecords = 0;
        }
        this.loading = false;
      });
    }, 0);
  }

  onPage(event: any): void {
    this.pageSize = event.rows;
    this.pageData.currentPage = event.first / event.rows;
  }

  onOpenRecordDialog(year: any): void {
    this.year = String(year);
    this.displayRecordDialog.display = true;
  }

  onCloseRecordDialog(): void {
    this.year = '';
  }

  exportCSV(dataTable: Table): void {
    const options = this.ns2HttpService.convertLazyEvent(this.lazyLoadEvent, 0, this.totalRecords);
    options.params = options.params.append("nric", this.nsmanInfo.nric);

    this.enquiryNSmanService.getPaydayTab(options).subscribe(
      resp => {
        if (resp.body?.data?.content) {
          const data = resp.body?.data?.content;
          const columns = [
            { field: 'sn', header: 'SN', index: 0 },
            { field: 'year', header: 'Year', index: 1 },
            { field: 'nsmanAmt', header: 'NSman/SV', index: 2 },
            { field: 'employerAmt', header: 'Employer', index: 3 },
            { field: 'safraAmt', header: 'SAFRA', index: 4 },
            { field: 'nsmanOpOffsetAmt', header: 'Offset (NSman)', index: 5 },
            { field: 'employerOpOffsetAmt', header: 'Offset (Employer)', index: 6 },
            { field: 'bcpAmt', header: 'BCP', index: 7 },
            { field: 'othersAmt', header: 'Other', index: 8 },
            { field: 'totalAmt', header: 'Total', index: 9 },
          ];

          const dtoList: any[] = [];
          console.log("data", data);
          data.forEach((record: EnqPaydayTab, index: number) => {
            const dto = {
              sn: index + 1,
              year: record.year,
              nsmanAmt: record.nsmanAmt,
              employerAmt: record.employerAmt,
              safraAmt: record.safraAmt,
              nsmanOpOffsetAmt: record.nsmanOpOffsetAmt,
              employerOpOffsetAmt: record.employerOpOffsetAmt,
              bcpAmt: record.bcpAmt,
              othersAmt: record.othersAmt,
              totalAmt: record.totalAmt,
            }
            dtoList.push(dto);
          });
          this.exportUtilsService.exportCSV(dataTable, dtoList, resp.body.data.totalElements, columns, "Enquiry Payday Tab");
        }
      },
      error => {
        console.log(error.error?.error_desc);
        this.loading = false;
      })
  }

}
