import { Injectable } from '@angular/core';
import { ApplicationConfigService } from 'app/core/config/application-config.service';
import { Ns2HttpService } from 'app/shared/service/ns2-http.service';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class EnquiryNSmanService {

  public resourceUrl = this.applicationConfigService.getEndpointFor('api/enquiry/nsman');

  constructor(
    private ns2HttpService: Ns2HttpService,
    private applicationConfigService: ApplicationConfigService
  ) { }

  public getNSmen(partialNric: string): Observable<any>{
    const path =`${this.resourceUrl}/nsmen/${partialNric}`;
    return this.ns2HttpService.get(path);
  }

  public getNSmanByNric(nric: string): Observable<any>{
    const path =`${this.resourceUrl}/nric/${nric}`;
    return this.ns2HttpService.get(path);
  }

  public getNSmanByCaseId(caseId: string): Observable<any>{
    const path =`${this.resourceUrl}/caseId/${caseId}`;
    return this.ns2HttpService.get(path);
  }

  public getTrainingTab(options: any) : Observable<any>{
    const path = `${this.resourceUrl}/training`;
    return this.ns2HttpService.get(path, options);
  }

  public getTrainingPayrollRecord(id: string) : Observable<any>{
    const path = `${this.resourceUrl}/training_payroll/${id}`;
    return this.ns2HttpService.get(path);
  }

  public getPaydayTab(options: any) : Observable<any>{
    const path = `${this.resourceUrl}/payday`;
    return this.ns2HttpService.get(path, options);
  }

  public getPaydayYear(options: any) : Observable<any>{
    const path = `${this.resourceUrl}/payday_year`;
    return this.ns2HttpService.get(path, options);
  }

  public getPrepopEclaimTab(options: any) : Observable<any>{
    const path = `${this.resourceUrl}/prepop_eclaim`;
    return this.ns2HttpService.get(path, options);
  }

  public getPrepopEclaimRecord(id: string) : Observable<any>{
    const path = `${this.resourceUrl}/prepop_eclaim/${id}`;
    return this.ns2HttpService.get(path);
  }

  public getClaimTab(options: any) : Observable<any>{
    const path = `${this.resourceUrl}/claim`;
    return this.ns2HttpService.get(path, options);
  }

  public getSPRankPayBefFy10(options: any) : Observable<any>{
    const path = `${this.resourceUrl}/sp_rank_pay_bef_fy10`;
    return this.ns2HttpService.get(path, options);
  }

  public getSPVocAllwBefFy10(options: any) : Observable<any>{
    const path = `${this.resourceUrl}/sp_voc_allw_bef_fy10`;
    return this.ns2HttpService.get(path, options);
  }

  public getSPPilotAllwBefFy10(options: any) : Observable<any>{
    const path = `${this.resourceUrl}/sp_pilot_allw_bef_fy10`;
    return this.ns2HttpService.get(path, options);
  }

  public getPayrollEventTable(options: any) : Observable<any>{
    const path = `${this.resourceUrl}/payroll_event_table`;
    return this.ns2HttpService.get(path, options);
  }

}
