import { Component, OnInit, ViewChild } from '@angular/core';
import { CustomToastService } from 'app/layouts/service/customtoast.service';
import { Ns2HttpService } from 'app/shared/service/ns2-http.service';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { DateValidator } from 'app/shared/validator/date-validator.directive';
import { FileUploadComponent } from 'app/shared/component/file-upload/file-upload.component';
import { FileUploadValidator2 } from 'app/shared/validator/fileupload2.directive';
import { HttpErrorResponse, HttpResponse } from '@angular/common/http';
import { NspConfirmationService } from 'app/shared/confirm/nsp-confirm.service';
import { IQueryVES } from '../model/create-ves-request.model';
import { CreateVesService } from '../service/create-ves.service';
import { NRICValidator } from 'app/shared/validator/nric.directive';
import { EmailValidator } from 'app/shared/validator/email.directive';
import { Router } from '@angular/router';

@Component({
  selector: 'n2-create-ves-request',
  templateUrl: './create-ves-request.component.html',
  styleUrls: ['./create-ves-request.component.scss']
})
export class CreateVesRequestComponent implements OnInit {

  createdRequest: IQueryVES[] = [];
  status: boolean;

  generateForm: FormGroup = this.fb.group({
    nric: new FormControl('', [Validators.required, NRICValidator()]),
    name: new FormControl('-', Validators.required),
    remarks: new FormControl('', [Validators.maxLength(1000)]),
    extensionStartDt: new FormControl(null, Validators.required),
    extensionEndDt: new FormControl(null, Validators.required),
  }, {
    validators: DateValidator.dateLessThanOrEquals("extensionStartDt", "extensionEndDt")
  }
  );

  uploadForm: FormGroup = this.fb.group({
    file: new FormControl([], [FileUploadValidator2.allowableExtension(".pdf")])
  }, {});

  emailForm: FormGroup = this.fb.group({
    email: new FormControl('', [Validators.required, EmailValidator()]),
  }, {});

  isFileVerified = false;

  currentDate: Date;

  @ViewChild("vesFileUpload", { static: true })
  private vesFileUpload!: FileUploadComponent;

  constructor(
    private fb: FormBuilder,
    private toastService: CustomToastService,
    private n2httpService: Ns2HttpService,
    private createVesService: CreateVesService,
    private nspConfirmService: NspConfirmationService,
    private router: Router
  ) {

    this.currentDate = new Date();
  }

  ngOnInit(): void {
    this.generateForm.get('name').patchValue("-");
  }

  getNsManName(): void {
    const nric = this.generateForm.get('nric');

    if (nric.value != null && !nric.hasError('nric')) {
      this.createVesService.getNsManName(nric.value).subscribe(
        (res: HttpResponse<any>) => {
          console.log(res);
          if (res.body.data) {
            this.generateForm.get('name').patchValue(res.body.data.name);
            this.toastService.success("NSMan found");
          } else {
            this.toastService.info("No NSMan found");
          }
        },
        (error: HttpErrorResponse) => {
          console.log(error);
          this.toastService.error("No NSMan found");
        }
      )
    }
  }

  addRecord(): void {

    const id = this.generateForm.get('nric').value as string;
    this.createVesService.checkIfNricHasVesActive(id).subscribe(
      (res: HttpResponse<any>) => {
        this.status = res.body.data
      }
    );

    if (this.generateForm.get('nric')?.hasError('nric')) {
      this.toastService.error("NRIC Invalid");
      // } else if (this.generateForm.invalid) {
      //   this.toastService.error("Form Invalid");
    } else if (this.createdRequest.some((item) => item.nric === this.generateForm.get('nric').value as string)) {
      this.toastService.error("Duplicated NRIC");
    } else if (this.status) {
      this.toastService.error("PRL-E37206");
    } else {
      this.createdRequest.push({
        nric: this.generateForm.get('nric').value as string,
        name: this.generateForm.get('name').value as string,
        remarks: this.generateForm.get('remarks').value as string,
        extensionStartDt: this.generateForm.get('extensionStartDt').value as Date,
        extensionEndDt: this.generateForm.get('extensionEndDt').value as Date
      })
    }

    this.generateForm.reset();
    this.generateForm.get('name').patchValue("-");
  }

  removeRecord(id: any): void {
    this.createdRequest.splice(id, 1);
  }

  onCancel(): void {
    this.toastService.info('Cancelled');
    this.createdRequest = [];
    this.generateForm.markAsPristine();
    this.emailForm.markAsPristine();
    this.generateForm.reset({
      nric: null,
      name: null,
      remarks: null,
      extensionStartDt: null,
      extensionEndDt: null,
    });
    this.emailForm.reset({
      email: null
    });

    this.vesFileUpload.pFileUpload.clear();
    this.vesFileUpload.selectedFiles = [];
    this.vesFileUpload.onChange(this.vesFileUpload.pFileUpload.files);
    this.vesFileUpload.selectedFiles = [];
    this.vesFileUpload.pFileUpload.files = [];
    this.vesFileUpload.verifiedFiles = [];
    this.vesFileUpload.baseFilesObj = [];
    this.vesFileUpload.selectedFilesObj = [];
    this.vesFileUpload.selectedFiles = [];
    this.vesFileUpload.errCount = 0;
    this.vesFileUpload.ngOnDestroy();
    this.generateForm.get('name').patchValue("-");
  }

  addRequest(): void {

    const fileTxnId = this.vesFileUpload.verifiedFiles.map(x => String(x.meta_data.id));

    const request = {
      adminEmail: this.emailForm.get('email').value as string,
      supportingDocs: fileTxnId,
      records: this.createdRequest
    }

    setTimeout(() => {
      if (this.createdRequest.length === 0) {
        this.toastService.error("No serviceman added");
      } else if (this.emailForm.get('email')?.hasError('email')) {
        this.toastService.error("Email Invalid");
      }
      else {
        this.createVesService.postVesRequest(request).subscribe(
          (res: HttpResponse<any>) => {
            if (res.body) {
              this.isFileVerified = res.body?.data;
              if (this.isFileVerified) {
                this.vesFileUpload.disableButtons(true);
              }
              this.toastService.success("Created successfully");
              this.router.navigate(['payroll/ves']);
            }
            else {
              this.toastService.error("Unsuccessful");
            }
          },
          (error: HttpErrorResponse) => {
            console.log(error);
            this.toastService.error(error.error.error_desc);
          }
        );
      }
    }, 1000);
  }

  checkInputField(inputField: string): string {
    let customClass = '';
    const vdForm = this.generateForm
    const startDt = vdForm.get('extensionStartDt');
    const endDt = vdForm.get('extensionEndDt');
    if (inputField === 'extensionStartDt') {
      if (startDt.hasError('required') && startDt.touched) {
        customClass = 'ng-invalid ng-dirty'
      }
    } else if (inputField === 'extensionEndDt') {
      if ((endDt.hasError('required') && endDt.touched) || (vdForm.errors?.['invalidDateLessThanOrEquals'] && endDt.dirty)) {
        customClass = 'ng-invalid ng-dirty'
      }
    }
    return customClass;
  }


}
