  //retain otherwise will have error expression changed after it has been checked
  ngAfterViewChecked(): void {
    this._cd.detectChanges();
  }
  ngOnInit(): void {
    console.log("start");
}

  getSEClaimOptions(): any{
      this.codeService.gets('claim_option_long')
      .subscribe((codes: any[]) => {
        console.log("the codes", codes);
        this.optionsList = codes[0].map((x:any)=> {
          return {
            value: x.codeId,
            label: x.label['en-US'] as string
          }
        });
        console.log("optionslist", this.optionsList);
      });

  }

  ngOnDestroy(): void {
    this.onDestroy$.next();
    this.onDestroy$.complete();
  }

  // Bind an observable to the option fields.
  mapOptionFields(fields: FormlyFieldConfig[]){
    console.log("inner fields", fields);
    return fields.map((f) => {
      console.log("the fieldgrp", f.fieldGroup);
      f.fieldGroup.forEach((innerGrp)=>{
        if(innerGrp.key === 'seClaimOption'){
          innerGrp.type = 'select';
          innerGrp.props.options = this.codeService.gets('claim_option_long').pipe(map((data: any)=>
            data[0].map((s:any)=>({
              value: s.codeId,
              label: s.label['en-US'] as string
            }))))
        }
      })

      return f;
    });
  }

  submit() {
    alert(JSON.stringify(this.model));
  }


}



