import { formatDate } from '@angular/common';
import { Component, Inject, LOCALE_ID, OnInit, ViewChild } from '@angular/core';
import { CustomToastService } from 'app/layouts/service/customtoast.service';
import { Ns2HttpService } from 'app/shared/service/ns2-http.service';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { DateValidator } from 'app/shared/validator/date-validator.directive';
import { FileUploadComponent } from 'app/shared/component/file-upload/file-upload.component';
import { FileUploadValidator2 } from 'app/shared/validator/fileupload2.directive';
import { Table } from 'primeng/table';
import { NspConfirmationService } from 'app/shared/confirm/nsp-confirm.service';
import { DateUtilsService } from 'app/core/util/date-utils.service';
import { ExportUtilsService } from 'app/core/util/export-utils.service';
import { NRICValidator } from 'app/shared/validator/nric.directive';
import { EmailValidator } from 'app/shared/validator/email.directive';
import { IQueryVES } from '../model/create-ves-request.model';
import { IQueryApproveVES, RecordList, RemarksList, SupportingDocsList } from '../model/approve-ves-request.model';
import { CreateVesService } from '../service/create-ves.service';
import { HttpErrorResponse, HttpResponse } from '@angular/common/http';
import { LazyLoadEvent } from "primeng/api";
import { NSPAY2_REQUESTTYPE } from 'app/nspay2.constants';
import { FileUploadService } from 'app/shared/service/file-upload.service';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'n2-approve-ves-request',
  templateUrl: './approve-ves-request.component.html',
  styleUrls: ['./approve-ves-request.component.scss'],
})
export class ApproveVesRequestComponent implements OnInit {

  getrequest: IQueryApproveVES;
  recordList: RecordList[] = [];
  remarksList: RemarksList[] = [];
  supportingDocsList: SupportingDocsList[] = [];
  request_type: string;
  requestId: string;

  readonly NSPAY2_REQUESTTYPE = NSPAY2_REQUESTTYPE;

  generateForm: FormGroup = this.fb.group({
    nric: new FormControl('', [Validators.required, NRICValidator()]),
    name: new FormControl('', Validators.required),
    remarks: new FormControl(null, Validators.required),
    extensionStartDt: new FormControl(null, Validators.required),
    extensionEndDt: new FormControl(null, Validators.required),
  }, {
    validators: DateValidator.dateLessThanOrEquals("extensionStartDt", "extensionEndDt")
  }
  );

  uploadForm: FormGroup = this.fb.group({
    email: new FormControl('', [Validators.required, EmailValidator()]),
    file: new FormControl([], [FileUploadValidator2.allowableExtension(".pdf")])
  }, {});

  currentDate: Date;

  confirmationDialog: any = { display: false };
  confirmationRDialog: any = { display: false };

  vesFileUploadForm: FormGroup = this.fb.group({
    file: new FormControl([], [
      FileUploadValidator2.allowableExtension("pdf")])
  });

  reasonForm: FormGroup = this.fb.group({
    remarks: new FormControl(''),
  }, {});

  @ViewChild('fileTable') table: Table;

  @ViewChild("vesFileUpload")
  private vesFileUpload: FileUploadComponent;

  constructor(
    private fb: FormBuilder,
    private toastService: CustomToastService,
    private n2httpService: Ns2HttpService,
    private nspConfirmService: NspConfirmationService,
    private dateUtilsService: DateUtilsService,
    private exportUtilsService: ExportUtilsService,
    private createVesService: CreateVesService,
    private route: ActivatedRoute,
    private router: Router,
    @Inject(LOCALE_ID) protected localeId: string,
    private fileUploadService: FileUploadService,
  ) {
    this.currentDate = new Date();
  }

  ngOnInit(): void {
    console.log("VES Request ID: ", this.route.snapshot.paramMap.get("id"));
    this.requestId = this.route.snapshot.paramMap.get("id");
    this.loadData();
  }

  onOpen(dialog: any): void {
    dialog.display = true;
  }

  FileSubmit(event: Event, key: string, dialog: any): void {
    this.nspConfirmService.onConfirmPopup(event, key, dialog, () => {
      //
    });
  }

  addRequestApprove(): void {
    const request = {
      requestId: this.requestId,
      approve: true,
      rejectReason: this.reasonForm.get('remarks').value as string
    }
    console.log(request);
    setTimeout(() => {
      this.createVesService.postApproveRequest(request).subscribe(
        (res: HttpResponse<any>) => {
          if (res.body) {
            this.toastService.success("Request approved");
            this.router.navigate(['payroll/ves']);
          }
          else {
            this.toastService.warn("Unsuccessful");
          }
        },
        (error: HttpErrorResponse) => {
          this.toastService.error(error.error.error_desc);
        }
      );
    }, 1000);
  }

  addRequestReject(): void {
    const request = {
      requestId: this.requestId,
      approve: false,
      rejectReason: this.reasonForm.get('remarks').value as string
    }
    setTimeout(() => {
      this.createVesService.postApproveRequest(request).subscribe(
        (res: HttpResponse<any>) => {
          if (res.body) {
            this.toastService.success("Request rejected");
            this.router.navigate(['payroll/ves']);
          }
          else {
            this.toastService.warn("Unsuccessful");
          }
        },
        (error: HttpErrorResponse) => {
          this.toastService.error(error.error.error_desc);
        }
      );
    }, 1000);
  }

  checkEmail(input: string): string {
    let orange = '';
    for (let i = 0; i < this.recordList.length; i++) {
      console.log(this.recordList[i])
      if (input === "newAdminEmail") {
        if (this.recordList[0].newAdminEmail !== this.recordList[0].adminEmail) {
          orange = 'border-dashed border-yellow-300 border-2';
        }
      }
    }
    return orange;
  }

  checkStartDate(input: string): string {
    let orange = '';
    for (let i = 0; i < this.recordList.length; i++) {
      console.log(this.recordList[i])
      if (input === "newExtensionStartDt") {
        if (this.recordList[0].newExtensionStartDt !== this.recordList[0].extensionStartDt) {
          orange = 'border-dashed border-yellow-300 border-3';
        }
      }
    }
    return orange;
  }

  checkEndDate(input: string): string {
    let orange = '';
    for (let i = 0; i < this.recordList.length; i++) {
      console.log(this.recordList[i])
      if (input === "newExtensionEndDt") {
        if (this.recordList[0].newExtensionEndDt !== this.recordList[0].extensionEndDt) {
          orange = 'border-dashed border-yellow-300 border-3';
        }
      }
    }
    return orange;
  }

  loadData(): void {
    setTimeout(() => {
      this.createVesService.getById(this.requestId).subscribe(
        (res: HttpResponse<any>) => {
          console.log(res);
          if (res.body.data.status !== "PENDING_APPROVAL") {
            this.toastService.info("Request is not pending approval");
            this.router.navigate(['payroll/ves']);
          }
          this.getrequest = res.body.data;
          this.recordList = res.body.data.records;
          this.remarksList = res.body.data.remarks;
          this.supportingDocsList = res.body.data.supportingDocs;
          this.request_type = this.getrequest.requestType;

          console.log(this.supportingDocsList);
          if (this.recordList[0].newExtensionEndDt === null) {
            this.recordList[0].newExtensionEndDt = this.recordList[0].extensionEndDt
          }

          if (this.recordList[0].newExtensionStartDt === null) {
            this.recordList[0].newExtensionStartDt = this.recordList[0].extensionStartDt
          }

          if (this.recordList[0].newAdminEmail === null) {
            this.recordList[0].newAdminEmail = this.recordList[0].adminEmail
          }
        },
        (error: HttpErrorResponse) => {
          console.log(error.message);
          console.log(this.getrequest);
        }
      );
    }, 1000);
  }

  displayRemarksList(): string {
    let displayRemarks = "";
    for (const remark of this.remarksList) {
      displayRemarks += remark.remarks + "\n\n";
    }

    return displayRemarks;
  }

  downloadDoc(docId: string): void {
    if (docId) {
      this.fileUploadService.downloadTxnId(docId).subscribe(
        res => {
          if (res) {
            const data = res;
            const contentDispositionHeader: string = data.headers.get('content-disposition');
            const fileName = contentDispositionHeader.split(';')[1].trim().split('=')[1].replace(/"/g, '');
            const blob = new Blob([data.body], { type: data.headers.get('content-type') });
            const anchor = document.createElement("a");
            anchor.href = URL.createObjectURL(blob);
            anchor.download = fileName;
            anchor.click();
          }
        },
        error => {
          console.log(error);
          this.toastService.error(error.error?.error_desc);
        }
      );
    }
  }

  exportCSV(dataTable: Table): void {
    this.createVesService.getById(this.requestId).subscribe(
      res => {
        if (res.body.data.supportingDocs) {
          const data = res.body.data.supportingDocs;
          const columns =
            [
              { field: 'filename', header: 'File Name', index: 0 },
              { field: 'createdDt', header: 'Created Date', index: 1 },
              { field: 'createdBy', header: 'Created By', index: 2 }
            ];

          const dtoList: any[] = [];
          console.log("data", data);
          data.forEach((record: SupportingDocsList, index: number) => {
            const dto = {
              filename: `${String(record.filename)}`,
              createdDt: `${String(this.dateUtilsService.formatStrDate(record.createdDt, "DD-MM-YYYY"))}`,
              createdBy: `${String(record.createdBy)}`
            }
            dtoList.push(dto);
          });
          this.exportUtilsService.exportCSV(dataTable, dtoList, res.body.data.totalElements, columns,
            "data_".concat(formatDate(new Date(), "dd-MM-YYYY", this.localeId)));
        }
      },
      error => {
        console.log(error.error?.error_desc);
      })
  }

}
<p-panel header="Approve/Reject: Create VES Request" *ngIf="request_type === NSPAY2_REQUESTTYPE.CREATE">

    <p-panel class="remarksPanel" header='Request Remarks'>
        <textarea pInputTextarea disabled="true" class="form-control" rows="8">{{displayRemarksList()}}
        </textarea>
    </p-panel>
    <p></p>

    <p-panel header='NSmen'>
        <ng-template pTemplate='header'>
            <span class='p-panel-title'></span>
        </ng-template>
        <p-table [value]="recordList" styleClass="p-datatable-gridlines"
            currentPageReportTemplate="Showing {first} to {last} of {totalRecords} entries">
            <ng-template pTemplate="header">
                <tr>
                    <th scope="col" style="width: 20%">NRIC</th>
                    <th scope="col" style="width: 20%">Name</th>
                    <th scope="col" style="width: 20%"> Start of Extension</th>
                    <th scope="col" style="width: 20%"> End of Extension</th>
                    <th scope="col" style="width: 20%"> Email</th>
                </tr>
            </ng-template>
            <ng-template pTemplate="body" let-record let-i="rowIndex">
                <tr>
                    <td>{{ record.nric }}</td>
                    <td>{{ record.name }}</td>
                    <td>{{ record.extensionStartDt | date: 'dd-MM-yyyy' }}</td>
                    <td>{{ record.extensionEndDt | date: 'dd-MM-yyyy' }}</td>
                    <td>{{ record.adminEmail }}</td>
                </tr>
            </ng-template>
        </p-table>
    </p-panel>
    <p></p>
    <p-panel header='Unit Administrator'>
        <ng-template pTemplate='header'>
            <span class='p-panel-title'></span>
        </ng-template>
        <form [formGroup]="uploadForm">
            <p-table #fileTable styleClass='p-datatable-gridlines' [value]="supportingDocsList">
                <ng-template pTemplate='caption'>
                    <div class='grid'>
                        <div class='col-6 nsp-align-left flex justify-content-start align-items-center'>
                            <button type='button' pButton pRipple icon='pi pi-file-o' pTooltip='Export as CSV'
                                (click)="exportCSV(fileTable)" tooltipPosition='bottom'></button>
                        </div>
                    </div>
                </ng-template>
                <ng-template pTemplate='header'>
                    <tr>
                        <th scope="col" pSortableColumn='filename'>Filename<p-sortIcon field='filename'>
                            </p-sortIcon>
                        </th>
                        <th scope="col" pSortableColumn='uploadedon'>Uploaded On<p-sortIcon field='uploadedon'>
                            </p-sortIcon>
                        </th>
                        <th scope="col" pSortableColumn='uploadedby'>Uploaded By<p-sortIcon field='uploadedby'>
                            </p-sortIcon>
                        </th>
                    </tr>
                </ng-template>
                <ng-template pTemplate="body" let-record let-i="rowIndex">
                    <tr>
                        <td><a class="underline text-blue-500"
                                (click)="downloadDoc(record.documentId)">{{record.filename}}</a>
                        <td>{{record.createdDt | date: 'dd-MM-YYYY (HHmm)'}}</td>
                        <td>{{record.uploadedBy}}</td>
                    </tr>
                </ng-template>
            </p-table>
        </form>
    </p-panel>
    <p></p>
    <div class="flex justify-content-end align-items-center">
        <div>
            <button pButton type="button" label="Reject" class="p-button-danger"
                (click)="onOpen(confirmationRDialog);"></button>
            <button pButton type="button" label="Approve" (click)="onOpen(confirmationDialog);"></button>
        </div>
    </div>
</p-panel>





<p-panel header="Approve/Reject: Edit VES Request" *ngIf="request_type === NSPAY2_REQUESTTYPE.EDIT">

    <p-panel header='Request Remarks'>
        <div *ngFor="let item of remarksList">
            <textarea pInputTextarea disabled="true" class="form-control" placeholder="Item Code" required=""
                value="{{item.remarks}}">
                                </textarea>
        </div>
    </p-panel>
    <p></p>

    <p-panel header='Before'>
        <ng-template pTemplate='header'>
            <span class='p-panel-title'></span>
        </ng-template>
        <p-table [value]="recordList" styleClass="p-datatable-gridlines"
            currentPageReportTemplate="Showing {first} to {last} of {totalRecords} entries">
            <ng-template pTemplate="header">
                <tr>
                    <th scope="col" style="width: 15%">Paid</th>
                    <th scope="col" style="width: 15%">NRIC</th>
                    <th scope="col" style="width: 15%">Name</th>
                    <th scope="col" style="width: 15%"> Start of Extension</th>
                    <th scope="col" style="width: 15%"> End of Extension</th>
                    <th scope="col" style="width: 15%"> Email</th>
                </tr>
            </ng-template>
            <ng-template pTemplate="body" let-record let-i="rowIndex">
                <tr>
                    <td>{{ (record.lastPaymentDt == null)? 'N' : 'Y' }}</td>
                    <td>{{ record.nric }}</td>
                    <td>{{ record.name }}</td>
                    <td>{{ record.extensionStartDt | date: 'dd-MM-yyyy' }}</td>
                    <td>{{ record.extensionEndDt | date: 'dd-MM-yyyy' }}</td>
                    <td>{{ record.adminEmail }}</td>
                </tr>
            </ng-template>
        </p-table>
    </p-panel>
    <p></p>
    <p-panel header='After'>
        <ng-template pTemplate='header'>
            <span class='p-panel-title'></span>
        </ng-template>
        <p-table [value]="recordList" styleClass="p-datatable-gridlines"
            currentPageReportTemplate="Showing {first} to {last} of {totalRecords} entries">
            <ng-template pTemplate="header">
                <tr>
                    <th scope="col" style="width: 15%">Paid</th>
                    <th scope="col" style="width: 15%">NRIC</th>
                    <th scope="col" style="width: 15%">Name</th>
                    <th scope="col" style="width: 15%"> Start of Extension</th>
                    <th scope="col" style="width: 15%"> End of Extension</th>
                    <th scope="col" style="width: 15%"> Email</th>
                </tr>
            </ng-template>
            <ng-template pTemplate="body" let-record let-i="rowIndex">
                <tr>
                    <td>{{ (record.lastPaymentDt == null)? 'N' : 'Y' }}</td>
                    <td>{{ record.nric }}</td>
                    <td>{{ record.name }}</td>
                    <td [ngClass]="checkStartDate('newExtensionStartDt')">
                        {{ record.newExtensionStartDt | date:
                        'dd-MM-yyyy' }}
                    </td>
                    <td [ngClass]="checkEndDate('newExtensionEndDt')">
                        {{ record.newExtensionEndDt | date:
                        'dd-MM-yyyy' }}
                    </td>
                    <td [ngClass]="checkEmail('newAdminEmail')">
                        {{ record.newAdminEmail }}
                    </td>
                </tr>
            </ng-template>
        </p-table>
    </p-panel>
    <p></p>

    <p-panel header='Unit Administrator'>
        <ng-template pTemplate='header'>
            <span class='p-panel-title'></span>
        </ng-template>
        <form [formGroup]="uploadForm">
            <p-table #fileTable styleClass='p-datatable-gridlines' [value]="supportingDocsList">
                <ng-template pTemplate='caption'>
                    <div class='grid'>
                        <div class='col-6 nsp-align-left flex justify-content-start align-items-center'>
                            <button type='button' pButton pRipple icon='pi pi-file-o' pTooltip='Export as CSV'
                                (click)="exportCSV(fileTable)" tooltipPosition='bottom'></button>
                        </div>
                    </div>
                </ng-template>
                <ng-template pTemplate='header'>
                    <tr>
                        <th scope="col" pSortableColumn='filename'>Filename<p-sortIcon field='filename'>
                            </p-sortIcon>
                        </th>
                        <th scope="col" pSortableColumn='uploadedon'>Uploaded On<p-sortIcon field='uploadedon'>
                            </p-sortIcon>
                        </th>
                        <th scope="col" pSortableColumn='uploadedby'>Uploaded By<p-sortIcon field='uploadedby'>
                            </p-sortIcon>
                        </th>
                    </tr>
                </ng-template>
                <ng-template pTemplate="body" let-record let-i="rowIndex">
                    <tr>
                        <td><a class="underline text-blue-500"
                                (click)="downloadDoc(record.documentId)">{{record.filename}}</a>
                        <td>{{record.createdDt | date: 'dd-MM-YYYY (HHmm)'}}</td>
                        <td>{{record.uploadedBy}}</td>
                    </tr>
                </ng-template>
            </p-table>
        </form>
    </p-panel>
    <p></p>
    <div class="flex justify-content-end align-items-center">
        <div>
            <button pButton type="button" label="Reject" class="p-button-danger"
                (click)="onOpen(confirmationRDialog);"></button>
            <button pButton type="button" label="Approve" (click)="onOpen(confirmationDialog);"></button>
        </div>
    </div>
</p-panel>





<p-panel header="Approve/Reject: Delete VES Request" *ngIf="request_type === NSPAY2_REQUESTTYPE.DELETE">

    <p-panel header='Request Remarks'>
        <div *ngFor="let item of remarksList">
            <textarea pInputTextarea disabled="true" class="form-control" placeholder="Item Code" required=""
                value="{{item.remarks}}">
                                </textarea>
        </div>
    </p-panel>
    <p></p>
    <p-panel header='Deleted Record(s)'>
        <ng-template pTemplate='header'>
            <span class='p-panel-title'></span>
        </ng-template>
        <p-table [value]="recordList" styleClass="p-datatable-gridlines"
            currentPageReportTemplate="Showing {first} to {last} of {totalRecords} entries">
            <ng-template pTemplate="header">
                <tr>
                    <th scope="col" style="width: 20%">NRIC</th>
                    <th scope="col" style="width: 20%">Name</th>
                    <th scope="col" style="width: 20%"> Start of Extension</th>
                    <th scope="col" style="width: 20%"> End of Extension</th>
                    <th scope="col" style="width: 20%"> Email</th>
                </tr>
            </ng-template>
            <ng-template pTemplate="body" let-record let-i="rowIndex">
                <tr>
                    <td>{{ record.nric }}</td>
                    <td>{{ record.name }}</td>
                    <td>{{ record.extensionStartDt | date: 'dd-MM-yyyy' }}</td>
                    <td>{{ record.extensionEndDt | date: 'dd-MM-yyyy' }}</td>
                    <td>{{ record.adminEmail }}</td>
                </tr>
            </ng-template>
        </p-table>
    </p-panel>
    <p></p>
    <p-panel header='Unit Administrator'>
        <ng-template pTemplate='header'>
            <span class='p-panel-title'></span>
        </ng-template>
        <form [formGroup]="uploadForm">
            <p-table #fileTable styleClass='p-datatable-gridlines' [value]="supportingDocsList">
                <ng-template pTemplate='caption'>
                    <div class='grid'>
                        <div class='col-6 nsp-align-left flex justify-content-start align-items-center'>
                            <button type='button' pButton pRipple icon='pi pi-file-o' pTooltip='Export as CSV'
                                (click)="exportCSV(fileTable)" tooltipPosition='bottom'></button>
                        </div>
                    </div>
                </ng-template>
                <ng-template pTemplate='header'>
                    <tr>
                        <th scope="col" pSortableColumn='filename'>Filename<p-sortIcon field='filename'>
                            </p-sortIcon>
                        </th>
                        <th scope="col" pSortableColumn='uploadedon'>Uploaded On<p-sortIcon field='uploadedon'>
                            </p-sortIcon>
                        </th>
                        <th scope="col" pSortableColumn='uploadedby'>Uploaded By<p-sortIcon field='uploadedby'>
                            </p-sortIcon>
                        </th>
                    </tr>
                </ng-template>
                <ng-template pTemplate="body" let-record let-i="rowIndex">
                    <tr>
                        <td><a class="underline text-blue-500"
                                (click)="downloadDoc(record.documentId)">{{record.filename}}</a>
                        <td>{{record.createdDt | date: 'dd-MM-YYYY (HHmm)'}}</td>
                        <td>{{record.uploadedBy}}</td>
                    </tr>
                </ng-template>
            </p-table>
        </form>
    </p-panel>
    <p></p>
    <div class="flex justify-content-end align-items-center">
        <div>
            <button pButton type="button" label="Reject" class="p-button-danger"
                (click)="onOpen(confirmationRDialog);"></button>
            <button pButton type="button" label="Approve" (click)="onOpen(confirmationDialog);"></button>
        </div>
    </div>
</p-panel>



<p-dialog header='Approve Request Confirmation' [(visible)]='confirmationDialog.display'
    [breakpoints]="{'960px': '75vw'}" [style]="{width: '50vw'}" [draggable]='false' [resizable]='false' [modal]='true'>
    <form>
        <label for="remarks">Remarks</label>
        <textarea type="text" pInputText id="remarks" formControlName="remarks" rows="10"
            class="text-base text-color surface-overlay p-2 border-1 border-solid surface-border border-round appearance-none outline-none focus:border-primary w-full"></textarea>
    </form>
    <ng-template pTemplate='footer'>
        <button pButton class='p-button-danger' label='Cancel' (click)='confirmationDialog.display=false'
            [style]="{'margin-left': '.5em' , 'margin-bottom': '.5em'}"></button>
        <p-confirmPopup key='FileUpload'></p-confirmPopup>
        <button pButton label='Submit' (click)="addRequestApprove()"
            [style]="{'margin-left': '.5em' , 'margin-bottom': '.5em'}"></button>
    </ng-template>
</p-dialog>

<p-dialog header='Approve Request Confirmation' [(visible)]='confirmationRDialog.display'
    [breakpoints]="{'960px': '75vw'}" [style]="{width: '50vw'}" [draggable]='false' [resizable]='false' [modal]='true'>
    <form [formGroup]="reasonForm">
        <label for="remarks">Remarks</label>
        <textarea type="text" pInputText id="remarks" formControlName="remarks" rows="10"
            class="text-base text-color surface-overlay p-2 border-1 border-solid surface-border border-round appearance-none outline-none focus:border-primary w-full"></textarea>
    </form>
    <ng-template pTemplate='footer'>
        <button pButton class='p-button-danger' label='Cancel' (click)='confirmationRDialog.display=false'
            [style]="{'margin-left': '.5em' , 'margin-bottom': '.5em'}"></button>
        <p-confirmPopup key='FileUpload'></p-confirmPopup>
        <button pButton label='Submit' (click)="addRequestReject()"
            [style]="{'margin-left': '.5em' , 'margin-bottom': '.5em'}"></button>
    </ng-template>
</p-dialog>
