import {Component, EventEmitter, Input, OnChanges, OnInit, Output, SimpleChanges} from '@angular/core';
import {SEClaimItemDto} from "app/claim/se-claim/model/se-claim-item.model";
import {InitialSEClaim} from "app/claim/se-claim/model/initial-se-claim.model";
import {ClaimService} from "app/claim/service/claim.service";
import {HttpParams} from "@angular/common/http";

@Component({
  selector: 'n2-item-details-for-eclaim-section',
  templateUrl: './item-details-section-for-eclaim.component.html',
  styleUrls: ['./item-details-section-for-eclaim.component.scss']
})
export class ItemDetailsSectionForEclaimComponent implements OnInit, OnChanges {

  @Input() selected:boolean;
  @Output() selectedChange = new EventEmitter<boolean>();
  @Input() itemDetailsList: SEClaimItemDto[];
  @Input() initialSEClaimDetails: InitialSEClaim;
  @Input() isNewPolicy = false;
  fullItemStatusMap: Map<string,string>;
  itemExpandState: boolean[];
  formulaMap: Map<string,string>;

  constructor(private claimService: ClaimService) {
    console.log("test");
  }

  ngOnInit(): void {
    this.claimService.retrieveItemStatusMap(null).subscribe((res:any) => {
      if (res.body.data) {
        this.fullItemStatusMap = new Map<string, string>(Object.entries(res.body.data));
      }
    });

    let params = new HttpParams();
    params = params.append("claimType", "S");
    this.claimService.retrieveFormulaList({params}).subscribe((res:any) => {
      if(res.body.data){
        this.formulaMap = new Map<string, string>();
        res.body.data.forEach((x:any)=>{
          this.formulaMap.set(x.formulaCode,x.formulaDesc);
        });
      }
    });
  }

  ngOnChanges(changes: SimpleChanges): void {
    if(changes['itemDetailsList']?.currentValue){
      this.itemDetailsList = changes['itemDetailsList']?.currentValue;
      this.itemExpandState = Array(this.itemDetailsList.length).fill(true);
    }
  }

  accordionClick(): void{
    this.selectedChange.emit(this.selected);
  }

  checkFormulaCode(item: SEClaimItemDto): boolean{
    let isDiffFromPrepop = false;
    if(item.formulaCode !== this.initialSEClaimDetails.formulaCode){
      isDiffFromPrepop = true;
    }
    return this.isNewPolicy && item.itemType === "ITX" && isDiffFromPrepop;
  }


}
<p-accordionTab header="Item Details" [(selected)]="selected" (click) = "accordionClick()">
  <div *ngIf ="itemDetailsList !== undefined">
    <div *ngIf="itemDetailsList.length !== 0; else emptymessage">
      <div class = "grid col-12 m-0 p-0">
        <p-card *ngFor = "let item of itemDetailsList; let i = index;" class = "flex col-6 align-items-stretch p-1 m-0" [style]="{'width': '100%'}">
          <div class = "grid p-0 m-0">
            <div class = "col-7 flex align-items-center p-0 m-0" style = "height:50px;">
              <p-toggleButton *ngIf = "item.itemType === 'ITX'" class ="collapse-card" [(ngModel)]="itemExpandState[i]"
                             onIcon="pi pi-plus" offIcon="pi pi-minus" [style]="{'margin-right': '0.5em'}"></p-toggleButton>
              <div style="margin-right: 2em"><b>{{ item.itemName }}</b></div>
              <div style="margin-right: 2em">Appr Amt: {{ item.apprAmt }}</div>
              <div style="margin-right: 2em" pTooltip="{{this.initialSEClaimDetails.formulaCode}} {{this.formulaMap?.get(this.initialSEClaimDetails.formulaCode)}}"
                   [tooltipDisabled] = "!checkFormulaCode(item)"
                   [ngClass]="checkFormulaCode(item)? 'warning-date' : null">
                Formula: {{ item.formulaCode }} {{this.formulaMap?.get(item.formulaCode)}}
              </div>
            </div>
            <div class = "col-5 flex align-items-center justify-content-end p-0 m-0" style = "height:50px;">
              <div class="flex align-items-center">
                <b>{{ this.fullItemStatusMap?.get(item.latestStatus).toUpperCase()}}</b>
              </div>
            </div>

            <div class = "col-12 flex align-items-center p-0 m-0" >
              <div >Computation: {{ item.computedFormula }}</div>
            </div>
          </div>
          <div *ngIf = "item.itemType === 'ITX' && itemExpandState[i]">
            <p-divider></p-divider>
              <p-table
                [value]= [1]
                styleClass="p-datatable-gridlines p-datatable-striped"
              >
                <ng-template pTemplate="header">
                  <tr>
                    <th scope="col">Accounting Start</th>
                    <th scope="col">Accounting End</th>
