import { Component, OnInit, OnDestroy } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { NgbActiveModal } from '@ng-bootstrap/ng-bootstrap';
import { EventManager } from 'app/core/util/event-manager.service';
import { Resource } from './resource.model';
import { ResourceService } from './resource.service';
import { ResourcePopupService } from './resource-popup.service';

@Component({
  selector: 'ic-resource-delete-dialog',
  templateUrl: './resource-delete-dialog.component.html',
})
export class ResourceDeleteDialogComponent {
  resource: Resource = new Resource();

  constructor(
    private resourceService: ResourceService,
    public activeModal: NgbActiveModal,
    private eventManager: EventManager,
    private router: Router
  ) {}

  clear(): void {
    this.activeModal.dismiss('cancel');
    this.backToPrevious();
  }

  confirmDelete(id: any): void {
    this.resourceService.delete(id).subscribe(() => {
      this.eventManager.broadcast({
        name: 'resourceListModification',
        content: 'Deleted an resource',
      });
      this.activeModal.dismiss(true);
      this.backToPrevious();
    });
  }

  backToPrevious(): void {
    setTimeout(() => {
      this.router.navigate(['admin/acm/ic-resource']);
    }, 0);
  }
}

@Component({
  selector: 'ic-resource-delete-popup',
  template: '',
})
export class ResourceDeletePopupComponent implements OnInit, OnDestroy {
  routeSub: any;

  constructor(private route: ActivatedRoute, private resourcePopupService: ResourcePopupService) {}

  ngOnInit(): void {
    this.routeSub = this.route.params.subscribe(params => {
      this.resourcePopupService.open(ResourceDeleteDialogComponent as Component, params['id']);
    });
  }

  ngOnDestroy(): void {
    this.routeSub.unsubscribe();
  }
}
<form name="deleteForm" (ngSubmit)="confirmDelete(resource.resourceId)">
  <div class="modal-header">
    <h4 class="modal-title" icTranslate="entity.delete.title">Confirm delete operation</h4>
    <button type="button" class="close" data-dismiss="modal" aria-hidden="true" (click)="clear()">&times;</button>
  </div>
  <div class="modal-body">
    <ic-alert-error></ic-alert-error>
    <p icTranslate="acmAdmin.resource.delete.question" [translateValues]="{ id: resource.resourceName }">
      Are you sure you want to delete this Resource?
    </p>
  </div>
  <div class="modal-footer">
    <button id="deleteResource" type="submit" class="btn btn-danger btn-sm">
      <fa-icon [icon]="'times'"></fa-icon>&nbsp;<span icTranslate="entity.action.delete">Delete</span>
    </button>
    <button type="button" class="btn btn-secondary btn-sm" data-dismiss="modal" (click)="clear()">
      <fa-icon [icon]="'ban'"></fa-icon>&nbsp;<span icTranslate="entity.action.cancel">Cancel</span>
    </button>
  </div>
</form>
