import { Component, OnInit, ViewChild } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { HttpErrorResponse, HttpResponse } from '@angular/common/http';
import { CustomToastService } from 'app/layouts/service/customtoast.service';
import { FileUploadComponent } from 'app/shared/component/file-upload/file-upload.component';
import { Ns2HttpService } from 'app/shared/service/ns2-http.service';
import { FileUploadValidator2 } from 'app/shared/validator/fileupload2.directive';
import { VesPassDataService } from '../service/ves-pass-data.service';
import { VesService } from '../service/ves.service';
import { Router } from '@angular/router';

@Component({
  selector: 'n2-ves-record-delete',
  templateUrl: './ves-record-delete.component.html',
  styleUrls: ['./ves-record-delete.component.scss']
})
export class VesRecordDeleteComponent implements OnInit {
  confirmDeleteVesRecordDialog: any = { display: false };

  vesRecordsToDelete: any[] = [];
  recordIds: string[] = [];

  deleteVesRecordsForm: FormGroup = this.fb.group({
    // emailInput: new FormControl(''),
    supportingDocuments: new FormControl([], FileUploadValidator2.allowableExtension(".pdf")),
    remarksInput: new FormControl(null, Validators.required)
  })

  @ViewChild('vesRecordSupportingDocuments', { static: true })
  private supportingDocsFileUploadComponent!: FileUploadComponent;

  constructor(
    private fb: FormBuilder,
    private toastService: CustomToastService,
    private n2httpService: Ns2HttpService,
    private vesService: VesService,
    private passDataService: VesPassDataService,
    private router: Router
  ) {
    //
  }

  ngOnInit(): void {
    this.passDataService.passedData.subscribe((passedData) => {
      this.recordIds = passedData
      // this.vesRecordsToDelete = passedData;
      // for (const record of this.vesRecordsToDelete) {
      //   this.recordIds.push(record.id);
      // }
      // console.log(this.vesRecordsToDelete);
      console.log(this.recordIds);
    })
    this.loadAllVesRecordById();
  }

  loadAllVesRecordById(): void {
    const ids = {
      ids: this.recordIds.join(',')
    }
    this.vesService.getAllRecordsById(ids).subscribe(
      (res: HttpResponse<any>) => {
        console.log(res);
        this.vesRecordsToDelete = res.body.data;
      },
      (error: HttpErrorResponse) => {
        console.log(error);
        this.toastService.error(error.error.error_desc);
      });
  }

  removeVesRecord(index: any): void {
    this.vesRecordsToDelete.splice(index, 1);
    this.recordIds.splice(index, 1);
    console.log(this.vesRecordsToDelete);
    console.log(this.recordIds);
  }

  onSubmit(dialog: any): void {
    dialog.display = true;
  }

  onCancel(): void {
    this.toastService.info('Edit cancelled. All fields have been reset.');
    this.supportingDocsFileUploadComponent.clearAndReset();
    this.deleteVesRecordsForm.markAsPristine();
    this.deleteVesRecordsForm.reset({
      emailInput: null
    });
  }

  onClose(dialog: any): void {
    dialog.display = false;
  }

  deleteVesRecord(): void {
    const idsToDelete = [];
    for (const record of this.vesRecordsToDelete) {
      idsToDelete.push(record.id)
    }
    const recordsToDelete = {
      recordIds: this.recordIds,
      supportingDocs: this.supportingDocsFileUploadComponent.verifiedFiles.map(x => String(x.meta_data.id)),
      remarks: this.deleteVesRecordsForm.get('remarksInput').value as string
    };

    console.log(recordsToDelete);
    this.vesService.createDeleteRecordRequest(recordsToDelete).subscribe(
      (res: HttpResponse<any>) => {
        console.log(res);
        this.confirmDeleteVesRecordDialog.display = false;
        this.router.navigate(['payroll/ves']);
        this.toastService.success("Requested VES record(s) to be deleted");
      },
      (error: HttpErrorResponse) => {
        console.log(error);
        this.confirmDeleteVesRecordDialog.display = false;
        this.toastService.error(error.error.error_desc);
      }
    )
  }

}
<p-panel header="Delete VES Record">
  <form [formGroup]="deleteVesRecordsForm" (ngSubmit)="deleteVesRecord()">
    <div class="grid">
      <div class="col-6">
        <p-table #vdt [value]="vesRecordsToDelete" styleClass="p-datatable-gridlines" dataKey="id"
          responsiveLayout="scroll">
          <ng-template pTemplate="caption">
            <span class="font-bold">Delete Record(s)</span>
          </ng-template>
          <ng-template pTemplate="header">
            <tr>
              <th scope="col">NRIC</th>
              <th scope="col">Name</th>
              <th scope="col">Start of Extension</th>
              <th scope="col">End of Extension</th>
              <th scope="col">Paid</th>
              <th scope="col"></th>
            </tr>
          </ng-template>
          <ng-template pTemplate="body" let-vesRecord let-i="rowIndex">
            <tr>
              <td>{{ vesRecord.nric }}</td>
              <td>{{ vesRecord.name }}</td>
              <td>{{ vesRecord.extensionStartDt | date: 'dd-MM-yyyy' }}
              <td>{{ vesRecord.extensionEndDt | date: 'dd-MM-yyyy' }}</td>
              <td>{{ (vesRecord.paid)? 'Y' : 'N' }}</td>
              <td><button pButton pRipple type="button" icon="pi pi-times" class="p-button-rounded p-button-danger"
                  (click)="removeVesRecord(i)"></button>
              </td>
            </tr>
          </ng-template>
        </p-table>
      </div>
      <div class="col-6">
        <p-panel>
          <ng-template pTemplate="header">
            <span class="font-bold">Unit Administrator</span>
          </ng-template>
          <!-- Email Input -->
          <!-- <div class="grid col-7 align-items-center">
            <label class="col-3">Email:</label>
            <div class="col-9">
              <input type="text" pInputText class="w-full" id="emailInput" formControlName="emailInput"
                placeholder="Email" />
            </div>
          </div> -->
          <n2-file-upload formControlName='supportingDocuments' id='supportingDocuments' #vesRecordSupportingDocuments
            [contentHeight]="'200px'"></n2-file-upload>
        </p-panel>
      </div>
    </div>
    <div class="mt-5 nsp-align-right gap-2">
      <button pButton class="p-button-danger" type='button' label="Cancel" (click)="onCancel()"></button>
      <button pButton type='button' label="Submit" [disabled]="vesRecordsToDelete.length<1"
        (click)="onSubmit(confirmDeleteVesRecordDialog)"></button>
    </div>
    <p-dialog header="Edit Ves Record Confirmation" [(visible)]='confirmDeleteVesRecordDialog.display'
      [draggable]='false' [resizable]='false' [modal]='true' [breakpoints]="{'960px': '75vw'}"
      [style]="{width: '50vw'}">
      <span>
        [System} Removing NSman
        <span *ngFor="let record of vesRecordsToDelete;let i = index">
          <span *ngIf="i < vesRecordsToDelete.length-1">{{record.nric}}, </span>
          <span *ngIf="i == vesRecordsToDelete.length-1">{{record.nric}}</span>
        </span>
      </span>
      <div class="field col w-full">
        <label class="align-items-start" for="confirmRemarksInput">Remarks*:</label>
        <div class="w-full">
          <textarea pInputTextarea [rows]="10" class="w-full" formControlName="remarksInput"></textarea>
        </div>
        <div
          *ngIf="deleteVesRecordsForm.get('remarksInput')?.hasError('required') && deleteVesRecordsForm.get('remarksInput')?.touched"
          class="form-text text-danger">
          Remarks required
        </div>
      </div>
      <div class="mt-5 nsp-align-right gap-2">
        <button pButton class="p-button-danger" type='button' label="Cancel"
          (click)="onClose(confirmDeleteVesRecordDialog)"></button>
        <button pButton type='submit' label="Submit"
          [disabled]="vesRecordsToDelete.length<1 || !deleteVesRecordsForm.valid"></button>
      </div>
    </p-dialog>
  </form>
</p-panel>
