import { Component } from '@angular/core';
import { Code } from '../code/code.model';
import { NgbActiveModal } from '@ng-bootstrap/ng-bootstrap';
import { EventManager } from 'app/core/util/event-manager.service';
import { CodeService } from './code.service';

@Component({
  selector: 'ic-code-delete-dialog',
  templateUrl: './code-delete-dialog.component.html',
})
export class CodeDeleteDialogComponent {
  code?: Code;

  constructor(protected codeService: CodeService, public activeModal: NgbActiveModal, protected eventManager: EventManager) {}

  cancel(): void {
    this.activeModal.dismiss();
  }

  confirmDelete(codeTypePk: string, codeId: string): void {
    this.codeService.delete({ codeTypePk, codeId }).subscribe(() => {
      this.eventManager.broadcast('codeListModification');
      this.activeModal.close();
    });
  }
}
<form name="deleteForm" (ngSubmit)="confirmDelete(code?.codeTypePK!, code?.codeId!)">
  <div class="modal-header">
    <h4 class="modal-title" icTranslate="entity.delete.title">Confirm delete operation</h4>
    <button type="button" class="close" data-dismiss="modal" aria-hidden="true" (click)="cancel()">&times;</button>
  </div>

  <div class="modal-body">
    <ic-alert-error></ic-alert-error>
    <p icTranslate="codeAdmin.code.delete.question" [translateValues]="{ id: code?.codeId }">Are you sure you want to delete this Code?</p>
  </div>

  <div class="modal-footer">
    <button type="submit" class="btn btn-danger btn-sm">
      <fa-icon [icon]="'times'"></fa-icon>&nbsp;<span icTranslate="entity.action.delete">Delete</span>
    </button>
    <button type="button" class="btn btn-secondary btn-sm" data-dismiss="modal" (click)="cancel()">
      <fa-icon [icon]="'ban'"></fa-icon>&nbsp;<span icTranslate="entity.action.cancel">Cancel</span>
    </button>
  </div>
</form>
